// API route for recommendation cache management
import { NextRequest, NextResponse } from 'next/server';
import { logger } from '@/lib/logger';

import { recommendationCache } from '@/lib/recommendation-cache';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const action = searchParams.get('action');

    switch (action) {
      case 'stats': {
        const stats = recommendationCache.getStats();
        return NextResponse.json({
          success: true,
          cache: {
            ...stats,
            memoryUsage: `${stats.memoryUsage} KB`
          },
          timestamp: new Date().toISOString()
        });

      }

      case 'popular': {
        const limit = parseInt(searchParams.get('limit') || '10');
        const popular = recommendationCache.getPopularEntries(limit);
        return NextResponse.json({
          success: true,
          popularEntries: popular.map(entry => ({
            key: entry.key,
            hits: entry.hits,
            resultCount: entry.data.length
          })),
          timestamp: new Date().toISOString()
        });

      }

      default:
        const basicStats = recommendationCache.getStats();
        return NextResponse.json({
          success: true,
          message: 'Recommendation cache is running',
          stats: basicStats,
          actions: {
            stats: '/api/recommendations/cache?action=stats',
            popular: '/api/recommendations/cache?action=popular&limit=10',
            invalidate: 'POST /api/recommendations/cache with action: invalidate'
          },
          timestamp: new Date().toISOString()
        });
    }

  } catch (error) {
    logger.error('❌ Cache stats API error:', error as Record<string, unknown>);
    return NextResponse.json(
      { 
        error: 'Failed to get cache statistics',
        details: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { action, productIds, categories, healthBenefits } = body;

    switch (action) {
      case 'invalidate':
        if (productIds || categories || healthBenefits) {
          // Selective invalidation
          recommendationCache.invalidate(productIds, categories, healthBenefits);
          return NextResponse.json({
            success: true,
            message: 'Cache selectively invalidated',
            invalidated: {
              productIds: productIds || [],
              categories: categories || [],
              healthBenefits: healthBenefits || []
            },
            timestamp: new Date().toISOString()
          });
        } else {
          // Invalidate all
          recommendationCache.invalidateAll();
          return NextResponse.json({
            success: true,
            message: 'All cache entries invalidated',
            timestamp: new Date().toISOString()
          });
        }

      case 'warm-up':
        // This would implement cache warming logic
        return NextResponse.json({
          success: true,
          message: 'Cache warm-up initiated',
          note: 'Warm-up logic would be implemented here',
          timestamp: new Date().toISOString()
        });

      default:
        return NextResponse.json(
          { error: 'Invalid action. Use: invalidate, warm-up' },
          { status: 400 }
        );
    }

  } catch (error) {
    logger.error('❌ Cache management API error:', error as Record<string, unknown>);
    return NextResponse.json(
      { 
        error: 'Failed to manage cache',
        details: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { logger } from '@/lib/logger';

import { ReviewRequestStatus } from '@/types/reviews';

// PUT /api/reviews/requests/[id] - Update review request status
export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id: requestId } = await params;
    const { action } = await request.json();

    if (!['send', 'reminder', 'complete', 'expire'].includes(action)) {
      return NextResponse.json(
        { success: false, error: 'Invalid action' },
        { status: 400 }
      );
    }

    // In production, this would update the database
    let newStatus: ReviewRequestStatus | undefined;
    let updateFields: Record<string, string> = {};

    switch (action) {
      case 'send':
        newStatus = ReviewRequestStatus.SENT;
        updateFields.sentAt = new Date().toISOString();
        await sendReviewRequestEmail(requestId);
        break;
      case 'reminder':
        newStatus = ReviewRequestStatus.REMINDER_SENT;
        updateFields.reminderSentAt = new Date().toISOString();
        await sendReminderEmail(requestId);
        break;
      case 'complete':
        newStatus = ReviewRequestStatus.COMPLETED;
        updateFields.completedAt = new Date().toISOString();
        break;
      case 'expire':
        newStatus = ReviewRequestStatus.EXPIRED;
        break;
    }

    // Check if newStatus was assigned
    if (newStatus === undefined) {
      return NextResponse.json(
        { success: false, error: 'Invalid action' },
        { status: 400 }
      );
    }

    logger.info('🔄 Review request updated:', {
      requestId,
      action,
      newStatus,
      updateFields,
      timestamp: new Date().toISOString()
    });

    return NextResponse.json({
      success: true,
      data: {
        requestId,
        status: newStatus,
        ...updateFields
      },
      message: `Review request ${action}ed successfully`
    });

  } catch (error) {
    logger.error('Review request update error:', error as Record<string, unknown>);
    return NextResponse.json(
      { success: false, error: 'Failed to update review request' },
      { status: 500 }
    );
  }
}

// DELETE /api/reviews/requests/[id] - Cancel review request
export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id: requestId } = await params;

    // In production, this would soft delete from database
    logger.info('🗑️ Review request cancelled:', {
      requestId,
      timestamp: new Date().toISOString()
    });

    return NextResponse.json({
      success: true,
      message: 'Review request cancelled successfully'
    });

  } catch (error) {
    logger.error('Review request cancellation error:', error as Record<string, unknown>);
    return NextResponse.json(
      { success: false, error: 'Failed to cancel review request' },
      { status: 500 }
    );
  }
}

async function sendReviewRequestEmail(requestId: string): Promise<void> {
  // In production, this would send actual email
  logger.info('📧 Sending initial review request email for:', { requestId });
  await new Promise(resolve => setTimeout(resolve, 100));
}

async function sendReminderEmail(requestId: string): Promise<void> {
  // In production, this would send reminder email
  logger.info('🔔 Sending review reminder email for:', { requestId });
  await new Promise(resolve => setTimeout(resolve, 100));
}

import { NextRequest, NextResponse } from 'next/server';
import axios from 'axios';
import * as cheerio from 'cheerio';
import { validateScraperAuth, validateScrapingUrl, checkRateLimit } from '@/lib/scrapers/scraper-auth';
import { logger } from '@/lib/logger';

// Server-side proxy with security controls
export async function POST(request: NextRequest) {
  try {
    // Validate authentication
    const authResult = await validateScraperAuth(request);
    if (!authResult.success) {
      return NextResponse.json(
        { error: authResult.error || 'Unauthorized' },
        { status: 401 }
      );
    }

    // Check rate limiting
    if (!authResult.userId) {
      return NextResponse.json(
        { error: 'User ID not found for rate limiting' },
        { status: 400 }
      );
    }
    const rateLimitResult = checkRateLimit(authResult.userId);
    if (!rateLimitResult.allowed) {
      return NextResponse.json(
        { error: rateLimitResult.error },
        { status: 429 }
      );
    }

    const { url, selectors, headers = {} } = await request.json();

    if (!url) {
      return NextResponse.json(
        { error: 'URL is required' },
        { status: 400 }
      );
    }

    // Validate URL is allowed
    const urlValidation = validateScrapingUrl(url);
    if (!urlValidation.valid) {
      logger.warn(`Blocked scraping attempt for URL: ${url} by user: ${authResult.userId}`);
      return NextResponse.json(
        { error: urlValidation.error },
        { status: 403 }
      );
    }

    // Fetch the page server-side (no CORS issues)
    const response = await axios.get(url, {
      headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36',
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.5',
        'Accept-Encoding': 'gzip, deflate',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1',
        ...headers,
      },
      timeout: 30000,
      maxRedirects: 5,
    });

    // Parse HTML with cheerio
    const $ = cheerio.load(response.data);

    // Extract data based on selectors
    const extractedData: Record<string, unknown> = {};

    if (selectors) {
      // Extract product name
      if (selectors.productName) {
        extractedData.title = $(selectors.productName).first().text().trim() || null;
      }

      // Extract price
      if (selectors.price) {
        const priceText = $(selectors.price).first().text().trim();
        const price = parseFloat(priceText.replace(/[^0-9.]/g, ''));
        extractedData.price = isNaN(price) ? null : price;
      }

      // Extract availability
      if (selectors.availability) {
        extractedData.availability = $(selectors.availability).first().text().trim() || null;
      }

      // Extract image URL
      if (selectors.imageUrl) {
        const img = $(selectors.imageUrl).first();
        extractedData.imageUrl = img.attr('src') || img.attr('data-src') || img.attr('data-lazy-src') || null;

        // Make URL absolute if relative
        if (extractedData.imageUrl && typeof extractedData.imageUrl === 'string' && !extractedData.imageUrl.startsWith('http')) {
          const urlObj = new URL(url);
          if (typeof extractedData.imageUrl === 'string' && extractedData.imageUrl.startsWith('//')) {
            extractedData.imageUrl = urlObj.protocol + extractedData.imageUrl;
          } else if (typeof extractedData.imageUrl === 'string' && extractedData.imageUrl.startsWith('/')) {
            extractedData.imageUrl = `${urlObj.protocol}//${urlObj.host}${extractedData.imageUrl}`;
          }
        }
      }

      // Extract description
      if (selectors.description) {
        extractedData.description = $(selectors.description).first().text().trim() || null;
      }

      // Extract rating
      if (selectors.rating) {
        const ratingText = $(selectors.rating).first().text().trim();
        const rating = parseFloat(ratingText.match(/[\d.]+/)?.[0] || '0');
        extractedData.rating = isNaN(rating) ? null : Math.min(5, rating);
      }

      // Extract review count
      if (selectors.reviews) {
        const reviewText = $(selectors.reviews).first().text().trim();
        const reviews = parseInt(reviewText.replace(/\D/g, ''));
        extractedData.reviewCount = isNaN(reviews) ? null : reviews;
      }

      // Extract SKU
      if (selectors.sku) {
        extractedData.sku = $(selectors.sku).first().text().trim() || null;
      }
    }

    // Also extract meta tags for additional data
    const metaData = {
      ogTitle: $('meta[property="og:title"]').attr('content'),
      ogDescription: $('meta[property="og:description"]').attr('content'),
      ogImage: $('meta[property="og:image"]').attr('content'),
      ogPrice: $('meta[property="product:price:amount"]').attr('content'),
      ogCurrency: $('meta[property="product:price:currency"]').attr('content'),
      ogAvailability: $('meta[property="product:availability"]').attr('content'),
    };

    // Extract JSON-LD structured data if available
    let structuredData = null;
    const jsonLdScript = $('script[type="application/ld+json"]').first().html();
    if (jsonLdScript) {
      try {
        structuredData = JSON.parse(jsonLdScript);
      } catch (e) {
        // Ignore parsing errors
      }
    }

    return NextResponse.json({
      success: true,
      url,
      data: {
        ...extractedData,
        url,
        scrapedAt: new Date().toISOString(),
      },
      metaData,
      structuredData,
      statusCode: response.status,
    });
  } catch (error: unknown) {
    console.error('Proxy scraping error:', error);

    // Return detailed error information
    const err = error as any;
    return NextResponse.json(
      {
        success: false,
        error: err.message || 'Failed to fetch URL',
        code: err.code,
        statusCode: err.response?.status,
      },
      { status: err.response?.status || 500 }
    );
  }
}

// GET endpoint for testing
export async function GET() {
  return NextResponse.json({
    message: 'Scraper proxy endpoint',
    usage: 'POST with { url, selectors, headers }',
    example: {
      url: 'https://example.com/product',
      selectors: {
        productName: 'h1.product-title',
        price: '.price',
        availability: '.stock-status',
      },
    },
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { logger } from '@/lib/logger';
import { performHybridSearch } from '@/lib/hybrid-search';
import { contextualSearch } from '@/lib/contextual-search';
import { trackSearchEvent } from '@/lib/search-quality-metrics';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q') || '';
    const limit = parseInt(searchParams.get('limit') || '20');
    const expandQuery = searchParams.get('expand') !== 'false';
    const category = searchParams.get('category') || undefined;
    const inStockOnly = searchParams.get('inStock') !== 'false';

    if (!query) {
      return NextResponse.json({
        success: false,
        error: 'Query parameter is required'
      }, { status: 400 });
    }

    logger.info(`🔍 Enhanced hybrid search API called: "${query}"`);

    // Generate session ID for tracking
    const sessionId = request.headers.get('x-session-id') ||
                      request.headers.get('x-forwarded-for') ||
                      `anon-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    try {
      // Use enhanced contextual search with hybrid capabilities
      const { results: contextualResults, contextualInsights, qualityMetrics } = await contextualSearch(query, {
        sessionId,
        userId: searchParams.get('userId') || undefined,
        limit,
        category,
        inStockOnly,
        enablePersonalization: searchParams.get('personalization') !== 'false',
        enableSemanticClustering: true,
        enableIntentAnalysis: true,
        enableQueryExpansion: expandQuery
      });

      // Track search event
      trackSearchEvent({
        sessionId,
        query,
        searchType: 'hybrid',
        results: contextualResults.slice(0, 10).map((result, index) => ({
          productId: result.product.id,
          position: index + 1,
          score: result.score,
          title: result.product.name,
          relevanceScore: result.score,
          personalizationBoost: result.personalizationBoost
        })),
        userActions: {
          clickedResults: [],
          purchasedResults: [],
          dwellTimes: {},
          abandonedSession: false
        },
        metadata: {
          responseTime: 0,
          totalResults: contextualResults.length
        }
      });

      return NextResponse.json({
        success: true,
        data: {
          query,
          count: contextualResults.length,
          results: contextualResults,
          insights: {
            intent: (contextualInsights as any).searchIntent,
            clusters: (contextualInsights as any).semanticClusters?.slice(0, 3),
            personalizedBoosts: Object.keys(contextualInsights.personalizedBoosts).length,
            qualityScore: (qualityMetrics as any)?.relevanceScore
          }
        }
      });

    } catch (contextualError) {
      logger.warn('Enhanced search failed, falling back to basic hybrid:', contextualError as Record<string, unknown>);

      // Fallback to basic hybrid search
      const results = await performHybridSearch(query, {
        limit,
        expandQuery,
        category,
        inStockOnly
      });

      return NextResponse.json({
        success: true,
        data: {
          query,
          count: results.length,
          results,
          fallback: true
        }
      });
    }

  } catch (error) {
    logger.error('❌ Hybrid search API error:', error as Record<string, unknown>);
    return NextResponse.json({
      success: false,
      error: 'Failed to perform hybrid search',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { logger } from '@/lib/logger';
import { improvedSearch, searchHealthProducts, searchSeasonalProducts } from '@/lib/improved-search';
import { hybridQdrantSearch, checkQdrantHealth } from '@/lib/qdrant';
import { checkEndpointRateLimit, createRateLimitResponse } from '@/lib/rate-limit';
import { contextualSearch } from '@/lib/contextual-search';
import { trackSearchEvent } from '@/lib/search-quality-metrics';
import type { WCCategory, WCTag, WCImage } from '@/types/woocommerce';

// Simple in-memory cache for GET queries (short TTL)
interface SearchResultData {
  success: boolean;
  query: string;
  results: Array<Record<string, unknown>>;
  count: number;
  searchType: string;
  filters?: Record<string, unknown>;
  totalMatches?: number;
}

type CacheEntry = { body: SearchResultData; expiresAt: number };
const getCache = new Map<string, CacheEntry>();
const GET_TTL_MS = 10_000; // 10 seconds

export async function GET(request: NextRequest) {
  try {
    // Rate limiting for search endpoints
    const rl = checkEndpointRateLimit(request, 'search');
    if (!rl.success) {
      return createRateLimitResponse(rl);
    }

    const { searchParams } = new URL(request.url);

    const query = searchParams.get('q') ?? '';
    const limit = Math.min(parseInt(searchParams.get('limit') ?? '10'), 50);
    const category = searchParams.get('category') ?? undefined;
    const inStock = searchParams.get('inStock') === 'true' ? true :
                    searchParams.get('inStock') === 'false' ? false : undefined;
    const searchType = searchParams.get('type') ?? 'general';

    if (!query.trim() && searchType === 'general') {
      return NextResponse.json(
        { error: 'Query parameter is required' },
        { status: 400 }
      );
    }

    // Check cache
    const cacheKey = JSON.stringify({ q: query, limit, category, inStock, searchType });
    const cached = getCache.get(cacheKey);
    if (cached && cached.expiresAt > Date.now()) {
      return NextResponse.json(cached.body);
    }

    logger.info(`🔍 Semantic search for: "${query}" (type: ${searchType})`);

    let searchResults;

    // Try Qdrant first if available
    const qdrantHealthy = await checkQdrantHealth();

    // Generate a session ID for search tracking
    const sessionId = request.headers.get('x-session-id') ||
                      request.headers.get('x-forwarded-for') ||
                      `anon-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    if (qdrantHealthy && searchType === 'general') {
      // Use enhanced contextual search for general queries
      try {
        const { results: contextualResults } = await contextualSearch(query, {
          sessionId,
          userId: searchParams.get('userId') || undefined,
          limit,
          category,
          inStockOnly: inStock,
          enablePersonalization: searchParams.get('personalization') !== 'false',
          enableSemanticClustering: true,
          enableIntentAnalysis: true
        });

        // Track search event for quality metrics
        trackSearchEvent({
          sessionId,
          query,
          searchType: 'semantic',
          results: contextualResults.slice(0, 10).map((result, index) => ({
            productId: result.product.id,
            position: index + 1,
            score: result.score,
            title: result.product.name,
            relevanceScore: result.score,
            personalizationBoost: result.personalizedScore
          })),
          userActions: {
            clickedResults: [],
            purchasedResults: [],
            dwellTimes: {},
            abandonedSession: false
          },
          metadata: {
            responseTime: 0,
            totalResults: contextualResults.length
          }
        });

        searchResults = contextualResults.map(result => ({
          product: result.product,
          score: result.score,
          matchedTerms: (result as any).matchedTerms || ['semantic'],
          semanticRelevance: (result as any).semanticRelevance,
          personalizedScore: result.personalizedScore
        }));
      } catch (contextualError) {
        logger.warn('Contextual search failed, falling back to Qdrant:', contextualError as Record<string, unknown>);

        // Fallback to Qdrant hybrid search
        const qdrantResults = await hybridQdrantSearch(query, {
          limit,
          category,
          inStock,
          semanticWeight: 0.7
        });

        searchResults = qdrantResults.map(result => ({
          product: {
            id: result.id as number,
            slug: result.payload.slug,
            name: result.payload.name,
            price: String(result.payload.price),
            sale_price: result.payload.sale_price ? String(result.payload.sale_price) : '',
            categories: Array.isArray(result.payload.categories) ? result.payload.categories.map((c: string) => ({ name: c, slug: c })) : [],
            tags: Array.isArray(result.payload.tags) ? result.payload.tags.map((t: string) => ({ name: t, slug: t })) : [],
            in_stock: result.payload.in_stock,
            stock_status: result.payload.in_stock ? 'instock' : 'outofstock',
            featured: result.payload.featured,
            images: result.payload.image ? [{ src: result.payload.image }] : [],
            short_description: result.payload.short_description,
            average_rating: result.payload.average_rating,
            total_sales: result.payload.total_sales
          },
          score: result.score,
          matchedTerms: [result.matchType as string]
        }));
      }
    } else {
      // Fallback to improved text search
      switch (searchType) {
        case 'health':
          searchResults = await searchHealthProducts(query, limit);
          break;
        case 'seasonal':
          searchResults = await searchSeasonalProducts(limit);
          break;
        default:
          searchResults = await improvedSearch(query, {
            limit,
            category,
            inStock,
            sortBy: 'relevance'
          });
      }
    }

    // Format results for frontend
    const formattedResults = searchResults.map(result => ({
      productId: result.product.id,
      slug: result.product.slug,
      title: result.product.name,
      price: result.product.price,
      categories: result.product.categories?.map((c: string | { name: string }) => typeof c === 'string' ? c : c.name) ?? [],
      inStock: result.product.stock_status === 'instock',
      featured: result.product.featured,
      relevanceScore: result.score,
      matchedTerms: result.matchedTerms,
      image: result.product.images?.[0]?.src ?? '',
      shortDescription: typeof result.product.short_description === 'string' ? result.product.short_description.replace(/<[^>]*>/g, '').slice(0, 100) : '',
      timestamp: new Date().toISOString()
    }));

    const responseBody: SearchResultData = {
      success: true,
      query,
      results: formattedResults,
      count: formattedResults.length,
      searchType: searchType === 'general' ? 'semantic' : searchType,
      filters: { category, inStock },
      totalMatches: searchResults.length
    };

    // Cache the result
    getCache.set(cacheKey, {
      body: responseBody,
      expiresAt: Date.now() + GET_TTL_MS
    });

    // Clean old cache entries periodically
    if (Math.random() < 0.1) {
      const now = Date.now();
      for (const [key, entry] of getCache.entries()) {
        if (entry.expiresAt < now) {
          getCache.delete(key);
        }
      }
    }

    return NextResponse.json(responseBody);
  } catch (error) {
    logger.error('Semantic search error:', error as Record<string, unknown>);
    return NextResponse.json(
      {
        success: false,
        error: 'Search failed',
        message: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    // Rate limiting
    const rl = checkEndpointRateLimit(request, 'search');
    if (!rl.success) {
      return createRateLimitResponse(rl);
    }

    const body = await request.json();

    const {
      query = '',
      limit = 10,
      filters = {},
      searchType = 'general'
    } = body;

    const safeLimit = Math.min(limit, 50);

    logger.info(`🔍 POST semantic search: "${query}" (type: ${searchType})`);

    let searchResults;

    switch (searchType) {
      case 'health':
        searchResults = await searchHealthProducts(query, safeLimit);
        break;
      case 'seasonal':
        searchResults = await searchSeasonalProducts(safeLimit);
        break;
      default:
        searchResults = await improvedSearch(query, {
          limit: safeLimit,
          category: filters.category,
          inStock: filters.inStock,
          minPrice: filters.minPrice,
          maxPrice: filters.maxPrice,
          sortBy: filters.sortBy ?? 'relevance'
        });
    }

    // Format for response
    const formattedResults = searchResults.map(result => ({
      productId: result.product.id,
      slug: result.product.slug,
      title: result.product.name,
      price: result.product.price,
      salePrice: result.product.sale_price,
      categories: result.product.categories?.map((c: WCCategory) => c.name) ?? [],
      tags: result.product.tags?.map((t: WCTag) => t.name) ?? [],
      inStock: result.product.stock_status === 'instock',
      featured: result.product.featured,
      relevanceScore: result.score,
      matchedTerms: result.matchedTerms,
      image: result.product.images?.[0]?.src ?? '',
      gallery: result.product.images?.map((img: WCImage) => img.src) ?? [],
      shortDescription: result.product.short_description?.replace(/<[^>]*>/g, '').slice(0, 150),
      attributes: result.product.attributes,
      timestamp: new Date().toISOString()
    }));

    return NextResponse.json({
      success: true,
      query,
      results: formattedResults,
      count: formattedResults.length,
      searchType,
      filters,
      suggestions: searchResults.length === 0 ?
        ['Try different keywords', 'Check spelling', 'Use more general terms'] :
        []
    });

  } catch (error) {
    logger.error('POST semantic search error:', error as Record<string, unknown>);
    return NextResponse.json(
      {
        success: false,
        error: 'Search failed',
        message: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    );
  }
}

