      </Tabs>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import {
  Target,
  DollarSign,
  ShoppingCart,
  Users,
  ArrowUpRight,
  ArrowDownRight,
  Minus,
  Eye,
  BarChart3,
  Activity,
  Zap
} from 'lucide-react';

// Competitive Analysis Types
interface CompetitorProfile {
  id: string;
  name: string;
  domain: string;
  industry: string;
  marketShare: number;
  revenueEstimate: number;
  employeeCount: number;
  lastUpdated: Date;
  status: 'active' | 'dormant' | 'emerging';
  threatLevel: 'high' | 'medium' | 'low';
}

interface PriceComparison {
  product: string;
  ourPrice: number;
  competitorPrice: number;
  difference: number;
  percentageDiff: number;
  pricePosition: 'premium' | 'competitive' | 'discount';
  lastChanged: Date;
}

interface ProductComparison {
  category: string;
  ourProducts: number;
  competitorProducts: number;
  marketCoverage: number;
  gapAnalysis: {
    missingFeatures: string[];
    advantages: string[];
    opportunities: string[];
  };
}

interface ChannelAnalysis {
  channel: string;
  ourPresence: boolean;
  competitorPresence: boolean;
  effectiveness: number;
  opportunity: 'high' | 'medium' | 'low';
}

interface CompetitiveIntelligence {
  competitorId: string;
  competitorName: string;
  lastAnalysis: Date;
  overallThreatScore: number;
  keyFindings: string[];
  strategicRecommendations: string[];
  confidence: number;
}

export default function CompetitiveAnalysisWidget() {
  // State Management
  const [selectedCompetitor, setSelectedCompetitor] = useState<string>('techcorp');
  const [analysisView, setAnalysisView] = useState<'overview' | 'pricing' | 'products' | 'channels'>('overview');
  const [timeRange, setTimeRange] = useState<'7d' | '30d' | '90d'>('30d');

  // Real Data from API
  const [competitors, setCompetitors] = useState<CompetitorProfile[]>([]);

  // Fetch competitors from API
  useEffect(() => {
    const fetchCompetitors = async () => {
      try {
        const response = await fetch('/api/competitors');
