// Report System Types
interface Report {
  id: string;
  title: string;
  description: string;
  type: 'competitive_analysis' | 'market_forecast' | 'pricing_intelligence' | 'swot_analysis' | 'executive_summary' | 'custom';
  format: 'pdf' | 'excel' | 'powerpoint' | 'json' | 'csv';
  schedule: 'manual' | 'daily' | 'weekly' | 'monthly' | 'quarterly';
  recipients: string[];
  dataFilters: {
    dateRange: { start: Date; end: Date };
    competitors: string[];
    categories: string[];
    confidenceThreshold: number;
  };
  sections: ReportSection[];
  template: string;
  createdAt: Date;
  lastGenerated?: Date;
  createdBy: string;
  isActive: boolean;
}

interface ReportSection {
  id: string;
  title: string;
  type: 'summary' | 'chart' | 'table' | 'analysis' | 'recommendations' | 'trends';
  dataSource: string;
  configuration: Record<string, any>;
  order: number;
}

interface GeneratedReport {
  id: string;
  reportConfigId: string;
  title: string;
  generatedAt: Date;
  generatedBy: string;
  format: Report['format'];
  fileUrl: string;
  fileSize: number;
  downloadCount: number;
  sharedWith: string[];
  expiresAt?: Date;
}

interface ReportTemplate {
  id: string;
  name: string;
  description: string;
  type: Report['type'];
  sections: ReportSection[];
  isDefault: boolean;
  category: 'executive' | 'operational' | 'analytical' | 'strategic';
}


