// Advanced Analytics Data Collection System
import { getSession } from './memgraph';
import neo4j, { Session } from 'neo4j-driver';
import { logger } from './logger';

// Analytics event types
export interface AnalyticsEvent {
  id: string;
  userId?: string;
  sessionId: string;
  timestamp: Date;
  type: string;
  data: Record<string, unknown>;
  metadata?: {
    userAgent?: string;
    ip?: string;
    referrer?: string;
    viewport?: { width: number; height: number };
    device?: 'mobile' | 'tablet' | 'desktop';
  };
}

// Specific event types
export interface ProductViewEvent extends AnalyticsEvent {
  type: 'product_view';
  data: {
    productId: number;
    productName: string;
    category: string;
    price: number;
    source: 'search' | 'recommendation' | 'category' | 'direct' | 'featured';
    recommendationType?: string;
    dwellTime?: number;
  };
}

export interface RecommendationEvent extends AnalyticsEvent {
  type: 'recommendation_shown' | 'recommendation_clicked';
  data: {
    recommendationType: string;
    productId: number;
    position: number;
    score: number;
    confidence: number;
    factors: Record<string, number>;
    context?: Record<string, unknown>;
  };
}

export interface SearchEvent extends AnalyticsEvent {
  type: 'search_performed';
  data: {
    query: string;
    resultsCount: number;
    searchType: 'keyword' | 'semantic' | 'hybrid';
    clickedResults: number[];
    timeToFirstClick?: number;
  };
}

export interface CartEvent extends AnalyticsEvent {
  type: 'add_to_cart' | 'remove_from_cart' | 'cart_viewed' | 'checkout_started' | 'purchase_completed';
  data: {
    productId?: number;
    quantity?: number;
    cartValue?: number;
    cartItems?: Array<{ productId: number; quantity: number; price: number }>;
    orderId?: string;
    paymentMethod?: string;
  };
}

export interface UserBehaviorEvent extends AnalyticsEvent {
  type: 'page_view' | 'session_start' | 'session_end' | 'scroll_depth' | 'time_on_page';
  data: {
    path?: string;
    title?: string;
    scrollPercentage?: number;
    timeSpent?: number;
    exitIntent?: boolean;
  };
}

// Analytics collector class
export class AnalyticsCollector {
  private batchSize = 50;
  private flushInterval = 30000; // 30 seconds
  private eventQueue: AnalyticsEvent[] = [];
  private flushTimer?: NodeJS.Timeout;

  constructor() {
    this.startBatchFlush();
  }

  // Track individual events
  async track(event: AnalyticsEvent): Promise<void> {
    // Add to queue for batch processing
    this.eventQueue.push({
      ...event,
      id: event.id ?? this.generateEventId(),
      timestamp: event.timestamp ?? new Date()
    });

    // Flush immediately for critical events
    const criticalEvents = ['purchase_completed', 'checkout_started', 'error'];
    if (criticalEvents.includes(event.type)) {
      await this.flush();
    }

    // Auto-flush if batch size reached
    if (this.eventQueue.length >= this.batchSize) {
      await this.flush();
    }
  }

  // Batch flush events to storage
  async flush(): Promise<void> {
    if (this.eventQueue.length === 0) return;

    const events = [...this.eventQueue];
    this.eventQueue = [];

    try {
      await Promise.all([
        this.storeEventsInGraph(events),
        this.storeEventsInMemory(events),
        this.updateRealTimeMetrics(events)
      ]);

      logger.debug(`Flushed ${events.length} analytics events`, { count: events.length }, 'analytics');
    } catch (error) {
      logger.error('Failed to flush analytics events', error as Record<string, unknown>, 'analytics');
      // Re-queue events on failure
      this.eventQueue.unshift(...events);
    }
  }

  // Store events in MemGraph for complex analysis
  private async storeEventsInGraph(events: AnalyticsEvent[]): Promise<void> {
    const session = await getSession();

    try {
      for (const event of events) {
        await session.run(`
          MERGE (e:AnalyticsEvent {id: $id})
          SET e.userId = $userId,
              e.sessionId = $sessionId,
              e.timestamp = $timestamp,
              e.type = $type,
              e.data = $data,
              e.metadata = $metadata
        `, {
          id: event.id,
          userId: event.userId ?? null,
          sessionId: event.sessionId,
          timestamp: event.timestamp.toISOString(),
          type: event.type,
          data: JSON.stringify(event.data),
          metadata: JSON.stringify(event.metadata ?? {})
        });

        // Create relationships based on event type
        await this.createEventRelationships(session, event);
      }
    } finally {
      await session.close();
    }
  }

  // Create graph relationships for events
  private async createEventRelationships(session: Session, event: AnalyticsEvent): Promise<void> {
    switch (event.type) {
      case 'product_view':
      case 'recommendation_clicked':
        interface ProductEventData {
          productId?: number;
        }
        const productData = event.data as ProductEventData;
        const productId = productData.productId;
        if (productId) {
          await session.run(`
            MATCH (e:AnalyticsEvent {id: $eventId})
            MATCH (p:Product {id: $productId})
            MERGE (e)-[:RELATES_TO]->(p)
          `, { eventId: event.id, productId: neo4j.int(productId) });
        }
        break;

      case 'search_performed':
        // Create search query nodes for analysis
        interface SearchEventData {
          query?: string;
        }
        const searchData = event.data as SearchEventData;
        const query = searchData.query;
        if (query) {
          await session.run(`
            MATCH (e:AnalyticsEvent {id: $eventId})
            MERGE (q:SearchQuery {text: $query})
            MERGE (e)-[:SEARCHED_FOR]->(q)
          `, { eventId: event.id, query });
        }
        break;

      case 'recommendation_shown':
        // Track recommendation context
        interface RecommendationEventData {
          recommendationType?: string;
        }
        const recData = event.data as RecommendationEventData;
        const recType = recData.recommendationType;
        if (recType) {
          await session.run(`
            MATCH (e:AnalyticsEvent {id: $eventId})
            MERGE (rt:RecommendationType {name: $recType})
            MERGE (e)-[:USED_RECOMMENDATION]->(rt)
          `, { eventId: event.id, recType });
        }
        break;
    }
  }

  // Store events in memory for fast access
  private async storeEventsInMemory(events: AnalyticsEvent[]): Promise<void> {
    // This would typically go to Redis or another in-memory store
    // For now, we'll use a simple in-memory cache
    events.forEach(event => {
      analyticsCache.addEvent(event);
    });
  }

  // Update real-time metrics
  private async updateRealTimeMetrics(events: AnalyticsEvent[]): Promise<void> {
    const metrics = realTimeMetrics;

    for (const event of events) {
      switch (event.type) {
        case 'product_view':
          metrics.incrementProductViews();
          break;
        case 'recommendation_clicked':
          metrics.incrementRecommendationClicks();
          break;
        case 'search_performed':
          metrics.incrementSearches();
          break;
        case 'add_to_cart':
          metrics.incrementCartAdditions();
          break;
        case 'purchase_completed':
          interface CartEventData {
            cartValue?: number;
          }
          const cartData = event.data as CartEventData;
          const orderValue = cartData.cartValue ?? 0;
          metrics.incrementPurchases(orderValue);
          break;
      }
    }
  }

  // Start batch flush timer
  private startBatchFlush(): void {
    this.flushTimer = setInterval(() => {
      this.flush().catch(error => logger.error('Auto-flush failed', error, 'analytics'));
    }, this.flushInterval);
  }

  // Stop collector
  stop(): void {
    if (this.flushTimer) {
      clearInterval(this.flushTimer);
    }
    this.flush().catch(error => logger.error('Final flush failed', error, 'analytics'));
  }

  // Generate unique event ID
  private generateEventId(): string {
    return `evt_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Get queue status
  getQueueStatus(): { queued: number; batchSize: number } {
    return {
      queued: this.eventQueue.length,
      batchSize: this.batchSize
    };
  }
}

// In-memory analytics cache
class AnalyticsCache {
  private events: AnalyticsEvent[] = [];
  private maxSize = 10000;

  addEvent(event: AnalyticsEvent): void {
    this.events.push(event);
    if (this.events.length > this.maxSize) {
      this.events = this.events.slice(-this.maxSize);
    }
  }

  getEvents(filter?: Partial<AnalyticsEvent>): AnalyticsEvent[] {
    if (!filter) return [...this.events];

    return this.events.filter(event => {
      return Object.keys(filter).every(key => 
        event[key as keyof AnalyticsEvent] === filter[key as keyof AnalyticsEvent]
      );
    });
  }

  getEventsByTimeRange(start: Date, end: Date): AnalyticsEvent[] {
    return this.events.filter(event => 
      event.timestamp >= start && event.timestamp <= end
    );
  }

  clear(): void {
    this.events = [];
  }

  getStats(): { totalEvents: number; eventTypes: Record<string, number> } {
    const eventTypes: Record<string, number> = {};

    this.events.forEach(event => {
      eventTypes[event.type] = (eventTypes[event.type] ?? 0) + 1;
    });

    return {
      totalEvents: this.events.length,
      eventTypes
    };
  }
}

// Real-time metrics tracking
class RealTimeMetrics {
  private metrics = {
    productViews: 0,
    recommendationClicks: 0,
    searches: 0,
    cartAdditions: 0,
    purchases: 0,
    revenue: 0,
    activeUsers: new Set<string>(),
    activeSessions: new Set<string>()
  };

  private hourlyMetrics: Array<{
    hour: string;
    productViews: number;
    recommendationClicks: number;
    searches: number;
    cartAdditions: number;
    purchases: number;
    revenue: number;
  }> = [];

  incrementProductViews(): void {
    this.metrics.productViews++;
    this.updateHourlyMetrics('productViews');
  }

  incrementRecommendationClicks(): void {
    this.metrics.recommendationClicks++;
    this.updateHourlyMetrics('recommendationClicks');
  }

  incrementSearches(): void {
    this.metrics.searches++;
    this.updateHourlyMetrics('searches');
  }

  incrementCartAdditions(): void {
    this.metrics.cartAdditions++;
    this.updateHourlyMetrics('cartAdditions');
  }

  incrementPurchases(revenue: number = 0): void {
    this.metrics.purchases++;
    this.metrics.revenue += revenue;
    this.updateHourlyMetrics('purchases');
    this.updateHourlyMetrics('revenue', revenue);
  }

  trackUser(userId: string): void {
    this.metrics.activeUsers.add(userId);
  }

  trackSession(sessionId: string): void {
    this.metrics.activeSessions.add(sessionId);
  }

  private updateHourlyMetrics(metric: string, value: number = 1): void {
    const currentHour = new Date().toISOString().slice(0, 13);
    let hourlyEntry = this.hourlyMetrics.find(entry => entry.hour === currentHour);

    if (!hourlyEntry) {
      hourlyEntry = {
        hour: currentHour,
        productViews: 0,
        recommendationClicks: 0,
        searches: 0,
        cartAdditions: 0,
        purchases: 0,
        revenue: 0
      };
      this.hourlyMetrics.push(hourlyEntry);

      // Keep only last 24 hours
      if (this.hourlyMetrics.length > 24) {
        this.hourlyMetrics.shift();
      }
    }

    // Update specific metrics instead of casting
    switch (metric) {
      case 'productViews':
        hourlyEntry.productViews += value;
        break;
      case 'recommendationClicks':
        hourlyEntry.recommendationClicks += value;
        break;
      case 'searches':
        hourlyEntry.searches += value;
        break;
      case 'cartAdditions':
        hourlyEntry.cartAdditions += value;
        break;
      case 'purchases':
        hourlyEntry.purchases += value;
        break;
      case 'revenue':
        hourlyEntry.revenue += value;
        break;
    }
  }

  getMetrics() {
    const metrics = this.metrics;
    const hourlyMetrics = this.hourlyMetrics;

    return {
      ...metrics,
      activeUsers: metrics.activeUsers.size,
      activeSessions: metrics.activeSessions.size,
      hourlyMetrics: [...hourlyMetrics]
    };
  }

  getConversionRate(): number {
    return this.metrics.productViews > 0 
      ? (this.metrics.purchases / this.metrics.productViews) * 100 
      : 0;
  }

  getRecommendationCTR(): number {
    return this.metrics.recommendationClicks > 0 
      ? (this.metrics.recommendationClicks / (this.metrics.productViews ?? 1)) * 100 
      : 0;
  }

  reset(): void {
    this.metrics = {
      productViews: 0,
      recommendationClicks: 0,
      searches: 0,
      cartAdditions: 0,
      purchases: 0,
      revenue: 0,
      activeUsers: new Set<string>(),
      activeSessions: new Set<string>()
    };
  }
}

// Singleton instances
export const analyticsCollector = new AnalyticsCollector();
export const analyticsCache = new AnalyticsCache();
export const realTimeMetrics = new RealTimeMetrics();

// Utility functions for common tracking scenarios
export const trackProductView = (productId: number, options: {
  productName: string;
  category: string;
  price: number;
  source: 'search' | 'recommendation' | 'category' | 'direct' | 'featured';
  userId?: string;
  sessionId: string;
  recommendationType?: string;
  dwellTime?: number;
}): void => {
  analyticsCollector.track({
    id: '',
    userId: options.userId,
    sessionId: options.sessionId,
    timestamp: new Date(),
    type: 'product_view',
    data: {
      productId,
      productName: options.productName,
      category: options.category,
      price: options.price,
      source: options.source,
      recommendationType: options.recommendationType,
      dwellTime: options.dwellTime
    }
  });
};

export const trackRecommendationClick = (productId: number, options: {
  recommendationType: string;
  position: number;
  score: number;
  confidence: number;
  factors: Record<string, number>;
  userId?: string;
  sessionId: string;
  context?: Record<string, unknown>;
}): void => {
  analyticsCollector.track({
    id: '',
    userId: options.userId,
    sessionId: options.sessionId,
    timestamp: new Date(),
    type: 'recommendation_clicked',
    data: {
      productId,
      recommendationType: options.recommendationType,
      position: options.position,
      score: options.score,
      confidence: options.confidence,
      factors: options.factors,
      context: options.context
    }
  });
};

export const trackSearch = (query: string, options: {
  resultsCount: number;
  searchType: 'keyword' | 'semantic' | 'hybrid';
  userId?: string;
  sessionId: string;
  clickedResults?: number[];
  timeToFirstClick?: number;
}): void => {
  analyticsCollector.track({
    id: '',
    userId: options.userId,
    sessionId: options.sessionId,
    timestamp: new Date(),
    type: 'search_performed',
    data: {
      query,
      resultsCount: options.resultsCount,
      searchType: options.searchType,
      clickedResults: options.clickedResults ?? [],
      timeToFirstClick: options.timeToFirstClick
    }
  });
};

/**
 * API-specific validators for request/response validation
 * Ensures type safety at API boundaries
 */

import { NextRequest } from 'next/server';
import { z } from 'zod';
import type { FormValidationResult, ValidationError } from '@/types/type-safety';

// ============================================
// Zod Schema Definitions
// ============================================

// Product schemas
export const ProductQuerySchema = z.object({
  category: z.string().optional(),
  minPrice: z.coerce.number().min(0).optional(),
  maxPrice: z.coerce.number().positive().optional(),
  inStock: z.coerce.boolean().optional(),
  featured: z.coerce.boolean().optional(),
  sortBy: z.enum(['name', 'price', 'date', 'popularity']).optional(),
  order: z.enum(['asc', 'desc']).optional(),
  page: z.coerce.number().int().positive().default(1),
  limit: z.coerce.number().int().min(1).max(100).default(20)
});

export const ProductCreateSchema = z.object({
  name: z.string().min(1).max(200),
  description: z.string().max(5000).optional(),
  price: z.number().positive(),
  category: z.string().min(1),
  stock: z.number().int().min(0).default(0),
  images: z.array(z.string().url()).optional(),
  featured: z.boolean().default(false),
  metadata: z.record(z.unknown()).optional()
});

// Order schemas
export const OrderCreateSchema = z.object({
  items: z.array(z.object({
    productId: z.number().int().positive(),
    quantity: z.number().int().positive(),
    price: z.number().positive()
  })).min(1),
  customerId: z.string().optional(),
  shippingAddress: z.object({
    firstName: z.string().min(1),
    lastName: z.string().min(1),
    addressLine1: z.string().min(1),
    addressLine2: z.string().optional(),
    city: z.string().min(1),
    state: z.string().min(1),
    postcode: z.string().min(1),
    country: z.string().min(1),
    phone: z.string().optional()
  }),
  billingAddress: z.object({
    firstName: z.string().min(1),
    lastName: z.string().min(1),
    email: z.string().email(),
    addressLine1: z.string().min(1),
    addressLine2: z.string().optional(),
    city: z.string().min(1),
    state: z.string().min(1),
    postcode: z.string().min(1),
    country: z.string().min(1),
    phone: z.string().optional()
  }),
  paymentMethod: z.string().min(1),
  couponCode: z.string().optional()
});

// User schemas
export const UserRegistrationSchema = z.object({
  email: z.string().email(),
  password: z.string().min(8).max(100),
  firstName: z.string().min(1).max(50),
  lastName: z.string().min(1).max(50),
  phone: z.string().optional(),
  acceptTerms: z.boolean().refine(val => val === true, {
    message: 'You must accept the terms and conditions'
  })
});

export const UserLoginSchema = z.object({
  email: z.string().email().optional(),
  username: z.string().min(1).optional(),
  password: z.string().min(1)
}).refine(data => data.email || data.username, {
  message: 'Either email or username is required'
});

// Review schemas
export const ReviewCreateSchema = z.object({
  productId: z.number().int().positive(),
  rating: z.number().int().min(1).max(5),
  title: z.string().min(1).max(100).optional(),
  comment: z.string().min(10).max(5000),
  verified: z.boolean().default(false),
  images: z.array(z.string().url()).max(5).optional()
});

// Search schemas
export const SearchQuerySchema = z.object({
  q: z.string().min(1).max(200),
  category: z.string().optional(),
  filters: z.record(z.union([z.string(), z.number(), z.boolean()])).optional(),
  page: z.coerce.number().int().positive().default(1),
  limit: z.coerce.number().int().min(1).max(100).default(20),
  sort: z.enum(['relevance', 'price_asc', 'price_desc', 'date', 'rating']).optional()
});

// Analytics event schemas
export const AnalyticsEventSchema = z.object({
  eventType: z.string().min(1),
  eventData: z.record(z.unknown()),
  timestamp: z.coerce.date().optional(),
  sessionId: z.string().min(1),
  userId: z.string().optional(),
  metadata: z.record(z.unknown()).optional()
});

// ============================================
// Validation Functions
// ============================================

export async function validateRequestBody<T>(
  request: NextRequest,
  schema: z.ZodSchema<T>
): Promise<FormValidationResult<T>> {
  try {
    const body = await request.json();
    const result = schema.safeParse(body);

    if (!result.success) {
      return {
        isValid: false,
        errors: result.error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message,
          code: err.code,
          severity: 'error' as const
        }))
      };
    }

    return {
      isValid: true,
      errors: [],
      sanitized: result.data
    };
  } catch (_error) {
    return {
      isValid: false,
      errors: [{
        field: 'body',
        message: 'Invalid JSON in request body',
        severity: 'error'
      }]
    };
  }
}

export function validateQueryParams<T>(
  request: NextRequest,
  schema: z.ZodSchema<T>
): FormValidationResult<T> {
  const searchParams = request.nextUrl.searchParams;
  const params: Record<string, string> = {};

  searchParams.forEach((value, key) => {
    params[key] = value;
  });

  const result = schema.safeParse(params);

  if (!result.success) {
    return {
      isValid: false,
      errors: result.error.errors.map(err => ({
        field: err.path.join('.'),
        message: err.message,
        code: err.code,
        severity: 'error' as const
      }))
    };
  }

  return {
    isValid: true,
    errors: [],
    sanitized: result.data
  };
}

export function validateHeaders(
  request: NextRequest,
  requiredHeaders: string[]
): ValidationError[] {
  const errors: ValidationError[] = [];

  for (const header of requiredHeaders) {
    if (!request.headers.get(header)) {
      errors.push({
        field: `headers.${header}`,
        message: `Missing required header: ${header}`,
        severity: 'error'
      });
    }
  }

  return errors;
}

// ============================================
// Response Validation
// ============================================

export const ApiResponseSchema = z.object({
  success: z.boolean(),
  data: z.unknown().optional(),
  error: z.object({
    code: z.string(),
    message: z.string(),
    details: z.record(z.unknown()).optional()
  }).optional(),
  metadata: z.object({
    timestamp: z.string(),
    requestId: z.string(),
    duration: z.number().optional(),
    version: z.string().optional()
  }).optional()
});

export function createValidatedResponse<T>(
  data: T,
  status = 200,
  metadata?: Record<string, unknown>
): Response {
  const responseData = {
    success: status >= 200 && status < 300,
    data,
    metadata: {
      timestamp: new Date().toISOString(),
      requestId: crypto.randomUUID(),
      ...metadata
    }
  };

  return new Response(JSON.stringify(responseData), {
    status,
    headers: {
      'Content-Type': 'application/json'
    }
  });
}

export function createErrorResponse(
  error: string | Error | ValidationError[],
  status = 400
): Response {
  let errorData: { code: string; message: string; details?: unknown };

  if (typeof error === 'string') {
    errorData = { code: 'ERROR', message: error };
  } else if (error instanceof Error) {
    errorData = {
      code: 'ERROR',
      message: error.message,
      details: { stack: error.stack }
    };
  } else {
    errorData = {
      code: 'VALIDATION_ERROR',
      message: 'Validation failed',
      details: { errors: error }
    };
  }

  const responseData = {
    success: false,
    error: errorData,
    metadata: {
      timestamp: new Date().toISOString(),
      requestId: crypto.randomUUID()
    }
  };

  return new Response(JSON.stringify(responseData), {
    status,
    headers: {
      'Content-Type': 'application/json'
    }
  });
}

// ============================================
// Middleware Validators
// ============================================

export function createValidationMiddleware<T>(
  schema: z.ZodSchema<T>
) {
  return async (request: NextRequest) => {
    const validation = await validateRequestBody(request, schema);
    if (!validation.isValid) {
      return createErrorResponse(validation.errors, 400);
    }
    return validation.sanitized;
  };
}

export function createQueryValidationMiddleware<T>(
  schema: z.ZodSchema<T>
) {
  return (request: NextRequest) => {
    const validation = validateQueryParams(request, schema);
    if (!validation.isValid) {
      return createErrorResponse(validation.errors, 400);
    }
    return validation.sanitized;
  };
}

// ============================================
// Type Guards for API Data
// ============================================

export function isValidApiResponse(data: unknown): boolean {
  const result = ApiResponseSchema.safeParse(data);
  return result.success;
}

export function assertValidApiResponse(data: unknown): asserts data is z.infer<typeof ApiResponseSchema> {
  const result = ApiResponseSchema.safeParse(data);
  if (!result.success) {
    throw new Error(`Invalid API response: ${result.error.message}`);
  }
}

// ============================================
// Sanitization Helpers
// ============================================

export function sanitizeApiInput<T>(
  input: unknown,
  schema: z.ZodSchema<T>
): T | null {
  const result = schema.safeParse(input);
  return result.success ? result.data : null;
}

export function sanitizeAndValidate<T>(
  input: unknown,
  schema: z.ZodSchema<T>
): { data?: T; errors?: ValidationError[] } {
  const result = schema.safeParse(input);

  if (result.success) {
    return { data: result.data };
  }

  return {
    errors: result.error.errors.map(err => ({
      field: err.path.join('.'),
      message: err.message,
      code: err.code,
      severity: 'error' as const
    }))
  };
}

import { NextRequest, NextResponse } from 'next/server';
import { verify } from 'jsonwebtoken';
import { redis } from '../database';
import { z } from 'zod';

// Types for middleware
export interface AuthenticatedRequest extends NextRequest {
  user?: {
    id: string;
    email: string;
    role: string;
    sessionId: string;
  };
}

export interface ApiError {
  code: string;
  message: string;
  details?: z.ZodError | Record<string, unknown>;
  statusCode: number;
}

export interface ApiResponse<T = unknown> {
  success: boolean;
  data?: T;
  error?: ApiError;
  meta?: {
    pagination?: {
      page: number;
      limit: number;
      total: number;
      totalPages: number;
    };
    timestamp: string;
    requestId: string;
  };
}

// Error classes
export class ApiValidationError extends Error {
  constructor(public details: z.ZodError | Record<string, unknown>) {
    super('Validation failed');
    this.name = 'ApiValidationError';
  }
}

export class ApiAuthenticationError extends Error {
  constructor(message = 'Authentication required') {
    super(message);
    this.name = 'ApiAuthenticationError';
  }
}

export class ApiAuthorizationError extends Error {
  constructor(message = 'Insufficient permissions') {
    super(message);
    this.name = 'ApiAuthorizationError';
  }
}

export class ApiNotFoundError extends Error {
  constructor(resource: string) {
    super(`${resource} not found`);
    this.name = 'ApiNotFoundError';
  }
}

export class ApiRateLimitError extends Error {
  constructor(message = 'Rate limit exceeded') {
    super(message);
    this.name = 'ApiRateLimitError';
  }
}

// Response helpers
export function successResponse<T>(
  data: T,
  meta?: ApiResponse<T>['meta']
): NextResponse<ApiResponse<T>> {
  const response: ApiResponse<T> = {
    success: true,
    data,
    meta: {
      timestamp: new Date().toISOString(),
      requestId: generateRequestId(),
      ...meta
    }
  };

  return NextResponse.json(response);
}

export function errorResponse(
  error: ApiError | Error | string,
  statusCode: number = 500
): NextResponse<ApiResponse> {
  let apiError: ApiError;

  if (typeof error === 'string') {
    apiError = {
      code: 'INTERNAL_ERROR',
      message: error,
      statusCode
    };
  } else if (error instanceof ApiValidationError) {
    apiError = {
      code: 'VALIDATION_ERROR',
      message: error.message,
      details: error.details,
      statusCode: 400
    };
  } else if (error instanceof ApiAuthenticationError) {
    apiError = {
      code: 'AUTHENTICATION_ERROR',
      message: error.message,
      statusCode: 401
    };
  } else if (error instanceof ApiAuthorizationError) {
    apiError = {
      code: 'AUTHORIZATION_ERROR',
      message: error.message,
      statusCode: 403
    };
  } else if (error instanceof ApiNotFoundError) {
    apiError = {
      code: 'NOT_FOUND',
      message: error.message,
      statusCode: 404
    };
  } else if (error instanceof ApiRateLimitError) {
    apiError = {
      code: 'RATE_LIMIT_EXCEEDED',
      message: error.message,
      statusCode: 429
    };
  } else if ('code' in error && 'message' in error) {
    apiError = error as ApiError;
  } else {
    apiError = {
      code: 'INTERNAL_ERROR',
      message: error instanceof Error ? error.message : 'An unexpected error occurred',
      statusCode
    };
  }

  const response: ApiResponse = {
    success: false,
    error: apiError,
    meta: {
      timestamp: new Date().toISOString(),
      requestId: generateRequestId()
    }
  };

  return NextResponse.json(response, { status: apiError.statusCode });
}

// Authentication middleware
export async function authenticateRequest(request: NextRequest): Promise<AuthenticatedRequest> {
  const token = extractToken(request);

  if (!token) {
    throw new ApiAuthenticationError('No authentication token provided');
  }

  try {
    const jwtSecret = process.env.JWT_SECRET;
    if (!jwtSecret) {
      throw new ApiAuthenticationError('JWT secret not configured');
    }
    const payload = verify(token, jwtSecret) as any;

    // Check if token is blacklisted
    const isBlacklisted = await redis.get(`blacklist:${token}`);
    if (isBlacklisted) {
      throw new ApiAuthenticationError('Token has been revoked');
    }

    const authenticatedRequest = request as AuthenticatedRequest;
    authenticatedRequest.user = {
      id: payload.userId,
      email: payload.email,
      role: payload.role,
      sessionId: payload.sessionId
    };

    return authenticatedRequest;
  } catch (error) {
    if (error instanceof ApiAuthenticationError) {
      throw error;
    }
    throw new ApiAuthenticationError('Invalid authentication token');
  }
}

// Authorization middleware
export function requireRole(allowedRoles: string[]) {
  return (request: AuthenticatedRequest) => {
    if (!request.user) {
      throw new ApiAuthenticationError();
    }

    if (!allowedRoles.includes(request.user.role)) {
      throw new ApiAuthorizationError(
        `Access denied. Required roles: ${allowedRoles.join(', ')}`
      );
    }
  };
}

// Rate limiting middleware
export async function rateLimitMiddleware(
  request: NextRequest,
  identifier: string = 'global',
  maxRequests: number = 100,
  windowMs: number = 60000
): Promise<void> {
  const key = `rate_limit:${identifier}`;
  const window = Math.floor(Date.now() / windowMs);
  const windowKey = `${key}:${window}`;

  const current = await redis.incr(windowKey);

  if (current === 1) {
    await redis.expire(windowKey, Math.ceil(windowMs / 1000));
  }

  if (current > maxRequests) {
    throw new ApiRateLimitError(
      `Rate limit exceeded. Maximum ${maxRequests} requests per ${windowMs / 1000} seconds`
    );
  }
}

// Request validation middleware
export function validateRequestBody<T>(schema: z.ZodSchema<T>) {
  return async (request: NextRequest): Promise<T> => {
    try {
      const body = await request.json();
      return schema.parse(body);
    } catch (error) {
      if (error instanceof z.ZodError) {
        throw new ApiValidationError(error);
      }
      throw new ApiValidationError({ message: 'Invalid request body' });
    }
  };
}

export function validateQueryParams<T>(schema: z.ZodSchema<T>) {
  return (request: NextRequest): T => {
    try {
      const searchParams = request.nextUrl.searchParams;
      const params: Record<string, any> = {};

      searchParams.forEach((value, key) => {
        params[key] = value;
      });

      return schema.parse(params);
    } catch (error) {
      if (error instanceof z.ZodError) {
        throw new ApiValidationError(error);
      }
      throw new ApiValidationError({ message: 'Invalid query parameters' });
    }
  };
}

// CORS middleware
export function corsMiddleware(request: NextRequest): NextResponse<any> | null {
  const origin = request.headers.get('origin');
  const allowedOrigins = process.env.CORS_ORIGIN?.split(',') || ['http://localhost:3000'];

  if (request.method === 'OPTIONS') {
    const response = new NextResponse(null, { status: 200 });

    if (origin && allowedOrigins.includes(origin)) {
      response.headers.set('Access-Control-Allow-Origin', origin);
    }

    response.headers.set('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    response.headers.set('Access-Control-Allow-Headers', 'Content-Type, Authorization');
    response.headers.set('Access-Control-Max-Age', '86400');

    return response;
  }

  return null;
}

// Utility functions
function extractToken(request: NextRequest): string | null {
  // First try Authorization header (for API clients)
  const authHeader = request.headers.get('authorization');
  if (authHeader && authHeader.startsWith('Bearer ')) {
    return authHeader.substring(7);
  }

  // Then try cookies (for web clients)
  const cookies = request.cookies;
  const sessionToken = cookies.get('admin-session');
  if (sessionToken) {
    return sessionToken.value;
  }

  return null;
}

function generateRequestId(): string {
  return Math.random().toString(36).substring(2) + Date.now().toString(36);
}

// Middleware composition helper
export function createApiHandler<T = any>(
  handler: (request: AuthenticatedRequest) => Promise<NextResponse<ApiResponse<T>>>,
  options: {
    requireAuth?: boolean;
    allowedRoles?: string[];
    rateLimit?: { maxRequests: number; windowMs: number };
    validateBody?: z.ZodSchema<any>;
    validateQuery?: z.ZodSchema<any>;
  } = {}
) {
  return async (request: NextRequest): Promise<NextResponse<ApiResponse<T>>> => {
    try {
      // Handle CORS preflight
      const corsResponse = corsMiddleware(request);
      if (corsResponse) return corsResponse;

      let authenticatedRequest = request as AuthenticatedRequest;

      // Authentication
      if (options.requireAuth) {
        authenticatedRequest = await authenticateRequest(request);
      }

      // Authorization
      if (options.allowedRoles && authenticatedRequest.user) {
        requireRole(options.allowedRoles)(authenticatedRequest);
      }

      // Rate limiting
      if (options.rateLimit) {
        const identifier = authenticatedRequest.user?.id ||
          request.headers.get('x-forwarded-for') ||
          'anonymous';
        await rateLimitMiddleware(
          request,
          identifier,
          options.rateLimit.maxRequests,
          options.rateLimit.windowMs
        );
      }

      // Validation
      if (options.validateBody) {
        await validateRequestBody(options.validateBody)(request);
      }

      if (options.validateQuery) {
        validateQueryParams(options.validateQuery)(request);
      }

      // Execute handler
      return await handler(authenticatedRequest);

    } catch (error) {
      console.error('API error:', error);

      if (error instanceof ApiValidationError ||
          error instanceof ApiAuthenticationError ||
          error instanceof ApiAuthorizationError ||
          error instanceof ApiNotFoundError ||
          error instanceof ApiRateLimitError) {
        return errorResponse(error) as NextResponse<ApiResponse<T>>;
      }

      return errorResponse(error instanceof Error ? error.message : 'Internal server error', 500) as NextResponse<ApiResponse<T>>;
    }
  };
}

import * as jwt from 'jsonwebtoken';
import { JwtPayload } from 'jsonwebtoken';
import * as bcrypt from 'bcryptjs';
import { query, redis } from '../database';
import { User, UserSession } from '../dao/types';
import { logger } from '../logger';

// JWT configuration
const JWT_SECRET = process.env.JWT_SECRET || 'fallback-secret-key';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '7d';
const _SESSION_EXPIRES_IN = process.env.SESSION_EXPIRES_IN || '24h';

export interface TokenPayload extends JwtPayload {
  userId: string;
  email: string;
  role: string;
  sessionId: string;
}

export interface AuthResult {
  user: Omit<User, 'password_hash'>;
  token: string;
  refreshToken: string;
  expiresAt: Date;
}

export interface RefreshResult {
  token: string;
  expiresAt: Date;
}

// Authentication service
export class AuthService {
  // Hash password
  static async hashPassword(password: string): Promise<string> {
    const saltRounds = 12;
    return bcrypt.hash(password, saltRounds);
  }

  // Verify password
  static async verifyPassword(password: string, hash: string): Promise<boolean> {
    return bcrypt.compare(password, hash);
  }

  // Generate JWT token
  static generateToken(payload: Omit<TokenPayload, 'iat' | 'exp' | 'jti'>): string {
    return jwt.sign(payload, JWT_SECRET, {
      expiresIn: JWT_EXPIRES_IN,
      issuer: 'agriko-bi',
      audience: 'agriko-bi-app'
    } as jwt.SignOptions);
  }

  // Generate refresh token
  static generateRefreshToken(): string {
    return jwt.sign(
      { type: 'refresh', jti: generateUniqueId() },
      JWT_SECRET,
      { expiresIn: '30d' }
    );
  }

  // Verify and decode token
  static async verifyToken(token: string): Promise<TokenPayload> {
    try {
      // Check if token is blacklisted
      const isBlacklisted = await redis.get(`blacklist:${token}`);
      if (isBlacklisted) {
        throw new Error('Token has been revoked');
      }

      const decoded = jwt.verify(token, JWT_SECRET, {
        issuer: 'agriko-bi',
        audience: 'agriko-bi-app'
      }) as TokenPayload;

      // Verify session still exists
      const sessionExists = await this.verifySession(decoded.sessionId);
      if (!sessionExists) {
        throw new Error('Session has expired');
      }

      return decoded;
    } catch {
      throw new Error('Invalid or expired token');
    }
  }

  // Authenticate user with email and password
  static async authenticate(email: string, password: string, ipAddress?: string, userAgent?: string): Promise<AuthResult> {
    // Find user by email
    const userResult = await query<User>(
      'SELECT * FROM users WHERE email = $1 AND is_active = true',
      [email.toLowerCase()]
    );

    if (userResult.rows.length === 0) {
      throw new Error('Invalid email or password');
    }

    const user = userResult.rows[0];
    if (!user) {
      throw new Error('Invalid email or password');
    }

    // Verify password
    const isPasswordValid = await this.verifyPassword(password, user.password_hash);
    if (!isPasswordValid) {
      throw new Error('Invalid email or password');
    }

    // Create session
    const sessionId = generateUniqueId();
    const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 hours

    await query(
      `INSERT INTO user_sessions (id, user_id, session_token, ip_address, user_agent, expires_at)
       VALUES ($1, $2, $3, $4, $5, $6)`,
      [sessionId, user.id, sessionId, ipAddress, userAgent, expiresAt]
    );

    // Generate tokens
    const tokenPayload: Omit<TokenPayload, 'iat' | 'exp' | 'jti'> = {
      userId: user.id,
      email: user.email,
      role: user.role,
      sessionId
    };

    const token = this.generateToken(tokenPayload);
    const refreshToken = this.generateRefreshToken();

    // Store refresh token
    await redis.setex(`refresh:${user.id}:${sessionId}`, 30 * 24 * 60 * 60, refreshToken);

    // Update last login
    await query(
      'UPDATE users SET last_login = CURRENT_TIMESTAMP WHERE id = $1',
      [user.id]
    );

    // Remove password hash from response
    const { password_hash: _password_hash, ...userWithoutPassword } = user;

    return {
      user: userWithoutPassword,
      token,
      refreshToken,
      expiresAt
    };
  }

  // Refresh access token
  static async refreshToken(refreshToken: string, sessionId: string): Promise<RefreshResult> {
    try {
      // Verify refresh token
      const decoded = jwt.verify(refreshToken, JWT_SECRET) as any;
      if (decoded.type !== 'refresh') {
        throw new Error('Invalid refresh token');
      }

      // Check if refresh token exists in Redis
      const storedRefreshToken = await redis.get(`refresh:*:${sessionId}`);
      if (!storedRefreshToken || storedRefreshToken !== refreshToken) {
        throw new Error('Invalid or expired refresh token');
      }

      // Get session details
      const sessionResult = await query<UserSession & { user_email: string; user_role: string }>(
        `SELECT s.*, u.email as user_email, u.role as user_role
         FROM user_sessions s
         JOIN users u ON s.user_id = u.id
         WHERE s.id = $1 AND s.expires_at > CURRENT_TIMESTAMP`,
        [sessionId]
      );

      if (sessionResult.rows.length === 0) {
        throw new Error('Session not found or expired');
      }

      const session = sessionResult.rows[0];
      if (!session) {
        throw new Error('Session not found or expired');
      }

      // Generate new access token
      const tokenPayload: Omit<TokenPayload, 'iat' | 'exp' | 'jti'> = {
        userId: session.user_id,
        email: session.user_email,
        role: session.user_role,
        sessionId: session.id
      };

      const newToken = this.generateToken(tokenPayload);
      const expiresAt = new Date(Date.now() + parseExpiry(JWT_EXPIRES_IN));

      // Update session activity
      await query(
        'UPDATE user_sessions SET last_activity = CURRENT_TIMESTAMP WHERE id = $1',
        [sessionId]
      );

      return {
        token: newToken,
        expiresAt
      };
    } catch {
      throw new Error('Failed to refresh token');
    }
  }

  // Logout user
  static async logout(token: string, sessionId?: string): Promise<void> {
    try {
      // Add token to blacklist
      const decoded = jwt.decode(token) as TokenPayload;
      if (decoded && decoded.exp) {
        const ttl = decoded.exp - Math.floor(Date.now() / 1000);
        if (ttl > 0) {
          await redis.setex(`blacklist:${token}`, ttl, 'true');
        }
      }

      // Remove session
      if (sessionId) {
        await query('DELETE FROM user_sessions WHERE id = $1', [sessionId]);

        // Remove refresh token
        const pattern = `refresh:*:${sessionId}`;
        const keys = await redis.keys(pattern);
        if (keys.length > 0) {
          await redis.del(keys);
        }
      }
    } catch (error) {
      console.error('Logout error:', error);
      // Don't throw error for logout operations
    }
  }

  // Logout all sessions for a user
  static async logoutAllSessions(userId: string): Promise<void> {
    try {
      // Get all sessions for user
      const sessionsResult = await query<UserSession>(
        'SELECT id FROM user_sessions WHERE user_id = $1',
        [userId]
      );

      const sessionIds = sessionsResult.rows.map(s => s.id);

      // Delete all sessions
      await query('DELETE FROM user_sessions WHERE user_id = $1', [userId]);

      // Remove all refresh tokens for this user
      for (const sessionId of sessionIds) {
        const pattern = `refresh:${userId}:${sessionId}`;
        const keys = await redis.keys(pattern);
        if (keys.length > 0) {
          await redis.del(keys);
        }
      }
    } catch (error) {
      console.error('Logout all sessions error:', error);
    }
  }

  // Verify session exists and is valid
  static async verifySession(sessionId: string): Promise<boolean> {
    try {
      const result = await query(
        'SELECT id FROM user_sessions WHERE id = $1 AND expires_at > CURRENT_TIMESTAMP',
        [sessionId]
      );
      return result.rows.length > 0;
    } catch {
      return false;
    }
  }

  // Get user sessions
  static async getUserSessions(userId: string): Promise<UserSession[]> {
    const result = await query<UserSession>(
      `SELECT * FROM user_sessions
       WHERE user_id = $1 AND expires_at > CURRENT_TIMESTAMP
       ORDER BY last_activity DESC`,
      [userId]
    );

    return result.rows;
  }

  // Revoke specific session
  static async revokeSession(sessionId: string): Promise<boolean> {
    try {
      const result = await query('DELETE FROM user_sessions WHERE id = $1', [sessionId]);

      if (result.rowCount > 0) {
        // Remove refresh token
        const pattern = `refresh:*:${sessionId}`;
        const keys = await redis.keys(pattern);
        if (keys.length > 0) {
          await redis.del(keys);
        }
        return true;
      }
      return false;
    } catch (error) {
      console.error('Revoke session error:', error);
      return false;
    }
  }

  // Clean up expired sessions
  static async cleanupExpiredSessions(): Promise<void> {
    try {
      // Get expired session IDs
      const expiredSessions = await query<{ id: string; user_id: string }>(
        'SELECT id, user_id FROM user_sessions WHERE expires_at <= CURRENT_TIMESTAMP'
      );

      // Delete expired sessions
      await query('DELETE FROM user_sessions WHERE expires_at <= CURRENT_TIMESTAMP');

      // Remove corresponding refresh tokens
      for (const session of expiredSessions.rows) {
        const pattern = `refresh:${session.user_id}:${session.id}`;
        const keys = await redis.keys(pattern);
        if (keys.length > 0) {
          await redis.del(keys);
        }
      }

      logger.info(`Cleaned up ${expiredSessions.rows.length} expired sessions`);
    } catch (error) {
      console.error('Session cleanup error:', error);
    }
  }

  // Change user password
  static async changePassword(userId: string, currentPassword: string, newPassword: string): Promise<void> {
    // Get current user
    const userResult = await query<User>(
      'SELECT * FROM users WHERE id = $1',
      [userId]
    );

    if (userResult.rows.length === 0) {
      throw new Error('User not found');
    }

    const user = userResult.rows[0];
    if (!user) {
      throw new Error('User not found');
    }

    // Verify current password
    const isCurrentPasswordValid = await this.verifyPassword(currentPassword, user.password_hash);
    if (!isCurrentPasswordValid) {
      throw new Error('Current password is incorrect');
    }

    // Hash new password
    const newPasswordHash = await this.hashPassword(newPassword);

    // Update password
    await query(
      'UPDATE users SET password_hash = $1, updated_at = CURRENT_TIMESTAMP WHERE id = $2',
      [newPasswordHash, userId]
    );

    // Logout all other sessions
    await this.logoutAllSessions(userId);
  }
}

// Utility functions
function generateUniqueId(): string {
  return Math.random().toString(36).substring(2) + Date.now().toString(36);
}

function parseExpiry(expiry: string): number {
  const units: Record<string, number> = {
    s: 1000,
    m: 60 * 1000,
    h: 60 * 60 * 1000,
    d: 24 * 60 * 60 * 1000
  };

  const match = expiry.match(/^(\d+)([smhd])$/);
  if (!match) {
    return 60 * 60 * 1000; // Default to 1 hour
  }

  const [, num, unit] = match;
  if (!num || !unit || !units[unit]) {
    return 60 * 60 * 1000; // Default to 1 hour
  }
  return parseInt(num, 10) * units[unit];
}

// Schedule cleanup job (run every hour)
if (process.env.NODE_ENV !== 'test') {
  setInterval(() => {
    AuthService.cleanupExpiredSessions().catch(console.error);
  }, 60 * 60 * 1000);
}

import { logger } from './logger';

interface PerformanceConfig {
  enableImageOptimization: boolean;
  enableCodeSplitting: boolean;
  enablePreloading: boolean;
  enableCSSOptimization: boolean;
}

export class BundleOptimizer {
  private static instance: BundleOptimizer;
  private config: PerformanceConfig;
  private isOptimizing = false;

  constructor(config: Partial<PerformanceConfig> = {}) {
    this.config = {
      enableImageOptimization: true,
      enableCodeSplitting: true,
      enablePreloading: true,
      enableCSSOptimization: true,
      ...config
    };
  }

  static getInstance(config?: Partial<PerformanceConfig>): BundleOptimizer {
    if (!BundleOptimizer.instance) {
      BundleOptimizer.instance = new BundleOptimizer(config);
    }
    return BundleOptimizer.instance;
  }

  // Initialize all optimizations
  initializeOptimizations() {
    if (this.isOptimizing || typeof window === 'undefined') return;

    this.isOptimizing = true;

    try {
      if (this.config.enablePreloading) {
        this.preloadCriticalResources();
      }

      if (this.config.enableCSSOptimization) {
        this.optimizeCSS();
      }

      if (this.config.enableImageOptimization) {
        this.optimizeImages();
      }
    } catch (error) {
      logger.error('BundleOptimizer.initializeOptimizations failed', { error });
    }
  }

  // Preload critical resources
  private preloadCriticalResources() {
    try {
      // Skip local font preloading - using Google Fonts instead

      // Preload checkout functionality
      BundleOptimizer.preloadCheckout();
    } catch (error) {
      logger.error('BundleOptimizer.preloadCriticalResources failed', { error });
    }
  }

  // Preload checkout functionality
  private static preloadCheckout() {
    try {
      const link = document.createElement('link');
      link.rel = 'prefetch';
      link.href = '/checkout';
      document.head.appendChild(link);
    } catch (error) {
      logger.error('BundleOptimizer.preloadCheckout failed', { error });
    }
  }

  // Remove unused CSS (purge CSS optimization)
  optimizeCSS() {
    if (typeof window === 'undefined') return;

    try {
      // This would be handled by the build process, but we can track usage
      const usedClasses = new Set<string>();

      // Track used Tailwind classes
      const observer = new MutationObserver((mutations) => {
        try {
          mutations.forEach((mutation) => {
            if (!mutation.addedNodes) return;

            // Convert NodeList to Array to ensure forEach is available
            Array.from(mutation.addedNodes).forEach((node) => {
              try {
                if (node && node.nodeType === Node.ELEMENT_NODE) {
                  const element = node as Element;
                  // Handle both regular elements and SVG elements
                  let classes: string[] = [];

                  try {
                    // Use getAttribute for the most reliable className access
                    const classAttr = element.getAttribute('class');
                    if (classAttr && typeof classAttr === 'string' && classAttr.trim().length > 0) {
                      classes = classAttr.trim().split(/\s+/).filter(Boolean);
                    }

                    // Add classes to tracking set
                    classes.forEach(cls => {
                      if (cls && typeof cls === 'string') {
                        usedClasses.add(cls);
                      }
                    });

                    // Also check existing elements for their classes (recursive scan)
                    if (element.children && element.children.length > 0) {
                      Array.from(element.querySelectorAll('*')).forEach(child => {
                        try {
                          const childClassAttr = child.getAttribute('class');
                          if (childClassAttr && typeof childClassAttr === 'string' && childClassAttr.trim().length > 0) {
                            const childClasses = childClassAttr.trim().split(/\s+/).filter(Boolean);
                            childClasses.forEach(cls => {
                              if (cls && typeof cls === 'string') {
                                usedClasses.add(cls);
                              }
                            });
                          }
                        } catch {
                          // Silent fail for child className processing
                        }
                      });
                    }

                  } catch (classError) {
                    // Silent fail for className processing
                    logger.error('BundleOptimizer.optimizeCSS.className failed', { error: classError });
                  }
                }
              } catch (nodeError) {
                // Silent fail for node processing
                logger.error('BundleOptimizer.optimizeCSS.node failed', { error: nodeError });
              }
            });
          });
        } catch (mutationError) {
          logger.error('BundleOptimizer.optimizeCSS.mutation failed', { error: mutationError });
        }
      });

      // Start observing
      observer.observe(document.body, {
        childList: true,
        subtree: true,
        attributes: true,
        attributeFilter: ['class']
      });

      // Store reference for cleanup
      (window as any).__cssOptimizationObserver = observer;

    } catch (error) {
      logger.error('BundleOptimizer.optimizeCSS failed', { error });
    }
  }

  // Optimize images
  private optimizeImages() {
    try {
      // Lazy load images that are not in viewport
      const images = document.querySelectorAll('img[data-src]');

      if ('IntersectionObserver' in window) {
        const imageObserver = new IntersectionObserver((entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              const img = entry.target as HTMLImageElement;
              if (img.dataset.src) {
                img.src = img.dataset.src;
                img.removeAttribute('data-src');
                imageObserver.unobserve(img);
              }
            }
          });
        });

        Array.from(images).forEach(img => imageObserver.observe(img));
      }
    } catch (error) {
      logger.error('BundleOptimizer.optimizeImages failed', { error });
    }
  }

  // Cleanup method
  cleanup() {
    try {
      if ((window as any).__cssOptimizationObserver) {
        (window as any).__cssOptimizationObserver.disconnect();
        delete (window as any).__cssOptimizationObserver;
      }
      this.isOptimizing = false;
    } catch (error) {
      logger.error('BundleOptimizer.cleanup failed', { error });
    }
  }
}

// Export initialization function
export function initializeBundleOptimizations(config?: Partial<PerformanceConfig>) {
  if (typeof window === 'undefined') return;

  try {
    const optimizer = BundleOptimizer.getInstance(config);
    optimizer.initializeOptimizations();

    // Cleanup on page unload
    window.addEventListener('beforeunload', () => {
      optimizer.cleanup();
    });

    return optimizer;
  } catch (error) {
    logger.error('initializeBundleOptimizations failed', { error });
    return null;
  }
}

export default {
  BundleOptimizer,
  initializeBundleOptimizations
};

// Business Intelligence - Memgraph Database Operations
import { logger } from '@/lib/logger';
import { memgraphBI } from './connection';
import { NODE_TEMPLATES } from './schema';
import type {
  Competitor,
  CompetitorProduct,
  CompetitorChannel,
  CompetitorCampaign,
  CreateCompetitorRequest,
  UpdateCompetitorRequest,
  CompetitorListResponse,
  CompetitorDetailsResponse
} from '../types/competitor';
import { CompetitorStatus } from '../types/competitor';

// Memgraph node interfaces
interface MemgraphNode {
  id: string | number;
  labels: string[];
  properties: Record<string, unknown>;
}

// Unused interface
// interface MemgraphRelationship {
//   id: string | number;
//   type: string;
//   startNodeId: string | number;
//   endNodeId: string | number;
//   properties: Record<string, unknown>;
// }

// Competitor CRUD Operations
export class CompetitorOperations {
  // Create a new competitor
  async createCompetitor(data: CreateCompetitorRequest): Promise<Competitor> {
    try {
      logger.debug('Creating competitor in Memgraph', { name: data.name, domain: data.domain });

      const competitorId = `comp_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      const now = new Date();

      const result = await memgraphBI.executeQuery(NODE_TEMPLATES.COMPETITOR, {
        id: competitorId,
        name: data.name,
        domain: data.domain,
        industry: data.industry || '',
        size: data.size || '',
        founded: data.founded || null,
        category: data.category || '',
        monitoringScope: data.monitoringScope || '',
        monitoringFrequency: data.monitoringFrequency || '',
        status: CompetitorStatus.ACTIVE,
        description: '',
        headquarters: '',
        employeeCount: null,
        revenue: null,
        marketCap: null,
        fundingStage: '',
        totalFunding: null,
        keyPersonnel: JSON.stringify([]),
        businessModel: '',
        targetMarket: JSON.stringify([]),
        coreCompetencies: JSON.stringify([]),
        weaknesses: JSON.stringify([]),
        strategicPartnerships: JSON.stringify([]),
        recentNews: JSON.stringify([]),
        socialMediaPresence: JSON.stringify({}),
        websiteTraffic: JSON.stringify({}),
        searchVisibility: JSON.stringify({}),
        brandMentions: JSON.stringify({}),
        customerSentiment: JSON.stringify({}),
        innovationIndex: null,
        marketPosition: '',
        threatLevel: '',
        opportunityScore: null,
        lastAnalyzed: null,
        createdAt: now.toISOString(),
        updatedAt: now.toISOString()
      });

      if (result.records.length === 0) {
        throw new Error('Failed to create competitor in Memgraph');
      }

      const competitorNode = result.records[0]?.get('c');
      if (!competitorNode) {
        throw new Error('Failed to retrieve competitor node');
      }
      const competitor = this.nodeToCompetitor(competitorNode);

      logger.info('Competitor created successfully in Memgraph', { competitorId, name: data.name });
      return competitor;
    } catch (error) {
      logger.error('Failed to create competitor in Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Get competitor by ID
  async getCompetitorById(id: string): Promise<Competitor | null> {
    try {
      logger.debug('Fetching competitor from Memgraph', { competitorId: id });

      const result = await memgraphBI.executeQuery(
        'MATCH (c:Competitor {id: $id}) RETURN c',
        { id }
      );

      if (result.records.length === 0) {
        logger.debug('Competitor not found in Memgraph', { competitorId: id });
        return null;
      }

      const competitorNode = result.records[0]?.get('c');
      if (!competitorNode) {
        throw new Error('Failed to retrieve competitor node');
      }
      const competitor = this.nodeToCompetitor(competitorNode);

      logger.debug('Competitor fetched successfully from Memgraph', { competitorId: id });
      return competitor;
    } catch (error) {
      logger.error('Failed to fetch competitor from Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Get competitor details with relationships
  async getCompetitorDetails(id: string): Promise<CompetitorDetailsResponse | null> {
    try {
      logger.debug('Fetching competitor details from Memgraph', { competitorId: id });

      // Get competitor with related products, channels, and campaigns
      const result = await memgraphBI.executeQuery(`
        MATCH (c:Competitor {id: $id})
        OPTIONAL MATCH (c)-[:OFFERS]->(p:Product)
        OPTIONAL MATCH (c)-[:USES_CHANNEL]->(ch:Channel)
        OPTIONAL MATCH (c)-[:RUNS]->(ca:Campaign)
        RETURN c,
               collect(DISTINCT p) as products,
               collect(DISTINCT ch) as channels,
               collect(DISTINCT ca) as campaigns
      `, { id });

      if (result.records.length === 0) {
        logger.debug('Competitor details not found in Memgraph', { competitorId: id });
        return null;
      }

      const record = result.records[0];
      if (!record) {
        throw new Error('No records found');
      }
      const competitorNode = record.get('c');
      const productNodes = record.get('products') || [];
      const channelNodes = record.get('channels') || [];
      const campaignNodes = record.get('campaigns') || [];

      const competitor = this.nodeToCompetitor(competitorNode);
      const products = productNodes.map((node: MemgraphNode) => this.nodeToProduct(node));
      const channels = channelNodes.map((node: MemgraphNode) => this.nodeToChannel(node));
      const campaigns = campaignNodes.map((node: MemgraphNode) => this.nodeToCampaign(node));

      const details: CompetitorDetailsResponse = {
        ...competitor,
        products,
        channels,
        campaigns,
        monitoringStats: {
          totalProducts: products.length,
          totalChannels: channels.length,
          activeCampaigns: campaigns.filter((c: any) => c.status === 'active').length,
          lastPriceChanges: 0 // TODO: Implement price change tracking
        }
      };

      logger.debug('Competitor details fetched successfully from Memgraph', { competitorId: id });
      return details;
    } catch (error) {
      logger.error('Failed to fetch competitor details from Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // List competitors with filters
  async listCompetitors(options: {
    page?: number;
    limit?: number;
    category?: string;
    status?: string;
    industry?: string;
  } = {}): Promise<CompetitorListResponse> {
    try {
      const { page = 1, limit = 20, category, status, industry } = options;
      const skip = (page - 1) * limit;

      logger.debug('Listing competitors from Memgraph', { page, limit, category, status, industry });

      // Build filter conditions
      const filters: string[] = [];
      const params: Record<string, unknown> = { limit, skip };

      if (category) {
        filters.push('c.category = $category');
        params.category = category;
      }

      if (status) {
        filters.push('c.status = $status');
        params.status = status;
      }

      if (industry) {
        filters.push('c.industry = $industry');
        params.industry = industry;
      }

      const whereClause = filters.length > 0 ? `WHERE ${filters.join(' AND ')}` : '';

      // Get competitors with count
      const countResult = await memgraphBI.executeQuery(`
        MATCH (c:Competitor)
        ${whereClause}
        RETURN count(c) as total
      `, params);

      const total = countResult.records[0]?.get('total')?.toNumber() || 0;

      const competitorsResult = await memgraphBI.executeQuery(`
        MATCH (c:Competitor)
        ${whereClause}
        RETURN c
        ORDER BY c.createdAt DESC
        SKIP $skip
        LIMIT $limit
      `, params);

      const competitors = competitorsResult.records.map(record => {
        const competitorNode = record.get('c');
        return this.nodeToCompetitor(competitorNode);
      });

      const response: CompetitorListResponse = {
        competitors,
        total,
        page,
        limit
      };

      logger.debug('Competitors listed successfully from Memgraph', {
        count: competitors.length,
        total,
        page
      });

      return response;
    } catch (error) {
      logger.error('Failed to list competitors from Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Update competitor
  async updateCompetitor(id: string, data: UpdateCompetitorRequest): Promise<Competitor> {
    try {
      logger.debug('Updating competitor in Memgraph', { competitorId: id, fields: Object.keys(data) });

      // Build SET clauses for non-null values
      const setClauses: string[] = [];
      const params: Record<string, unknown> = { id };

      Object.entries(data).forEach(([key, value]) => {
        if (value !== undefined) {
          setClauses.push(`c.${key} = $${key}`);
          if (typeof value === 'object' && value !== null) {
            params[key] = JSON.stringify(value);
          } else {
            params[key] = value;
          }
        }
      });

      setClauses.push('c.updatedAt = $updatedAt');
      params.updatedAt = new Date().toISOString();

      if (setClauses.length === 1) { // Only updatedAt
        throw new Error('No valid fields to update');
      }

      const result = await memgraphBI.executeQuery(`
        MATCH (c:Competitor {id: $id})
        SET ${setClauses.join(', ')}
        RETURN c
      `, params);

      if (result.records.length === 0) {
        throw new Error('Competitor not found for update');
      }

      const competitorNode = result.records[0]?.get('c');
      if (!competitorNode) {
        throw new Error('Failed to retrieve competitor node');
      }
      const competitor = this.nodeToCompetitor(competitorNode);

      logger.info('Competitor updated successfully in Memgraph', { competitorId: id });
      return competitor;
    } catch (error) {
      logger.error('Failed to update competitor in Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Delete competitor
  async deleteCompetitor(id: string): Promise<boolean> {
    try {
      logger.debug('Deleting competitor from Memgraph', { competitorId: id });

      const result = await memgraphBI.executeQuery(`
        MATCH (c:Competitor {id: $id})
        DETACH DELETE c
        RETURN count(c) as deletedCount
      `, { id });

      const deletedCount = result.records[0]?.get('deletedCount')?.toNumber?.() || 0;

      if (deletedCount === 0) {
        logger.warn('Competitor not found for deletion', { competitorId: id });
        return false;
      }

      logger.info('Competitor deleted successfully from Memgraph', { competitorId: id });
      return true;
    } catch (error) {
      logger.error('Failed to delete competitor from Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Search competitors by text
  async searchCompetitors(query: string, limit: number = 20): Promise<Competitor[]> {
    try {
      logger.debug('Searching competitors in Memgraph', { query, limit });

      const result = await memgraphBI.executeQuery(`
        MATCH (c:Competitor)
        WHERE toLower(c.name) CONTAINS toLower($query)
           OR toLower(c.domain) CONTAINS toLower($query)
           OR toLower(c.industry) CONTAINS toLower($query)
           OR toLower(c.description) CONTAINS toLower($query)
        RETURN c
        ORDER BY c.name
        LIMIT $limit
      `, { query, limit });

      const competitors = result.records.map(record => {
        const competitorNode = record.get('c');
        return this.nodeToCompetitor(competitorNode);
      });

      logger.debug('Competitor search completed', {
        query,
        resultCount: competitors.length
      });

      return competitors;
    } catch (error) {
      logger.error('Failed to search competitors in Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  // Helper method to convert Memgraph node to Competitor object
  private nodeToCompetitor(node: MemgraphNode): Competitor {
    const props = node.properties;

    return {
      id: String(props.id || ''),
      name: String(props.name || ''),
      domain: String(props.domain || ''),
      industry: String(props.industry || ''),
      size: props.size as any, // Will be validated by enum
      founded: props.founded ? Number(props.founded) : undefined,
      category: props.category as any, // Will be validated by enum
      monitoringScope: props.monitoringScope as any, // Will be validated by enum
      monitoringFrequency: props.monitoringFrequency as any, // Will be validated by enum
      status: props.status as any, // Will be validated by enum
      createdAt: new Date(props.createdAt ? String(props.createdAt) : Date.now()),
      updatedAt: new Date(props.updatedAt ? String(props.updatedAt) : Date.now())
    };
  }

  // Helper method to convert Memgraph node to Product object
  private nodeToProduct(node: MemgraphNode): CompetitorProduct {
    const props = node.properties;

    return {
      id: String(props.id || ''),
      competitorId: String(props.competitorId || ''),
      name: String(props.name || ''),
      description: String(props.description || ''),
      category: String(props.category || ''),
      price: props.price ? Number(props.price) : 0,
      currency: String(props.currency || 'USD'),
      url: props.url ? String(props.url) : undefined,
      features: this.parseJsonField(props.features ? String(props.features) : null, []),
      imageUrl: props.imageUrl ? String(props.imageUrl) : undefined,
      inStock: Boolean(props.inStock),
      createdAt: new Date(props.createdAt ? String(props.createdAt) : Date.now()),
      updatedAt: new Date(props.updatedAt ? String(props.updatedAt) : Date.now())
    };
  }

  // Helper method to convert Memgraph node to Channel object
  private nodeToChannel(node: MemgraphNode): CompetitorChannel {
    const props = node.properties;

    return {
      id: String(props.id || ''),
      competitorId: String(props.competitorId || ''),
      name: String(props.name || ''),
      type: props.type as any, // Will be validated by enum
      region: String(props.region || ''),
      url: props.url ? String(props.url) : undefined,
      description: props.description ? String(props.description) : undefined,
      isActive: Boolean(props.isActive),
      createdAt: new Date(props.createdAt ? String(props.createdAt) : Date.now()),
      updatedAt: new Date(props.updatedAt ? String(props.updatedAt) : Date.now())
    };
  }

  // Helper method to convert Memgraph node to Campaign object
  private nodeToCampaign(node: MemgraphNode): CompetitorCampaign {
    const props = node.properties;

    return {
      id: String(props.id || ''),
      competitorId: String(props.competitorId || ''),
      name: String(props.name || ''),
      type: props.type as any, // Will be validated by enum
      budget: props.budget ? Number(props.budget) : undefined,
      startDate: new Date(props.startDate ? String(props.startDate) : Date.now()),
      endDate: props.endDate ? new Date(String(props.endDate)) : undefined,
      description: props.description ? String(props.description) : undefined,
      targetAudience: props.targetAudience ? String(props.targetAudience) : undefined,
      channels: this.parseJsonField(props.channels ? String(props.channels) : null, []),
      status: props.status as any, // Will be validated by enum
      createdAt: new Date(props.createdAt ? String(props.createdAt) : Date.now()),
      updatedAt: new Date(props.updatedAt ? String(props.updatedAt) : Date.now())
    };
  }

  // Helper method to safely parse JSON fields
  private parseJsonField<T>(value: string | undefined | null, defaultValue: T | string): T {
    if (!value) {
      if (typeof defaultValue === 'string') {
        try {
          return JSON.parse(defaultValue);
        } catch {
          return {} as T;
        }
      }
      return defaultValue as T;
    }

    try {
      return JSON.parse(value);
    } catch {
      if (typeof defaultValue === 'string') {
        try {
          return JSON.parse(defaultValue);
        } catch {
          return {} as T;
        }
      }
      return defaultValue as T;
    }
  }
}

