// Client-safe search wrapper that prevents worker spawning
// This module can be safely imported on the client side

import { logger } from '@/lib/logger';

export interface SearchResult {
  productId: number;
  slug: string;
  title: string;
  price: string;
  categories: string[];
  inStock: boolean;
  featured: boolean;
  relevanceScore: number;
  matchedTerms: string[];
  image: string;
  shortDescription: string;
  timestamp: string;
}

export interface SearchOptions {
  limit?: number;
  category?: string;
  inStockOnly?: boolean;
  enablePersonalization?: boolean;
  sessionId?: string;
  userId?: string;
}

export interface SearchResponse {
  success: boolean;
  query: string;
  results: SearchResult[];
  count: number;
  searchType: string;
  qualityMetrics?: any;
  contextualInsights?: any;
}

/**
 * Client-safe search function that uses fetch to call API routes
 * This prevents any worker processes from being spawned on the client
 */
export async function performClientSearch(
  query: string,
  options: SearchOptions = {}
): Promise<SearchResponse> {
  try {
    const searchParams = new URLSearchParams({
      q: query,
      limit: String(options.limit || 20),
      ...(options.category && { category: options.category }),
      ...(options.inStockOnly !== undefined && { inStock: String(options.inStockOnly) }),
      ...(options.enablePersonalization !== undefined && { personalization: String(options.enablePersonalization) }),
      ...(options.sessionId && { sessionId: options.sessionId }),
      ...(options.userId && { userId: options.userId })
    });

    const response = await fetch(`/api/search/contextual?${searchParams}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error(`Search failed: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    logger.error('Client search error:', error as Record<string, unknown>);
    return {
      success: false,
      query,
      results: [],
      count: 0,
      searchType: 'client_error',
    };
  }
}

/**
 * Client-safe semantic search with fallback
 */
export async function performSemanticSearch(
  query: string,
  options: SearchOptions = {}
): Promise<SearchResponse> {
  try {
    const searchParams = new URLSearchParams({
      q: query,
      limit: String(options.limit || 20),
      ...(options.category && { category: options.category }),
      ...(options.inStockOnly !== undefined && { inStock: String(options.inStockOnly) }),
    });

    const response = await fetch(`/api/search/semantic?${searchParams}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error(`Semantic search failed: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    logger.error('Client semantic search error:', error as Record<string, unknown>);
    return {
      success: false,
      query,
      results: [],
      count: 0,
      searchType: 'semantic_error',
    };
  }
}

/**
 * Client-safe hybrid search
 */
export async function performHybridClientSearch(
  query: string,
  options: SearchOptions = {}
): Promise<SearchResponse> {
  try {
    const searchParams = new URLSearchParams({
      q: query,
      limit: String(options.limit || 20),
      ...(options.category && { category: options.category }),
      ...(options.inStockOnly !== undefined && { inStock: String(options.inStockOnly) }),
      expand: 'true',
    });

    const response = await fetch(`/api/search/hybrid?${searchParams}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error(`Hybrid search failed: ${response.status}`);
    }

    const data = await response.json();

    // Transform hybrid response to match SearchResponse interface
    return {
      success: data.success,
      query: data.data.query,
      results: data.data.results,
      count: data.data.count,
      searchType: 'hybrid',
      contextualInsights: data.data.insights,
    };
  } catch (error) {
    logger.error('Client hybrid search error:', error as Record<string, unknown>);
    return {
      success: false,
      query,
      results: [],
      count: 0,
      searchType: 'hybrid_error',
    };
  }
}

/**
 * Check if we're running on the client side
 */
export function isClient(): boolean {
  return typeof window !== 'undefined';
}

/**
 * Check if we're running on the server side
 */
export function isServer(): boolean {
  return typeof window === 'undefined';
}

/**
 * Safe import wrapper that only works on server side
 */
export async function safeServerImport<T>(
  importFn: () => Promise<T>
): Promise<T | null> {
  if (isServer()) {
    try {
      return await importFn();
    } catch (error) {
      logger.error('Server import failed:', error as Record<string, unknown>);
      return null;
    }
  }
  return null;
}

import { z } from 'zod';
import { sanitizeHtml, sanitizeUrl, sanitizeFileName } from '@/lib/sanitize';
import { logger } from '@/lib/logger';
import { createValidationError } from '@/lib/error-handler';

// Enhanced validation schemas with comprehensive sanitization
export const EnhancedValidation = {
  // Common field validators with built-in sanitization
  email: z.string()
    .trim()
    .toLowerCase()
    .email('Invalid email format')
    .max(254, 'Email too long')
    .refine(email => !email.includes('<script'), 'Email contains malicious content'),

  url: z.string()
    .trim()
    .url('Invalid URL format')
    .max(2048, 'URL too long')
    .refine(url => {
      // Only allow http and https protocols
      return url.startsWith('http://') || url.startsWith('https://');
    }, 'Only HTTP and HTTPS protocols are allowed')
    .transform(url => sanitizeUrl(url))
    .refine(url => url.length > 0, 'URL failed sanitization'),

  filename: z.string()
    .trim()
    .min(1, 'Filename required')
    .max(255, 'Filename too long')
    .transform(filename => sanitizeFileName(filename))
    .refine(filename => filename.length > 0, 'Filename failed sanitization'),

  html: z.string()
    .transform(html => sanitizeHtml(html, 'strict'))
    .refine(html => html.length <= 10000, 'HTML content too large'),

  richText: z.string()
    .transform(html => sanitizeHtml(html, 'permissive'))
    .refine(html => html.length <= 50000, 'Rich text content too large'),

  plainText: z.string()
    .trim()
    .max(1000, 'Text too long')
    .transform(text => sanitizeHtml(text, 'textOnly')),

  password: z.string()
    .min(8, 'Password must be at least 8 characters')
    .max(128, 'Password too long')
    .regex(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/, 'Password must contain uppercase, lowercase, and number'),

  id: z.number()
    .int('ID must be an integer')
    .positive('ID must be positive')
    .max(2147483647, 'ID too large'),

  slug: z.string()
    .trim()
    .toLowerCase()
    .min(1, 'Slug required')
    .max(100, 'Slug too long')
    .regex(/^[a-z0-9-]+$/, 'Slug can only contain lowercase letters, numbers, and hyphens'),

  currency: z.string()
    .regex(/^\d+(\.\d{2})?$/, 'Invalid currency format (use 0.00)')
    .transform(str => parseFloat(str))
    .refine(num => num >= 0, 'Currency must be non-negative')
    .refine(num => num <= 999999.99, 'Currency amount too large'),

  phoneNumber: z.string()
    .trim()
    .regex(/^\+?[\d\s\-\(\)]{7,20}$/, 'Invalid phone number format')
    .transform(phone => phone.replace(/[^\d+]/g, '')),

  ipAddress: z.string()
    .trim()
    .refine(ip => {
      // IPv4 validation with proper range checking
      const ipv4Regex = /^(\d{1,3}\.){3}\d{1,3}$/;
      if (ipv4Regex.test(ip)) {
        const octets = ip.split('.').map(Number);
        return octets.every(octet => octet >= 0 && octet <= 255);
      }

      // Simple IPv6 validation (more lenient for common formats)
      const ipv6Regex = /^([0-9a-fA-F]{0,4}:){7}[0-9a-fA-F]{0,4}$/;
      return ipv6Regex.test(ip);
    }, 'Invalid IP address format'),

  userAgent: z.string()
    .trim()
    .max(500, 'User agent too long')
    .transform(ua => sanitizeHtml(ua, 'textOnly')),

  // JSON validation with depth limits
  safeJson: z.string()
    .refine(str => {
      try {
        const parsed: unknown = JSON.parse(str);
        return JSON.stringify(parsed).length <= 10000; // Limit JSON size
      } catch {
        return false;
      }
    }, 'Invalid JSON or JSON too large')
    .transform(str => JSON.parse(str)),
};

// Product validation schemas with business logic
const ProductValidation = {
