// Extract health-related keywords from text
export function extractHealthKeywords(text: string): string[] {
  const lowerText = text.toLowerCase();
  const foundKeywords: string[] = [];

  // Check all keyword categories
  Object.values(healthKeywords).forEach(keywordList => {
    keywordList.forEach(keyword => {
      if (lowerText.includes(keyword.toLowerCase())) {
        foundKeywords.push(keyword);
      }
    });
  });

  // Remove duplicates and return
  return Array.from(new Set(foundKeywords));

}

// Enhanced text preparation with health keywords and nutritional information
export function prepareTextForEmbedding(
  title: string, 
  description: string, 
  categories?: string[],
  attributes?: Array<{name: string, options: string[]}>,
  tags?: Array<{name: string}>,
  healthBenefits?: string[]
): string {
  const parts = [];

  // Start with enriched title context
  const enrichedTitle = enrichProductTitle(title, categories);
  parts.push(enrichedTitle);

  if (description) {
    const enrichedDescription = enrichProductDescription(description);
    parts.push(enrichedDescription);
  }

  if (categories && categories.length > 0) {
    // Add category context with domain mapping
    const categoryContext = mapCategoriesWithContext(categories);
    parts.push(`Product Category: ${categoryContext}`);
  }

  // Add product attributes with enhanced context
  if (attributes && attributes.length > 0) {
    const attributeContext = enhanceAttributes(attributes);
    parts.push(`Product Properties: ${attributeContext}`);
  }

  // Add tags with contextual expansion
  if (tags && tags.length > 0) {
    const tagContext = expandTagsWithContext(tags);
    parts.push(`Product Tags: ${tagContext}`);
  }

  // Add extracted or provided health benefits with expansion
  if (healthBenefits && healthBenefits.length > 0) {
    const expandedBenefits = expandHealthBenefits(healthBenefits);
    parts.push(`Health Benefits: ${expandedBenefits}`);
  }

  // Extract and expand health keywords from existing text
  const combinedText = parts.join(' ');
  const keywords = extractHealthKeywords(combinedText);
  if (keywords.length > 0) {
    const expandedKeywords = expandHealthKeywords(keywords);
    parts.push(`Health Properties: ${expandedKeywords}`);
  }

  // Add semantic domain context
  const domainContext = inferDomainContext(combinedText);
  if (domainContext.length > 0) {
    parts.push(`Domain Context: ${domainContext.join(', ')}`);
  }

  return parts.join(' | ');
}

// Enrich product title with category context
function enrichProductTitle(title: string, categories?: string[]): string {
  let enriched = title;

  // Add organic context if not present
  if (categories?.some(cat => cat.toLowerCase().includes('organic')) && 
      !title.toLowerCase().includes('organic')) {
    enriched = `Organic ${enriched}`;
  }

  // Add food/supplement context based on categories
  if (categories?.some(cat => 
    ['spices', 'herbs', 'food', 'ingredients', 'supplements'].some(type => 
      cat.toLowerCase().includes(type)))) {
    if (!title.toLowerCase().match(/\b(food|spice|herb|supplement|ingredient)\b/)) {
      enriched = `${enriched} (natural food ingredient)`;
    }
  }

  return enriched;
}

// Enhance product description with health/agricultural context
function enrichProductDescription(description: string): string {
  let enhanced = description;

  // Add context cues for better semantic understanding
  const healthPatterns = [
    { pattern: /\b(contains|rich in|source of)\b/gi, context: 'nutritional source' },
    { pattern: /\b(helps|supports|promotes|aids)\b/gi, context: 'health benefit' },
    { pattern: /\b(traditional|ancient|centuries)\b/gi, context: 'traditional medicine' },
    { pattern: /\b(fresh|natural|pure|raw)\b/gi, context: 'natural product' }
  ];

  for (const {pattern, context} of healthPatterns) {
    if (pattern.test(enhanced) && !enhanced.includes(context)) {
      enhanced = `${enhanced} [${context}]`;
    }
  }

  return enhanced;
}

// Map categories with domain-specific context
function mapCategoriesWithContext(categories: string[]): string {
  const categoryMap: Record<string, string[]> = {
    'spices': ['culinary spice', 'cooking ingredient', 'flavor enhancer', 'seasoning'],
    'herbs': ['medicinal herb', 'healing plant', 'herbal remedy', 'natural medicine'],
    'organic': ['certified organic', 'pesticide-free', 'natural farming', 'sustainable agriculture'],
    'supplements': ['dietary supplement', 'nutritional support', 'health supplement', 'wellness product'],
    'tea': ['herbal tea', 'medicinal tea', 'wellness beverage', 'therapeutic drink'],
    'honey': ['natural sweetener', 'bee product', 'raw honey', 'medicinal honey'],
    'oil': ['essential oil', 'natural oil', 'therapeutic oil', 'aromatic oil']
  };

  const expandedCategories = categories.flatMap(category => {
    const lowerCat = category.toLowerCase();
    for (const [key, expansions] of Object.entries(categoryMap)) {
      if (lowerCat.includes(key)) {
        return [category, ...expansions];
      }
    }
    return [category];
  });

  // Use Array.from instead of spread operator for better compatibility
  return Array.from(new Set(expandedCategories)).join(', ');

}

// Enhance attributes with contextual meaning
function enhanceAttributes(attributes: Array<{name: string, options: string[]}>): string {
  const attributeContext: Record<string, string> = {
    'weight': 'package size',
    'origin': 'geographical source',
    'grade': 'quality level',
    'processing': 'preparation method',
    'certification': 'quality assurance',
    'purity': 'concentration level'
  };

  return attributes
    .map(attr => {
      const context = attributeContext[attr.name.toLowerCase()] ?? attr.name;
      return `${context}: ${attr.options.join(', ')}`;
    })
    .join('; ');
}

// Expand tags with related concepts
function expandTagsWithContext(tags: Array<{name: string}>): string {
  const tagExpansions: Record<string, string[]> = {
    'antioxidant': ['free radical scavenger', 'oxidative stress protection', 'cellular protection'],
    'anti-inflammatory': ['inflammation reducer', 'pain relief', 'swelling reduction'],
    'digestive': ['stomach health', 'gut wellness', 'digestion support'],
    'immune': ['immunity booster', 'defense system', 'resistance building'],
    'energy': ['vitality enhancer', 'stamina support', 'fatigue fighter'],
    'detox': ['cleansing agent', 'toxin removal', 'purification support']
  };

  const expandedTags = tags.flatMap(tag => {
    const lowerTag = tag.name.toLowerCase();
    const expansions = tagExpansions[lowerTag] ?? [];
    return [tag.name, ...expansions];
  });

  // Use Array.from instead of spread operator for better compatibility
  return Array.from(new Set(expandedTags)).join(', ');

}

// Expand health benefits with related terms
function expandHealthBenefits(benefits: string[]): string {
  const benefitExpansions: Record<string, string[]> = {
    'heart health': ['cardiovascular support', 'circulation improvement', 'blood pressure regulation'],
    'brain health': ['cognitive function', 'memory enhancement', 'mental clarity'],
    'bone health': ['skeletal strength', 'calcium absorption', 'joint support'],
    'skin health': ['dermal wellness', 'complexion improvement', 'skin vitality'],
    'weight management': ['metabolism support', 'appetite control', 'fat burning'],
    'blood sugar': ['glucose regulation', 'diabetes support', 'insulin sensitivity']
  };

  const expandedBenefits = benefits.flatMap(benefit => {
    const lowerBenefit = benefit.toLowerCase();
    for (const [key, expansions] of Object.entries(benefitExpansions)) {
      if (lowerBenefit.includes(key)) {
        return [benefit, ...expansions];
      }
    }
    return [benefit];
  });

  // Use Array.from instead of spread operator for better compatibility
  return Array.from(new Set(expandedBenefits)).join(', ');

}

// Expand health keywords with synonyms
function expandHealthKeywords(keywords: string[]): string {
  const keywordSynonyms: Record<string, string[]> = {
    'vitamin': ['nutrient', 'essential vitamin', 'micronutrient'],
    'mineral': ['trace element', 'essential mineral', 'micronutrient'],
    'protein': ['amino acids', 'muscle building', 'tissue repair'],
    'fiber': ['dietary fiber', 'digestive health', 'gut health'],
    'antioxidants': ['free radical fighters', 'cellular protection', 'aging prevention'],
    'omega-3': ['essential fatty acids', 'brain food', 'heart healthy fats']
  };

  const expandedKeywords = keywords.flatMap(keyword => {
    const lowerKeyword = keyword.toLowerCase();
    const synonyms = keywordSynonyms[lowerKeyword] ?? [];
    return [keyword, ...synonyms];
  });

  // Use Array.from instead of spread operator for better compatibility
  return Array.from(new Set(expandedKeywords)).join(', ');

}

// Infer domain context from the product text
function inferDomainContext(text: string): string[] {
  const lowerText = text.toLowerCase();
  const domains: string[] = [];

  // Agricultural/food domain
  if (lowerText.match(/\b(organic|natural|fresh|raw|farm|harvest|ingredient|spice|herb)\b/)) {
    domains.push('agricultural food product');
  }

  // Health/wellness domain
  if (lowerText.match(/\b(health|wellness|medicinal|therapeutic|healing|remedy|supplement)\b/)) {
    domains.push('health and wellness product');
  }

  // Traditional medicine domain
  if (lowerText.match(/\b(traditional|ancient|ayurvedic|herbal|folk|remedy|medicine)\b/)) {
    domains.push('traditional medicine');
  }

  // Nutritional domain
  if (lowerText.match(/\b(vitamin|mineral|nutrient|nutrition|dietary|supplement)\b/)) {
    domains.push('nutritional product');
  }

  // Culinary domain
  if (lowerText.match(/\b(cooking|culinary|kitchen|recipe|flavor|seasoning|taste)\b/)) {
    domains.push('culinary ingredient');
  }

  return domains;
}

// Enhanced embedding generation with text preprocessing
export async function generateEnhancedEmbedding(text: string, dimensions: number = 768): Promise<number[]> {
  // Skip during static generation - return dummy embedding
  if (process.env.NEXT_PHASE === 'phase-production-build' ||
      process.env.NODE_ENV === 'production' ||
      (global as any).__NEXT_PREBUILD__ ||
      (global as any).__NEXT_DATA_COLLECTION__) {
    console.warn('Skipping enhanced embedding generation during static generation');
    return new Array(dimensions).fill(0);
  }

  try {
    if (!embeddingManager?.isReady()) {
      await initializeEmbedder();
    }

    // Text preprocessing for better embedding quality
    const processedText = preprocessText(text);

    // Get the embedder from the manager
    const embedder = await embeddingManager?.getEmbedder();
    if (!embedder) {
      throw new Error('Embedder not initialized');
    }

    const output = await embedder(processedText, { pooling: 'mean', normalize: true });

    // Convert tensor to array and get the first embedding
    if (!output?.data) {
      throw new Error('Invalid embedder output: missing data');
    }

    const outputData = output.data;
    const embedding: number[] = [];

    // Convert the typed array to a regular array of numbers
    for (let i = 0; i < outputData.length; i++) {
      embedding.push(Number(outputData[i]));
    }

    // MPNet produces 768 dimensions natively
    const targetDimensions = dimensions || 768;
    if (embedding.length < targetDimensions) {
      // Pad with zeros
      const padding = new Array(targetDimensions - embedding.length).fill(0);
      embedding.push(...padding);
    } else if (embedding.length > targetDimensions) {
      // Truncate to target dimensions
      embedding.splice(targetDimensions);
    }

    return embedding;
  } catch (error) {
    logger.error('❌ Failed to generate enhanced embedding:', error as Record<string, unknown>);
    throw error;
  }
}

// Text preprocessing function
function preprocessText(text: string): string {
  // Remove excessive whitespace and normalize
  let processed = text.replace(/\s+/g, ' ').trim();

  // Convert to lowercase for consistency (embedding model handles this well)
  processed = processed.toLowerCase();

  // Remove common stop words that don't add semantic value for product search
  const stopWords = ['the', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did', 'will', 'would', 'should', 'could', 'can', 'may', 'might', 'must', 'shall'];

  // Only remove stop words if the text is long enough to preserve meaning
  if (processed.split(' ').length > 10) {
    const words = processed.split(' ');
    const filteredWords = words.filter(word => !stopWords.includes(word) || word.length < 3);
    processed = filteredWords.join(' ');
  }

  return processed;
}

// Semantic chunking for long descriptions
export function semanticChunking(text: string, maxChunkSize: number = 500): string[] {
  if (text.length <= maxChunkSize) {
    return [text];
  }

  const chunks: string[] = [];
  const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);

  let currentChunk = '';

  for (const sentence of sentences) {
    const trimmedSentence = sentence.trim();
    if (currentChunk.length + trimmedSentence.length + 1 <= maxChunkSize) {
      currentChunk += (currentChunk.length > 0 ? '. ' : '') + trimmedSentence;
    } else {
      if (currentChunk.length > 0) {
        chunks.push(currentChunk + '.');
      }
      currentChunk = trimmedSentence;
    }
  }

  if (currentChunk.length > 0) {
    chunks.push(currentChunk + '.');
  }

  return chunks;
}

// Enhanced Enterprise-Grade Webhook Security System
import crypto from 'crypto';
import { logger } from '@/lib/logger';
import { config } from '@/lib/unified-config';
import { monitoring } from '@/lib/monitoring-observability';

// Security configuration
export interface WebhookSecurityConfig {
  maxPayloadSize: number;          // Maximum payload size in bytes
  signatureTimeout: number;        // Max age for signatures in seconds
  allowedIpRanges: string[];       // Allowed IP CIDR ranges
  requireHttps: boolean;           // Require HTTPS in production
  rotateSecretsAfter: number;      // Rotate secrets after N days
  maxFailedAttempts: number;       // Max failed attempts before blocking
  blockDuration: number;           // Block duration in seconds
  enableReplayProtection: boolean; // Prevent replay attacks
  replayWindowMs: number;          // Replay protection window
}

// Default security configuration
const DEFAULT_SECURITY_CONFIG: WebhookSecurityConfig = {
  maxPayloadSize: 1024 * 1024,     // 1MB
  signatureTimeout: 300,           // 5 minutes
  allowedIpRanges: [],             // Empty = allow all (configure in production)
  requireHttps: process.env.NODE_ENV === 'production',
  rotateSecretsAfter: 30,          // 30 days
  maxFailedAttempts: 5,
  blockDuration: 900,              // 15 minutes
  enableReplayProtection: true,
  replayWindowMs: 300000           // 5 minutes
};

// Webhook security result
export interface WebhookSecurityResult {
  allowed: boolean;
  reason?: string;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  metadata: {
    sourceIp: string;
    userAgent?: string;
    timestamp: number;
    payloadSize: number;
    signatureValid: boolean;
  };
}

// IP blocking cache (in production, use Redis)
const blockedIps = new Map<string, { blockedUntil: number; attempts: number }>();
const usedNonces = new Set<string>();

// Cleanup interval reference
let cleanupInterval: NodeJS.Timeout | null = null;

// Initialize cleanup
function startCleanup() {
  if (cleanupInterval) {
    clearInterval(cleanupInterval);
  }

  cleanupInterval = setInterval(() => {
    const now = Date.now();

    // Clean blocked IPs
    for (const [ip, data] of blockedIps.entries()) {
      if (data.blockedUntil <= now) {
        blockedIps.delete(ip);
      }
    }

    // Clean old nonces (keep only last hour)
    if (usedNonces.size > 10000) {
      usedNonces.clear(); // Simple cleanup for memory
    }
  }, 60000); // Cleanup every minute
}

// Stop cleanup
function stopCleanup() {
  if (cleanupInterval) {
    clearInterval(cleanupInterval);
    cleanupInterval = null;
  }
}

// Start cleanup automatically
startCleanup();

export class EnhancedWebhookSecurity {
  private config: WebhookSecurityConfig;

  constructor(customConfig?: Partial<WebhookSecurityConfig>) {
    this.config = { ...DEFAULT_SECURITY_CONFIG, ...customConfig };
  }

  /**
   * Comprehensive webhook security validation
   */
  async validateWebhookSecurity(
    request: {
      headers: Headers;
      body: string;
      url: string;
      method: string;
    },
    sourceIp: string
  ): Promise<WebhookSecurityResult> {
    const metadata = {
      sourceIp,
      userAgent: request.headers.get('user-agent') || undefined,
      timestamp: Date.now(),
      payloadSize: Buffer.byteLength(request.body, 'utf8'),
      signatureValid: false
    };

    try {
      // 1. Check if IP is blocked
      const ipBlockCheck = this.checkIpBlocking(sourceIp);
      if (!ipBlockCheck.allowed) {
        monitoring.recordMetric('webhook.security.ip_blocked', 1, { ip: sourceIp });
        return {
          allowed: false,
          reason: 'IP address is temporarily blocked due to security violations',
          riskLevel: 'high',
          metadata
        };
      }

      // 2. Validate request method
      if (request.method !== 'POST') {
        this.recordSecurityViolation(sourceIp, 'invalid_method');
        return {
          allowed: false,
          reason: 'Invalid HTTP method - only POST allowed',
          riskLevel: 'medium',
          metadata
        };
      }

      // 3. Validate HTTPS in production
      if (this.config.requireHttps && !request.url.startsWith('https://')) {
        this.recordSecurityViolation(sourceIp, 'non_https');
        return {
          allowed: false,
          reason: 'HTTPS required in production environment',
          riskLevel: 'high',
          metadata
        };
      }

      // 4. Check payload size
      if (metadata.payloadSize > this.config.maxPayloadSize) {
        this.recordSecurityViolation(sourceIp, 'payload_too_large');
        return {
          allowed: false,
          reason: `Payload size ${metadata.payloadSize} exceeds maximum ${this.config.maxPayloadSize}`,
          riskLevel: 'medium',
          metadata
        };
      }

      // 5. Validate IP whitelist (if configured)
      if (this.config.allowedIpRanges.length > 0) {
        const ipAllowed = this.isIpAllowed(sourceIp);
        if (!ipAllowed) {
          this.recordSecurityViolation(sourceIp, 'ip_not_whitelisted');
          return {
            allowed: false,
            reason: 'Source IP not in allowed ranges',
            riskLevel: 'high',
            metadata
          };
        }
      }

      // 6. Validate webhook signature with enhanced security
      const signatureResult = await this.validateEnhancedSignature(request, sourceIp);
      metadata.signatureValid = signatureResult.valid;

      if (!signatureResult.valid) {
        this.recordSecurityViolation(sourceIp, 'invalid_signature');
        return {
          allowed: false,
          reason: signatureResult.reason || 'Invalid webhook signature',
          riskLevel: 'critical',
          metadata
        };
      }

      // 7. Check for replay attacks
      if (this.config.enableReplayProtection) {
        const replayCheck = this.checkReplayAttack(request, sourceIp);
        if (!replayCheck.allowed) {
          this.recordSecurityViolation(sourceIp, 'replay_attack');
          return {
            allowed: false,
            reason: 'Potential replay attack detected',
            riskLevel: 'critical',
            metadata
          };
        }
      }

      // 8. Validate content type
      const contentType = request.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        this.recordSecurityViolation(sourceIp, 'invalid_content_type');
        return {
          allowed: false,
          reason: 'Invalid content type - application/json required',
          riskLevel: 'medium',
          metadata
        };
      }

      // 9. Check for suspicious headers
      const suspiciousHeaderCheck = this.checkSuspiciousHeaders(request.headers, sourceIp);
      if (!suspiciousHeaderCheck.allowed) {
        return {
          allowed: false,
          reason: suspiciousHeaderCheck.reason,
          riskLevel: 'high',
          metadata
        };
      }

      // All checks passed
      monitoring.recordMetric('webhook.security.validated', 1, { ip: sourceIp });
      return {
        allowed: true,
        riskLevel: 'low',
        metadata
      };

    } catch (error) {
      logger.error('Webhook security validation error:', error as Record<string, unknown>);
      monitoring.recordMetric('webhook.security.validation_error', 1, { ip: sourceIp });

      return {
        allowed: false,
        reason: 'Security validation failed',
        riskLevel: 'critical',
        metadata
      };
    }
  }

  /**
   * Enhanced signature validation with multiple security layers
   */
  private async validateEnhancedSignature(
    request: { headers: Headers; body: string },
    sourceIp: string
  ): Promise<{ valid: boolean; reason?: string }> {
    const signature = request.headers.get('x-wc-webhook-signature');
    const topic = request.headers.get('x-wc-webhook-topic');
    const timestamp = request.headers.get('x-wc-webhook-timestamp');
    const _delivery = request.headers.get('x-wc-webhook-delivery');

    if (!signature) {
      return { valid: false, reason: 'Missing webhook signature' };
    }

    if (!topic) {
      return { valid: false, reason: 'Missing webhook topic' };
    }

    // Check signature timestamp (if provided)
    if (timestamp) {
      const signatureAge = Date.now() / 1000 - parseInt(timestamp);
      if (signatureAge > this.config.signatureTimeout) {
        monitoring.recordMetric('webhook.security.signature_expired', 1, { ip: sourceIp });
        return { valid: false, reason: 'Signature timestamp expired' };
      }
    }

    // Validate primary signature
    const webhookSecret = config.webhooks.secret || 'default-webhook-secret';
    const primaryValid = this.verifyHmacSignature(signature, request.body, webhookSecret);
    if (!primaryValid) {
      return { valid: false, reason: 'Primary signature verification failed' };
    }

    // Validate against backup secret (if configured)
    const backupSecret = process.env.WEBHOOK_BACKUP_SECRET;
    if (backupSecret) {
      const backupValid = this.verifyHmacSignature(signature, request.body, backupSecret);
      if (!backupValid && !primaryValid) {
        return { valid: false, reason: 'Both primary and backup signature verification failed' };
      }
    }

    // Additional validation for specific topics
    if (!this.isValidTopic(topic)) {
      return { valid: false, reason: 'Invalid or unauthorized webhook topic' };
    }

    return { valid: true };
  }

  /**
   * HMAC signature verification with enhanced security
   */
  private verifyHmacSignature(signature: string, body: string, secret: string): boolean {
    try {
      // Support both base64 and hex encodings
      const expectedBase64 = crypto
        .createHmac('sha256', secret)
        .update(body, 'utf8')
        .digest('base64');

      const expectedHex = crypto
        .createHmac('sha256', secret)
        .update(body, 'utf8')
        .digest('hex');

      // Try both formats
      const signatureBuffer = Buffer.from(signature);
      const expectedBase64Buffer = Buffer.from(expectedBase64);
      const expectedHexBuffer = Buffer.from(expectedHex);

      return (
        (signatureBuffer.length === expectedBase64Buffer.length &&
          crypto.timingSafeEqual(signatureBuffer, expectedBase64Buffer)) ||
        (signatureBuffer.length === expectedHexBuffer.length &&
          crypto.timingSafeEqual(signatureBuffer, expectedHexBuffer))
      );
    } catch (error) {
      logger.error('HMAC signature verification error:', error as Record<string, unknown>);
      return false;
    }
  }

  /**
   * Check for replay attacks using nonce and timestamp
   */
  private checkReplayAttack(
    request: { headers: Headers; body: string },
    sourceIp: string
  ): { allowed: boolean; reason?: string } {
    const _delivery = request.headers.get('x-wc-webhook-delivery');
    const timestamp = request.headers.get('x-wc-webhook-timestamp');

    // Create a unique nonce for this request
    const nonce = crypto
      .createHash('sha256')
      .update(request.body + (_delivery || '') + (timestamp || '') + sourceIp)
      .digest('hex');

    // Check if we've seen this nonce before
    if (usedNonces.has(nonce)) {
      monitoring.recordMetric('webhook.security.replay_detected', 1, { ip: sourceIp });
      return { allowed: false, reason: 'Duplicate request detected (replay attack)' };
    }

    // Check timestamp freshness
    if (timestamp) {
      const requestTime = parseInt(timestamp) * 1000;
      const now = Date.now();
      const age = now - requestTime;

      if (age > this.config.replayWindowMs) {
        return { allowed: false, reason: 'Request timestamp too old' };
      }

      if (age < -30000) { // 30 seconds in future
        return { allowed: false, reason: 'Request timestamp too far in future' };
      }
    }

    // Store nonce to prevent replay
    usedNonces.add(nonce);

    return { allowed: true };
  }

  /**
   * Check for suspicious headers
   */
  private checkSuspiciousHeaders(
    headers: Headers,
    sourceIp: string
  ): { allowed: boolean; reason?: string } {
    const userAgent = headers.get('user-agent') || '';
    const origin = headers.get('origin');
    const _referer = headers.get('referer');

    // Check for suspicious user agents
    const suspiciousPatterns = [
      /curl/i,
      /wget/i,
      /python/i,
      /bot/i,
      /scanner/i,
      /sqlmap/i,
      /nikto/i
    ];

    for (const pattern of suspiciousPatterns) {
      if (pattern.test(userAgent)) {
        monitoring.recordMetric('webhook.security.suspicious_user_agent', 1, {
          ip: sourceIp,
          userAgent
        });
        // Log but don't block - could be legitimate automation
        logger.warn('Suspicious user agent detected', { ip: sourceIp, userAgent });
      }
    }

    // Check for unexpected origin/referer
    if (origin && !this.isValidOrigin(origin)) {
      return {
        allowed: false,
        reason: 'Invalid origin header'
      };
    }

    // Check for injection attempts in headers
    const allHeaders = Array.from(headers.entries());
    for (const [name, value] of allHeaders) {
      if (this.containsSqlInjection(value) || this.containsXssAttempt(value)) {
        monitoring.recordMetric('webhook.security.injection_attempt', 1, {
          ip: sourceIp,
          header: name
        });
        return {
          allowed: false,
          reason: 'Malicious content detected in headers'
        };
      }
    }

    return { allowed: true };
  }

  /**
   * Check IP blocking status and update failure counts
   */
  private checkIpBlocking(sourceIp: string): { allowed: boolean } {
    const now = Date.now();
    const blocked = blockedIps.get(sourceIp);

    if (blocked) {
      if (blocked.blockedUntil > now) {
        return { allowed: false };
      } else {
        // Block expired, reset
        blockedIps.delete(sourceIp);
      }
    }

    return { allowed: true };
  }

  /**
   * Record security violation and potentially block IP
   */
  private recordSecurityViolation(sourceIp: string, violation: string): void {
    monitoring.recordMetric('webhook.security.violation', 1, {
      ip: sourceIp,
      violation
    });

    logger.warn('Webhook security violation', {
      ip: sourceIp,
      violation,
      timestamp: Date.now()
    });

    // Update failure count
    const current = blockedIps.get(sourceIp) || { blockedUntil: 0, attempts: 0 };
    current.attempts++;

    if (current.attempts >= this.config.maxFailedAttempts) {
      current.blockedUntil = Date.now() + (this.config.blockDuration * 1000);
      monitoring.recordMetric('webhook.security.ip_auto_blocked', 1, { ip: sourceIp });
      logger.error('IP auto-blocked due to security violations', {
        ip: sourceIp,
        attempts: current.attempts
      });
    }

    blockedIps.set(sourceIp, current);
  }

  /**
   * Validate if IP is in allowed ranges (CIDR)
   */
  private isIpAllowed(ip: string): boolean {
    if (this.config.allowedIpRanges.length === 0) {
      return true; // No restrictions
    }

    // Simple IP validation - in production, use proper CIDR library
    for (const range of this.config.allowedIpRanges) {
      if (range === ip || range === '0.0.0.0/0') {
        return true;
      }
      // Add proper CIDR matching logic here
    }

    return false;
  }

  /**
   * Validate webhook topic
   */
  private isValidTopic(topic: string): boolean {
    const allowedTopics = [
      'product.created',
      'product.updated',
      'product.deleted',
      'order.created',
      'order.updated',
      'order.deleted',
      'customer.created',
      'customer.updated'
    ];

    return allowedTopics.includes(topic);
  }

  /**
   * Validate origin header
   */
  private isValidOrigin(origin: string): boolean {
    const allowedOrigins = [
      config.app.baseUrl,
      config.woocommerce.apiUrl
    ].filter((url): url is string => Boolean(url));

    return allowedOrigins.some(allowed => origin.startsWith(allowed));
  }

  /**
   * Check for SQL injection patterns
   */
  private containsSqlInjection(value: string): boolean {
    const sqlPatterns = [
      /(\b(SELECT|INSERT|UPDATE|DELETE|DROP|CREATE|ALTER|EXEC|EXECUTE)\b)/i,
      /(UNION\s+ALL\s+SELECT)/i,
      /(\bOR\s+1\s*=\s*1\b)/i,
      /(\bAND\s+1\s*=\s*1\b)/i,
      /(\';\s*(SELECT|INSERT|UPDATE|DELETE))/i
    ];

    return sqlPatterns.some(pattern => pattern.test(value));
  }

  /**
   * Check for XSS attempts
   */
  private containsXssAttempt(value: string): boolean {
    const xssPatterns = [
      /<script[\s\S]*?>[\s\S]*?<\/script>/i,
      /javascript:/i,
      /on\w+\s*=/i,
      /<iframe[\s\S]*?>/i,
      /<object[\s\S]*?>/i,
      /<embed[\s\S]*?>/i
    ];

    return xssPatterns.some(pattern => pattern.test(value));
  }

  /**
   * Get security metrics for monitoring
   */
  getSecurityMetrics(): {
    blockedIps: number;
    usedNonces: number;
    config: WebhookSecurityConfig;
  } {
    return {
      blockedIps: blockedIps.size,
      usedNonces: usedNonces.size,
      config: this.config
    };
  }

  /**
   * Manual IP unblocking (admin function)
   */
  unblockIp(ip: string): boolean {
    const wasBlocked = blockedIps.has(ip);
    blockedIps.delete(ip);

    if (wasBlocked) {
      monitoring.recordMetric('webhook.security.ip_manually_unblocked', 1, { ip });
      logger.info('IP manually unblocked', { ip, timestamp: Date.now() });
    }

    return wasBlocked;
  }

  /**
   * Get blocked IPs list (admin function)
   */
  getBlockedIps(): Array<{ ip: string; blockedUntil: number; attempts: number }> {
    const now = Date.now();
    return Array.from(blockedIps.entries())
      .filter(([_, data]) => data.blockedUntil > now)
      .map(([ip, data]) => ({ ip, ...data }));
  }
}

