// Improved text-based search without vector databases
import { WCProduct } from '@/types/woocommerce';
import { getAllProducts } from '@/lib/woocommerce';

// Agricultural and Filipino food synonyms
const synonymMap: Record<string, string[]> = {
  'rice': ['bigas', 'palay', 'grain', 'cereal'],
  'vegetable': ['gulay', 'veggies', 'produce', 'greens'],
  'fruit': ['prutas', 'produce', 'fresh'],
  'organic': ['natural', 'pesticide-free', 'chemical-free', 'sustainable'],
  'coconut': ['niyog', 'buko', 'copra'],
  'corn': ['mais', 'maize'],
  'tomato': ['kamatis'],
  'onion': ['sibuyas'],
  'garlic': ['bawang'],
  'ginger': ['luya'],
  'pepper': ['sili', 'chili'],
  'sweet': ['matamis', 'dessert'],
  'healthy': ['nutritious', 'wellness', 'beneficial'],
  'farm': ['bukid', 'agricultural', 'harvest'],
  'fresh': ['sariwang', 'new', 'crisp']
};

// Levenshtein distance for fuzzy matching
function levenshteinDistance(str1: string, str2: string): number {
  const m = str1.length;
  const n = str2.length;
  const dp: number[][] = Array(m + 1).fill(null).map(() => Array(n + 1).fill(0));

  // Initialize first row and column
  for (let i = 0; i <= m; i++) {
    const row = dp[i];
    if (row) row[0] = i;
  }
  for (let j = 0; j <= n; j++) {
    if (dp[0]) dp[0][j] = j;
  }

  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const currentRow = dp[i];
      const prevRow = dp[i - 1];

      if (!currentRow || !prevRow) continue;

      if (str1[i - 1] === str2[j - 1]) {
        currentRow[j] = prevRow[j - 1] ?? 0;
      } else {
        currentRow[j] = Math.min(
          (prevRow[j] ?? 0) + 1,        // deletion
          (currentRow[j - 1] ?? 0) + 1,  // insertion
          (prevRow[j - 1] ?? 0) + 1      // substitution
        );
      }
    }
  }

  return dp[m]?.[n] ?? 0;
}

// Calculate similarity score (0-1)
function similarity(str1: string, str2: string): number {
  const maxLen = Math.max(str1.length, str2.length);
  if (maxLen === 0) return 1;
  const distance = levenshteinDistance(str1.toLowerCase(), str2.toLowerCase());
  return 1 - (distance / maxLen);
}

// Expand query with synonyms
function expandQuery(query: string): string[] {
  const words = query.toLowerCase().split(/\s+/);
  const expanded = new Set<string>(words);

  words.forEach(word => {
    // Add synonyms
    Object.entries(synonymMap).forEach(([key, synonyms]) => {
      if (word === key || synonyms.includes(word)) {
        expanded.add(key);
        synonyms.forEach(syn => expanded.add(syn));
      }
    });
  });

  return Array.from(expanded);
}

// Extract keywords from product for searching
function extractProductKeywords(product: WCProduct): string[] {
  const keywords: string[] = [];

  // Add name words
  keywords.push(...product.name.toLowerCase().split(/\s+/));

  // Add category names
  product.categories?.forEach(cat => {
    keywords.push(...cat.name.toLowerCase().split(/\s+/));
  });

  // Add tags
  product.tags?.forEach(tag => {
    keywords.push(...tag.name.toLowerCase().split(/\s+/));
  });

  // Extract words from short description
  if (product.short_description) {
    const cleanDesc = product.short_description
      .replace(/<[^>]*>/g, ' ')
      .toLowerCase();
    keywords.push(...cleanDesc.split(/\s+/).slice(0, 20));
  }

  // Add attributes
  product.attributes?.forEach(attr => {
    attr.options?.forEach(option => {
      keywords.push(option.toLowerCase());
    });
  });

  return keywords.filter(k => k.length > 2);
}

// Score a product against a query
function scoreProduct(
  product: WCProduct,
  queryTerms: string[],
  productKeywords: string[]
): number {
  let score = 0;
  const boostFactors = {
    nameMatch: 3.0,
    categoryMatch: 2.0,
    tagMatch: 1.5,
    descriptionMatch: 1.0,
    fuzzyMatch: 0.5
  };

  queryTerms.forEach(term => {
    const termLower = term.toLowerCase();

    // Exact match in name (highest weight)
    if (product.name.toLowerCase().includes(termLower)) {
      score += boostFactors.nameMatch;
    }

    // Category match
    product.categories?.forEach(cat => {
      if (cat.name.toLowerCase().includes(termLower)) {
        score += boostFactors.categoryMatch;
      }
    });

    // Tag match
    product.tags?.forEach(tag => {
      if (tag.name.toLowerCase().includes(termLower)) {
        score += boostFactors.tagMatch;
      }
    });

    // Description match
    if (product.short_description?.toLowerCase().includes(termLower)) {
      score += boostFactors.descriptionMatch;
    }

    // Fuzzy matching for typos
    productKeywords.forEach(keyword => {
      const sim = similarity(termLower, keyword);
      if (sim > 0.8 && sim < 1) { // Fuzzy match but not exact
        score += boostFactors.fuzzyMatch * sim;
      }
    });
  });

  // Boost popular/featured products
  const productWithExtras = product as unknown as Record<string, unknown>;
  if (productWithExtras.featured) score *= 1.2;
  if (productWithExtras.total_sales && (productWithExtras.total_sales as number) > 10) {
    score *= (1 + Math.log10(productWithExtras.total_sales as number) / 10);
  }

  // Boost in-stock products
  if (product.stock_status === 'instock') score *= 1.1;

  // Penalize out-of-stock
  if (product.stock_status !== 'instock') score *= 0.5;

  return score;
}

export interface ImprovedSearchOptions {
  limit?: number;
  category?: string;
  inStock?: boolean;
  minPrice?: number;
  maxPrice?: number;
  sortBy?: 'relevance' | 'price_asc' | 'price_desc' | 'name';
}

export interface SearchResult {
  product: WCProduct;
  score: number;
  matchedTerms: string[];
}

export async function improvedSearch(
  query: string,
  options: ImprovedSearchOptions = {}
): Promise<SearchResult[]> {
  const {
    limit = 20,
    category,
    inStock,
    minPrice,
    maxPrice,
    sortBy = 'relevance'
  } = options;

  // Get all products (with caching in production)
  const allProducts = await getAllProducts();

  // Expand query with synonyms
  const expandedTerms = expandQuery(query);

  // Pre-compute keywords for all products
  const productKeywordsMap = new Map<number, string[]>();
  allProducts.forEach(product => {
    productKeywordsMap.set(product.id, extractProductKeywords(product));
  });

  // Score and filter products
  let results: SearchResult[] = allProducts
    .filter(product => {
      // Apply filters
      if (inStock !== undefined && (product.stock_status === 'instock') !== inStock) return false;
      if (category && !product.categories?.some(c => c.slug === category)) return false;
      if (minPrice && product.price && parseFloat(product.price) < minPrice) return false;
      if (maxPrice && product.price && parseFloat(product.price) > maxPrice) return false;
      return true;
    })
    .map(product => {
      const keywords = productKeywordsMap.get(product.id) ?? [];
      const score = scoreProduct(product, expandedTerms, keywords);

      // Find which terms matched
      const matchedTerms = expandedTerms.filter(term => {
        const termLower = term.toLowerCase();
        return product.name.toLowerCase().includes(termLower) ||
               product.short_description?.toLowerCase().includes(termLower) ||
               keywords.some(k => similarity(termLower, k) > 0.8);
      });

      return { product, score, matchedTerms };
    })
    .filter(result => result.score > 0);

  // Sort results
  switch (sortBy) {
    case 'price_asc':
      results.sort((a, b) => parseFloat(a.product.price || '0') - parseFloat(b.product.price || '0'));
      break;
    case 'price_desc':
      results.sort((a, b) => parseFloat(b.product.price || '0') - parseFloat(a.product.price || '0'));
      break;
    case 'name':
      results.sort((a, b) => a.product.name.localeCompare(b.product.name));
      break;
    case 'relevance':
    default:
      results.sort((a, b) => b.score - a.score);
  }

  return results.slice(0, limit);
}

// Health-focused search
export async function searchHealthProducts(
  healthCondition: string,
  limit = 10
): Promise<SearchResult[]> {
  const healthKeywords: Record<string, string[]> = {
    'diabetes': ['low sugar', 'sugar-free', 'diabetic friendly', 'low glycemic'],
    'heart': ['low sodium', 'heart healthy', 'omega-3', 'fiber rich'],
    'weight': ['low calorie', 'weight management', 'diet', 'light'],
    'energy': ['protein', 'vitamin', 'energy boost', 'nutritious'],
    'immune': ['vitamin c', 'antioxidant', 'immune boost', 'zinc'],
    'digestion': ['fiber', 'probiotic', 'digestive', 'gut health']
  };

  const keywords = healthKeywords[healthCondition.toLowerCase()] ?? [healthCondition];
  return improvedSearch(keywords.join(' '), { limit });
}

// Seasonal search (Philippines seasons)
export async function searchSeasonalProducts(limit = 10): Promise<SearchResult[]> {
  const month = new Date().getMonth();

  // Philippines has wet (June-Nov) and dry (Dec-May) seasons
  const isWetSeason = month >= 5 && month <= 10;

  const seasonalQuery = isWetSeason
    ? 'rainy season soup warm comfort'
    : 'summer fresh cooling hydrating';

  return improvedSearch(seasonalQuery, { limit, inStock: true });
}

/**
 * WooCommerce API Integration Service
 * Connects to WooCommerce REST API to fetch real product and order data
 */

interface WooCommerceConfig {
  apiUrl: string;
  consumerKey: string;
  consumerSecret: string;
  version?: string;
}

interface WooCommerceProduct {
  id: number;
  name: string;
  slug: string;
  price: string;
  regular_price: string;
  sale_price: string;
  on_sale: boolean;
  stock_status: 'instock' | 'outofstock' | 'onbackorder';
  stock_quantity: number | null;
  categories: { id: number; name: string; slug: string }[];
  images: { src: string; alt: string }[];
  date_created: string;
  date_modified: string;
  status: 'publish' | 'draft' | 'pending' | 'private';
  total_sales: number;
  description: string;
  short_description: string;
  sku: string;
  weight: string;
  dimensions: {
    length: string;
    width: string;
    height: string;
  };
}

interface WooCommerceOrder {
  id: number;
  number: string;
  status: string;
  currency: string;
  total: string;
  total_tax: string;
  shipping_total: string;
  date_created: string;
  date_modified: string;
  customer_id: number;
  billing: {
    first_name: string;
    last_name: string;
    email: string;
    country: string;
    state: string;
    city: string;
  };
  line_items: {
    id: number;
    name: string;
    product_id: number;
    quantity: number;
    total: string;
    price: number;
  }[];
}

interface WooCommerceCustomer {
  id: number;
  email: string;
  first_name: string;
  last_name: string;
  username: string;
  date_created: string;
  date_modified: string;
  orders_count: number;
  total_spent: string;
  avatar_url: string;
  billing: {
    country: string;
    state: string;
    city: string;
  };
}

interface ApiResponse<T> {
  data: T;
  headers: Record<string, string>;
}

export class WooCommerceAPIService {
  private config: WooCommerceConfig;
  private baseUrl: string;

  constructor(config?: Partial<WooCommerceConfig>) {
    this.config = {
      apiUrl: config?.apiUrl || process.env.NEXT_PUBLIC_WC_API_URL || '',
      consumerKey: config?.consumerKey || process.env.WC_CONSUMER_KEY || '',
      consumerSecret: config?.consumerSecret || process.env.WC_CONSUMER_SECRET || '',
      version: config?.version || 'wc/v3'
    };

    if (!this.config.apiUrl || !this.config.consumerKey || !this.config.consumerSecret) {
      throw new Error('WooCommerce API configuration is incomplete');
    }

    this.baseUrl = `${this.config.apiUrl}/${this.config.version}`;
  }

  /**
   * Make authenticated request to WooCommerce API
   */
  private async makeRequest<T>(
    endpoint: string,
    method: 'GET' | 'POST' | 'PUT' | 'DELETE' = 'GET',
    data?: any,
    params?: Record<string, string | number>
  ): Promise<ApiResponse<T>> {
    const url = new URL(`${this.baseUrl}/${endpoint}`);

    // Add query parameters
    if (params) {
      Object.entries(params).forEach(([key, value]) => {
        url.searchParams.append(key, value.toString());
      });
    }

    // Add authentication
    url.searchParams.append('consumer_key', this.config.consumerKey);
    url.searchParams.append('consumer_secret', this.config.consumerSecret);

    const requestOptions: RequestInit = {
      method,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
    };

    if (data && method !== 'GET') {
      requestOptions.body = JSON.stringify(data);
    }

    try {
      const response = await fetch(url.toString(), requestOptions);

      if (!response.ok) {
        const errorBody = await response.text();
        throw new Error(`WooCommerce API Error: ${response.status} - ${errorBody}`);
      }

      const responseData = await response.json();
      const headers: Record<string, string> = {};

      response.headers.forEach((value, key) => {
        headers[key] = value;
      });

      return {
        data: responseData,
        headers
      };
    } catch (error) {
      console.error('WooCommerce API request failed:', error);
      throw error;
    }
  }

  /**
   * Get all products with pagination
   */
  async getProducts(params?: {
    page?: number;
    per_page?: number;
    search?: string;
    category?: string;
    status?: string;
    orderby?: string;
    order?: 'asc' | 'desc';
    on_sale?: boolean;
    min_price?: number;
    max_price?: number;
  }): Promise<{
    products: WooCommerceProduct[];
    totalPages: number;
    totalCount: number;
  }> {
    const queryParams: Record<string, string | number> = {
      page: params?.page || 1,
      per_page: params?.per_page || 20,
      ...Object.fromEntries(
        Object.entries(params || {}).map(([key, value]) => [
          key,
          String(value)
        ])
      )
    };

    const response = await this.makeRequest<WooCommerceProduct[]>('products', 'GET', null, queryParams);

    return {
      products: response.data,
      totalPages: parseInt(response.headers['x-wp-totalpages'] || '1'),
      totalCount: parseInt(response.headers['x-wp-total'] || '0')
    };
  }

  /**
   * Get specific product by ID
   */
  async getProduct(productId: number): Promise<WooCommerceProduct> {
    const response = await this.makeRequest<WooCommerceProduct>(`products/${productId}`);
    return response.data;
  }

  /**
   * Get recent orders
   */
  async getOrders(params?: {
    page?: number;
    per_page?: number;
    status?: string;
    customer?: number;
    after?: string;
    before?: string;
    orderby?: string;
    order?: 'asc' | 'desc';
  }): Promise<{
    orders: WooCommerceOrder[];
    totalPages: number;
    totalCount: number;
  }> {
    const queryParams: Record<string, string | number> = {
      page: params?.page || 1,
      per_page: params?.per_page || 20,
      ...Object.fromEntries(
        Object.entries(params || {}).map(([key, value]) => [
          key,
          String(value)
        ])
      )
    };

    const response = await this.makeRequest<WooCommerceOrder[]>('orders', 'GET', null, queryParams);

    return {
      orders: response.data,
      totalPages: parseInt(response.headers['x-wp-totalpages'] || '1'),
      totalCount: parseInt(response.headers['x-wp-total'] || '0')
    };
  }

  /**
   * Get customers
   */
  async getCustomers(params?: {
    page?: number;
    per_page?: number;
    search?: string;
    orderby?: string;
    order?: 'asc' | 'desc';
  }): Promise<{
    customers: WooCommerceCustomer[];
    totalPages: number;
    totalCount: number;
  }> {
    const queryParams: Record<string, string | number> = {
      page: params?.page || 1,
      per_page: params?.per_page || 20,
      ...Object.fromEntries(
        Object.entries(params || {}).map(([key, value]) => [
          key,
          String(value)
        ])
      )
    };

    const response = await this.makeRequest<WooCommerceCustomer[]>('customers', 'GET', null, queryParams);

    return {
      customers: response.data,
      totalPages: parseInt(response.headers['x-wp-totalpages'] || '1'),
      totalCount: parseInt(response.headers['x-wp-total'] || '0')
    };
  }

  /**
   * Get sales reports
   */
  async getSalesReport(period: 'week' | 'month' | 'year' = 'month'): Promise<{
    totalSales: number;
    totalOrders: number;
    totalCustomers: number;
    totalProducts: number;
    averageOrderValue: number;
    salesData: { date: string; sales: number; orders: number }[];
  }> {
    try {
      // Get recent orders for sales calculation
      const ordersResponse = await this.getOrders({
        per_page: 100,
        status: 'completed',
        after: this.getDateRangeStart(period)
      });

      const orders = ordersResponse.orders;
      const totalSales = orders.reduce((sum, order) => {
        const orderTotal = order.total ?? '0';
        return sum + parseFloat(orderTotal);
      }, 0);
      const totalOrders = orders.length;

      // Get product count
      const productsResponse = await this.getProducts({ per_page: 1 });
      const totalProducts = productsResponse.totalCount;

      // Get customer count
      const customersResponse = await this.getCustomers({ per_page: 1 });
      const totalCustomers = customersResponse.totalCount;

      const averageOrderValue = totalOrders > 0 ? totalSales / totalOrders : 0;

      // Generate sales data by day
      const salesData = this.aggregateSalesData(orders, period);

      return {
        totalSales,
        totalOrders,
        totalCustomers,
        totalProducts,
        averageOrderValue,
        salesData
      };
    } catch (error) {
      console.error('Failed to generate sales report:', error);
      throw error;
    }
  }

  /**
   * Get top selling products
   */
  async getTopSellingProducts(limit: number = 10): Promise<WooCommerceProduct[]> {
    const response = await this.getProducts({
      orderby: 'popularity',
      order: 'desc',
      per_page: limit
    });

    return response.products;
  }

  /**
   * Search products by name or SKU
   */
  async searchProducts(query: string, limit: number = 20): Promise<WooCommerceProduct[]> {
    const response = await this.getProducts({
      search: query,
      per_page: limit
    });

    return response.products;
  }

  /**
   * Get products by category
   */
  async getProductsByCategory(categoryId: number, limit: number = 20): Promise<WooCommerceProduct[]> {
    const response = await this.getProducts({
      category: categoryId.toString(),
      per_page: limit
    });

    return response.products;
  }

  /**
   * Test API connection
   */
  async testConnection(): Promise<{ success: boolean; message: string; storeInfo?: any }> {
    try {
      // Try to fetch system status or a simple endpoint
      const response = await this.makeRequest('system_status');

      return {
        success: true,
        message: 'Successfully connected to WooCommerce API',
        storeInfo: response.data
      };
    } catch (error) {
      return {
        success: false,
        message: error instanceof Error ? error.message : 'Unknown connection error'
      };
    }
  }

  /**
   * Helper: Get date range start for reports
   */
  private getDateRangeStart(period: 'week' | 'month' | 'year'): string {
    const now = new Date();
    const start = new Date();

    switch (period) {
      case 'week':
        start.setDate(now.getDate() - 7);
        break;
      case 'month':
        start.setMonth(now.getMonth() - 1);
        break;
      case 'year':
        start.setFullYear(now.getFullYear() - 1);
        break;
    }

    return start.toISOString();
  }

  /**
   * Helper: Aggregate sales data by date
   */
  private aggregateSalesData(
    orders: WooCommerceOrder[],
    _period: 'week' | 'month' | 'year'
  ): { date: string; sales: number; orders: number }[] {
    const salesMap = new Map<string, { sales: number; orders: number }>();

    orders.forEach(order => {
      const dateString = order.date_created ?? new Date().toISOString();
      const date = new Date(dateString);
      const isoString = date.toISOString();
      const dateKey = isoString.split('T')[0] || isoString.substring(0, 10); // YYYY-MM-DD format

      const existing = salesMap.get(dateKey) || { sales: 0, orders: 0 };
      const orderTotal = order.total ?? '0';
      existing.sales += parseFloat(orderTotal);
      existing.orders += 1;
      salesMap.set(dateKey, existing);
    });

    return Array.from(salesMap.entries())
      .map(([date, data]) => ({ date, ...data }))
      .sort((a, b) => a.date.localeCompare(b.date));
  }
}

