// Background Job Processor for Automatic Data Persistence
import { Redis } from 'ioredis';
import { logger } from '@/lib/logger';
import {
  BaseEvent,
  EventType,
  ProductEvent,
  SearchEvent,
  PageEvent,
  OrderEvent
} from './event-system';

// Database persistence modules
// Removed unused import: addProductToGraph
// import { upsertProductEmbedding } from './qdrant'; // Not used
// Enhanced auto-sync modules
import { autoSyncProductToMemgraph, autoSyncUserBehaviorToMemgraph, autoSyncSearchToMemgraph } from './memgraph-auto-sync';
import { autoSyncProductToQdrant, autoSyncUserSearchToQdrant, autoSyncUserBehaviorToQdrant } from './qdrant-auto-sync';

// Job types for background processing
export enum JobType {
  PERSIST_TO_MEMGRAPH = 'persist.memgraph',
  PERSIST_TO_QDRANT = 'persist.qdrant',
  PERSIST_ANALYTICS_TO_MEMGRAPH = 'persist.analytics.memgraph',
  UPDATE_USER_PROFILE = 'update.user_profile',
  GENERATE_RECOMMENDATIONS = 'generate.recommendations',
  SYNC_WOOCOMMERCE = 'sync.woocommerce',
  CLEANUP_OLD_DATA = 'cleanup.old_data',
  PROCESS_EVENT = 'process_event',
}

interface Job {
  id: string;
  type: JobType;
  data: unknown;
  attempts: number;
  maxAttempts: number;
  createdAt: number;
  scheduledFor?: number;
}

export class JobProcessor {
  private redis: Redis;
  private isProcessing = false;
  private processingInterval: NodeJS.Timeout | null = null;

  constructor() {
    this.redis = new Redis({
      host: process.env.REDIS_HOST || 'localhost',
      port: parseInt(process.env.REDIS_PORT || '6379'),
      password: process.env.REDIS_PASSWORD,
    });
  }

  // Start processing background jobs
  async start(): Promise<void> {
    if (this.isProcessing) return;

    this.isProcessing = true;
    logger.info('🚀 Starting background job processor');

    // Process events every 5 seconds
    this.processingInterval = setInterval(async () => {
      await this.processEventQueue();
      await this.processJobQueue();
    }, 5000);

    // Setup event listeners for automatic job creation
    this.setupEventListeners();
  }

  // Stop processing
  async stop(): Promise<void> {
    this.isProcessing = false;
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = null;
    }
    logger.info('⏹️ Stopped background job processor');
  }

  // Public method to enqueue jobs
  async enqueue(type: JobType, data: unknown, delay: number = 0): Promise<void> {
    await this.addJob(type, data, delay);
  }

  // Process events from the event queue
  private async processEventQueue(): Promise<void> {
    try {
      while (true) {
        const eventData = await this.redis.brpop('events:queue', 1);
        if (!eventData) break;

        const event: BaseEvent = JSON.parse(eventData[1]);
        await this.handleEvent(event);
      }
    } catch (error) {
      logger.error('Error processing event queue:', error as Record<string, unknown>);
    }
  }

  // Handle individual events and create appropriate jobs
  private async handleEvent(event: BaseEvent): Promise<void> {
    try {
      logger.info(`Processing event: ${event.type}`, { eventId: event.id });

      // Create jobs based on event type
      switch (event.type) {
        case EventType.PRODUCT_VIEWED:
        case EventType.PRODUCT_ADDED_TO_CART:
        case EventType.PRODUCT_PURCHASED:
          await this.createPersistenceJobs(event as ProductEvent);
          break;

        case EventType.SEARCH_PERFORMED:
          await this.handleSearchEvent(event as SearchEvent);
          break;

        case EventType.PAGE_VIEWED:
          await this.handlePageViewEvent(event as PageEvent);
          break;

        case EventType.ORDER_CREATED:
          await this.handleOrderEvent(event as OrderEvent);
          break;

        case EventType.USER_REGISTERED:
        case EventType.USER_LOGIN:
          await this.handleUserEvent(event);
          break;

        default:
          // Generic analytics persistence
          await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);
      }

    } catch (error) {
      logger.error(`Error handling event ${event.type}:`, error as Record<string, unknown>);
    }
  }

  // Create persistence jobs for product events
  private async createPersistenceJobs(event: ProductEvent): Promise<void> {
    // Save to Memgraph (relationships)
    await this.addJob(JobType.PERSIST_TO_MEMGRAPH, {
      eventType: event.type,
      productId: event.productId,
      userId: event.userId,
      sessionId: event.sessionId,
      timestamp: event.timestamp,
      metadata: event.metadata,
    });

    // Save analytics to Memgraph as graph nodes
    await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);

    // If new product interaction, trigger recommendation update
    if (event.userId) {
      await this.addJob(JobType.GENERATE_RECOMMENDATIONS, {
        userId: event.userId,
        productId: event.productId,
        interactionType: event.type,
      });
    }
  }

  // Handle search events
  private async handleSearchEvent(event: SearchEvent): Promise<void> {
    // Save search analytics to Memgraph
    await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);

    // Save search patterns to Qdrant
    await this.addJob(JobType.PERSIST_TO_QDRANT, {
      type: 'search_pattern',
      query: event.query,
      userId: event.userId,
      sessionId: event.sessionId,
      resultsCount: event.resultsCount,
      timestamp: event.timestamp
    });

    // If no results, log for product discovery
    if (event.resultsCount === 0) {
      await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, {
        ...event,
        type: 'search.no_results.analysis',
        priority: 'high',
      });
    }
  }

  // Handle page view events
  private async handlePageViewEvent(event: PageEvent): Promise<void> {
    await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);

    // Update user journey
    if (event.userId) {
      await this.addJob(JobType.UPDATE_USER_PROFILE, {
        userId: event.userId,
        pageUrl: event.pageUrl,
        timestamp: event.timestamp,
        deviceType: event.deviceType,
        browserType: event.browserType,
      });
    }
  }

  // Handle order events
  private async handleOrderEvent(event: OrderEvent): Promise<void> {
    // High priority - save order relationships to Memgraph
    await this.addJob(JobType.PERSIST_TO_MEMGRAPH, {
      eventType: event.type,
      orderId: event.orderId,
      userId: event.userId,
      items: event.items || [],
      orderValue: event.orderTotal,  // OrderEvent has orderTotal, not orderValue
      timestamp: event.timestamp,
    }, 0); // High priority

    // Save comprehensive analytics to Memgraph
    await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);

    // Update product relationships based on purchase
    if (event.items) {
      for (const item of event.items) {
      await this.addJob(JobType.PERSIST_TO_MEMGRAPH, {
        eventType: 'product.purchased.relationship',
        productId: item.productId,
        orderId: event.orderId,
        quantity: item.quantity,
        price: item.price,
        coProducts: event.items?.filter(i => i.productId !== item.productId) || [],
      });
      }
    }
  }

  // Handle user events
  private async handleUserEvent(event: BaseEvent): Promise<void> {
    await this.addJob(JobType.PERSIST_ANALYTICS_TO_MEMGRAPH, event);

    if (event.userId) {
      await this.addJob(JobType.UPDATE_USER_PROFILE, {
        userId: event.userId,
        eventType: event.type,
        timestamp: event.timestamp,
        metadata: event.metadata,
      });
    }
  }

  // Add job to queue
  private async addJob(
    type: JobType,
    data: unknown,
    delay: number = 0
  ): Promise<void> {
    const job: Job = {
      id: `job_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      type,
      data,
      attempts: 0,
      maxAttempts: 3,
      createdAt: Date.now(),
      scheduledFor: delay > 0 ? Date.now() + delay : undefined,
    };

    const queueName = delay > 0 ? 'jobs:delayed' : 'jobs:queue';
    await this.redis.lpush(queueName, JSON.stringify(job));
  }

  // Process job queue
  private async processJobQueue(): Promise<void> {
    try {
      // Process immediate jobs
      while (true) {
        const jobData = await this.redis.brpop('jobs:queue', 1);
        if (!jobData) break;

        const job: Job = JSON.parse(jobData[1]);
        await this.executeJob(job);
      }

      // Process delayed jobs that are ready
      await this.processDelayedJobs();
    } catch (error) {
      logger.error('Error processing job queue:', error as Record<string, unknown>);
    }
  }

  // Process delayed jobs
  private async processDelayedJobs(): Promise<void> {
    const now = Date.now();
    const delayedJobs = await this.redis.lrange('jobs:delayed', 0, -1);

    for (let i = 0; i < delayedJobs.length; i++) {
      const jobData = delayedJobs[i];
      if (!jobData) continue;
      const job: Job = JSON.parse(jobData);

      if (!job.scheduledFor || job.scheduledFor <= now) {
        // Move to immediate queue
        await this.redis.lrem('jobs:delayed', 1, jobData);
        await this.redis.lpush('jobs:queue', JSON.stringify(job));
      }
    }
  }

  // Execute individual job
  private async executeJob(job: Job): Promise<void> {
    try {
      logger.info(`Executing job: ${job.type}`, { jobId: job.id });

      switch (job.type) {
        case JobType.PERSIST_TO_MEMGRAPH:
          await this.persistToMemgraph(job.data);
          break;

        case JobType.PERSIST_TO_QDRANT:
          await this.persistToQdrant(job.data);
          break;

        case JobType.PERSIST_ANALYTICS_TO_MEMGRAPH:
          await this.persistAnalyticsToMemgraph(job.data);
          break;

        case JobType.UPDATE_USER_PROFILE:
          await this.updateUserProfile(job.data);
          break;

        case JobType.GENERATE_RECOMMENDATIONS:
          await this.generateRecommendations(job.data);
          break;

        case JobType.SYNC_WOOCOMMERCE:
          await this.syncWooCommerce(job.data);
          break;

        case JobType.CLEANUP_OLD_DATA:
          await this.cleanupOldData(job.data);
          break;

        case JobType.PROCESS_EVENT:
          await this.processEvent(job.data);
          break;

        default:
          logger.warn(`Unknown job type: ${job.type}`);
      }

      logger.info(`Job completed: ${job.type}`, { jobId: job.id });
    } catch (error) {
      logger.error(`Job failed: ${job.type}`, {
        jobId: job.id,
        error: error as Record<string, unknown>
      });

      // Retry logic
      job.attempts++;
      if (job.attempts < job.maxAttempts) {
        // Add back to queue with delay
        job.scheduledFor = Date.now() + (job.attempts * 30000); // Exponential backoff
        await this.redis.lpush('jobs:delayed', JSON.stringify(job));
      } else {
        // Move to failed queue
        await this.redis.lpush('jobs:failed', JSON.stringify(job));
      }
    }
  }

  // Job execution methods
  private async persistToMemgraph(data: any): Promise<void> {
    try {
      if (data.eventType) {
        // Use enhanced auto-sync for product/user events
        await autoSyncProductToMemgraph(data);
      } else if (data.query) {
        // Search event
        await autoSyncSearchToMemgraph(data);
      } else {
        // Generic user behavior
        await autoSyncUserBehaviorToMemgraph(data);
      }
      logger.info('Successfully persisted to Memgraph:', { type: data.eventType || data.type });
    } catch (error) {
      logger.error('Failed to persist to Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  private async persistToQdrant(data: any): Promise<void> {
    try {
      if (data.productId) {
        // Product event
        await autoSyncProductToQdrant(data);
      } else if (data.query || data.type === 'search_pattern') {
        // Search event
        await autoSyncUserSearchToQdrant(data);
      } else {
        // User behavior event
        await autoSyncUserBehaviorToQdrant(data);
      }
      logger.info('Successfully persisted to Qdrant:', { type: data.eventType || data.type });
    } catch (error) {
      logger.error('Failed to persist to Qdrant:', error as Record<string, unknown>);
      throw error;
    }
  }

  private async persistAnalyticsToMemgraph(data: any): Promise<void> {
    try {
      // Store analytics data as graph nodes in Memgraph
      // This will be implemented in the memgraph-analytics.ts file
      await autoSyncUserBehaviorToMemgraph({
        userId: data.userId || `anonymous_${data.sessionId}`,
        sessionId: data.sessionId,
        pageUrl: data.pageUrl || '/',
        timestamp: data.timestamp,
        eventType: data.type,
        metadata: data.metadata || {}
      });
      logger.info('Successfully persisted analytics to Memgraph:', { type: data.type });
    } catch (error) {
      logger.error('Failed to persist analytics to Memgraph:', error as Record<string, unknown>);
      throw error;
    }
  }

  private async updateUserProfile(data: unknown): Promise<void> {
    // Implementation for user profile updates
    logger.info('Updating user profile:', data as Record<string, unknown>);
  }

  private async generateRecommendations(data: unknown): Promise<void> {
    // Implementation for recommendation generation
    logger.info('Generating recommendations:', data as Record<string, unknown>);
  }

  private async syncWooCommerce(data: unknown): Promise<void> {
    // Implementation for WooCommerce sync
    logger.info('Syncing WooCommerce:', data as Record<string, unknown>);
  }

  private async cleanupOldData(data: unknown): Promise<void> {
    // Implementation for data cleanup
    logger.info('Cleaning up old data:', data as Record<string, unknown>);
  }

  private async processEvent(data: unknown): Promise<void> {
    // Process event data from the event tracking API
    const eventData = data as { eventData: BaseEvent; priority: string };
    if (eventData?.eventData) {
      await this.handleEvent(eventData.eventData);
    } else {
      logger.warn('Invalid event data structure:', data as Record<string, unknown>);
    }
  }

  // Setup event listeners
  private setupEventListeners(): void {
    // This would be called during application startup
    logger.info('Setting up event listeners for automatic persistence');
  }
}

// Singleton job processor
export const jobProcessor = new JobProcessor();

// Keyword Search Engine for Hybrid Search Implementation
import { WCProduct } from '../types/woocommerce';

export interface KeywordSearchOptions {
  fuzzyMatch?: boolean;
  stemming?: boolean;
  minScore?: number;
  boost?: {
    title?: number;
    description?: number;
    categories?: number;
    tags?: number;
  };
  includeMetadata?: boolean;
}

export interface KeywordSearchResult {
  productId: number;
  slug: string;
  title: string;
  price: string;
  categories: string[];
  inStock: boolean;
  featured: boolean;
  score: number;
  matchedFields: string[];
  matchedTerms: string[];
}

export interface SearchIndex {
  productId: number;
  title: string;
  description: string;
  categories: string[];
  tags: string[];
  searchableText: string;
  tokens: string[];
  metadata: {
    slug: string;
    price: string;
    inStock: boolean;
    featured: boolean;
  };
}

// Simple stemming function for common English words
function stem(word: string): string {
  const stemRules = [
    // Remove common suffixes
    [/ies$/, 'y'],
    [/ied$/, 'y'],
    [/ying$/, 'y'],
    [/ing$/, ''],
    [/ed$/, ''],
    [/es$/, ''],
    [/s$/, ''],
    [/ly$/, ''],
    [/er$/, ''],
    [/est$/, ''],
  ] as [RegExp, string][];

  let stemmed = word.toLowerCase();

  // Don't stem very short words
  if (stemmed.length <= 3) return stemmed;

  for (const [pattern, replacement] of stemRules) {
    if (pattern.test(stemmed)) {
      const newWord = stemmed.replace(pattern, replacement);
      // Don't make words too short
      if (newWord.length >= 2) {
        return newWord;
      }
    }
  }

  return stemmed;
}

// Calculate Levenshtein distance for fuzzy matching
function levenshteinDistance(a: string, b: string): number {
  if (a.length === 0) return b.length;
  if (b.length === 0) return a.length;

  // Initialize matrix with proper typing and values
  const matrix: number[][] = [];

  // Initialize first row
  matrix[0] = Array.from({ length: a.length + 1 }, (_, i) => i);

  // Initialize first column
  for (let j = 1; j <= b.length; j++) {
    const row = matrix[j];
    if (row) {
      row[0] = j;
    }
  }

  // Fill in the rest
  for (let j = 1; j <= b.length; j++) {
    const row = matrix[j];
    if (!row) continue;

    for (let i = 1; i <= a.length; i++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      const deletion = (matrix[j - 1]?.[i] ?? 0) + 1;
      const insertion = (matrix[j]?.[i - 1] ?? 0) + 1;
      const substitution = (matrix[j - 1]?.[i - 1] ?? 0) + cost;
      if (row[i] !== undefined) {
        row[i] = Math.min(deletion, insertion, substitution);
      }
    }
  }

  return matrix[b.length]?.[a.length] ?? 0;
}

// Check if two words are similar (for fuzzy matching)
function isFuzzyMatch(word1: string, word2: string, threshold: number = 0.7): boolean {
  const maxLength = Math.max(word1.length, word2.length);
  const distance = levenshteinDistance(word1, word2);
  const similarity = 1 - (distance / maxLength);
  return similarity >= threshold;
}

// Tokenize and clean text for indexing
function tokenize(text: string): string[] {
  return text
    .toLowerCase()
    .replace(/[^\w\s]/g, ' ') // Replace punctuation with spaces
    .split(/\s+/)
    .filter(token => token.length > 1) // Remove single characters
    .filter(token => !['the', 'and', 'or', 'but', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by'].includes(token)); // Remove stop words
}

// Build search index from products
export function buildSearchIndex(products: WCProduct[]): SearchIndex[] {
  return products.map(product => {
    const title = product.name ?? '';
    const description = (product.description ?? product.short_description ?? '').replace(/<[^>]*>/g, '');
    const categories = product.categories ? product.categories.map(cat => cat.name) : [];
    const tags = product.tags ? product.tags.map(tag => tag.name) : [];

    const searchableText = [
      title,
      description,
      ...categories,
      ...tags
    ].join(' ');

    const tokens = tokenize(searchableText);

    return {
      productId: product.id,
      title,
      description,
      categories,
      tags,
      searchableText,
      tokens,
      metadata: {
        slug: product.slug ?? '',
        price: product.price ?? '0',
        inStock: product.stock_status === 'instock',
        featured: product.featured ?? false
      }
    };
  });
}

// Perform keyword search on the index
export function keywordSearch(
  query: string,
  searchIndex: SearchIndex[],
  options: KeywordSearchOptions = {}
): KeywordSearchResult[] {
  const {
    fuzzyMatch = true,
    stemming = true,
    minScore = 0.1,
    boost = {
      title: 3.0,
      description: 1.0,
      categories: 2.0,
      tags: 1.5
    },
    includeMetadata: _includeMetadata = true
  } = options;
  void _includeMetadata; // Preserved for future metadata inclusion feature

  // Tokenize query
  const queryTokens = tokenize(query);
  const processedQuery = stemming ? queryTokens.map(stem) : queryTokens;

  const results: KeywordSearchResult[] = [];

  for (const item of searchIndex) {
    let totalScore = 0;
    const matchedFields: string[] = [];
    const matchedTerms: string[] = [];

    // Process item tokens for comparison
    const _itemTokens = stemming ? item.tokens.map(stem) : item.tokens;
    void _itemTokens; // Preserved for future advanced matching

    // Score different fields with different weights
    const fieldScores = {
      title: calculateFieldScore(processedQuery, tokenize(item.title), fuzzyMatch, stemming),
      description: calculateFieldScore(processedQuery, tokenize(item.description), fuzzyMatch, stemming),
      categories: calculateFieldScore(processedQuery, tokenize(item.categories.join(' ')), fuzzyMatch, stemming),
      tags: calculateFieldScore(processedQuery, tokenize(item.tags.join(' ')), fuzzyMatch, stemming)
    };

    // Apply boosts and accumulate scores
    if (fieldScores.title.score > 0) {
      totalScore += fieldScores.title.score * (boost?.title ?? 3.0);
      matchedFields.push('title');
      matchedTerms.push(...fieldScores.title.matches);
    }

    if (fieldScores.description.score > 0) {
      totalScore += fieldScores.description.score * (boost?.description ?? 1.0);
      matchedFields.push('description');
      matchedTerms.push(...fieldScores.description.matches);
    }

    if (fieldScores.categories.score > 0) {
      totalScore += fieldScores.categories.score * (boost?.categories ?? 2.0);
      matchedFields.push('categories');
      matchedTerms.push(...fieldScores.categories.matches);
    }

    if (fieldScores.tags.score > 0) {
      totalScore += fieldScores.tags.score * (boost?.tags ?? 1.5);
      matchedFields.push('tags');
      matchedTerms.push(...fieldScores.tags.matches);
    }

    // Normalize score by query length and apply minimum threshold
    const normalizedScore = totalScore / Math.max(queryTokens.length, 1);

    if (normalizedScore >= minScore) {
      results.push({
        productId: item.productId,
        slug: item.metadata.slug || '',
        title: item.title,
        price: item.metadata.price || '0',
        categories: item.categories || [],
        inStock: item.metadata.inStock || false,
        featured: item.metadata.featured || false,
        score: normalizedScore,
        matchedFields: Array.from(new Set(matchedFields)),
        matchedTerms: Array.from(new Set(matchedTerms))
      });
    }
  }

  // Sort by score (descending) and return
  return results.sort((a, b) => b.score - a.score);
}

// Calculate score for a specific field
function calculateFieldScore(
  queryTokens: string[],
  fieldTokens: string[],
  fuzzyMatch: boolean,
  stemming: boolean
): { score: number; matches: string[] } {
  let score = 0;
  const matches: string[] = [];

  const processedFieldTokens = stemming ? fieldTokens.map(stem) : fieldTokens;

  for (const queryToken of queryTokens) {
    let bestMatch = 0;
    let matchedToken = '';

    for (const fieldToken of processedFieldTokens) {
      // Exact match
      if (queryToken === fieldToken) {
        bestMatch = Math.max(bestMatch, 1.0);
        matchedToken = fieldToken;
      }
      // Partial match (query token contains field token or vice versa)
      else if (queryToken.includes(fieldToken) || fieldToken.includes(queryToken)) {
        const similarity = Math.min(queryToken.length, fieldToken.length) / Math.max(queryToken.length, fieldToken.length);
        if (similarity > bestMatch) {
          bestMatch = similarity * 0.8; // Partial matches get 80% of full score
          matchedToken = fieldToken;
        }
      }
      // Fuzzy match
      else if (fuzzyMatch && isFuzzyMatch(queryToken, fieldToken, 0.75)) {
        const similarity = 1 - (levenshteinDistance(queryToken, fieldToken) / Math.max(queryToken.length, fieldToken.length));
        if (similarity > bestMatch) {
          bestMatch = similarity * 0.6; // Fuzzy matches get 60% of full score
          matchedToken = fieldToken;
        }
      }
    }

    if (bestMatch > 0) {
      score += bestMatch;
      matches.push(matchedToken);
    }
  }

  return { score, matches };
}

// Helper function to get search suggestions
export function getSearchSuggestions(
  partialQuery: string,
  searchIndex: SearchIndex[],
  maxSuggestions: number = 5
): string[] {
  const suggestions = new Set<string>();
  const lowerQuery = partialQuery.toLowerCase();

  for (const item of searchIndex) {
    // Check title words
    const titleWords = tokenize(item.title ?? '');
    for (const word of titleWords) {
      if (word.startsWith(lowerQuery) && word.length > lowerQuery.length) {
        suggestions.add(word);
        if (suggestions.size >= maxSuggestions) return Array.from(suggestions);
      }
    }

    // Check category names
    for (const category of item.categories ?? []) {
      const categoryLower = category.toLowerCase();
      if (categoryLower.startsWith(lowerQuery) && categoryLower.length > lowerQuery.length) {
        suggestions.add(category);
        if (suggestions.size >= maxSuggestions) return Array.from(suggestions);
      }
    }
  }

  return Array.from(suggestions).slice(0, maxSuggestions);
}

/**
 * Production-optimized logging utility
 * Conditionally logs based on environment and log levels
 */

export type LogLevel = 'error' | 'warn' | 'info' | 'debug' | 'trace';

export interface LogEntry {
  timestamp: string;
  level: LogLevel;
  message: string;
  data?: Record<string, unknown>;
  context?: string;
  requestId?: string;
  userId?: string;
  sessionId?: string;
  tags?: string[];
}

export interface LoggerConfig {
  level: LogLevel;
  enableConsole: boolean;
  enableFile: boolean;
  enableRemote: boolean;
  maxLogSize: number;
  enabledContexts: string[];
}

// Log levels hierarchy (lower number = higher priority)
const LOG_LEVELS: Record<LogLevel, number> = {
  error: 0,
  warn: 1,
  info: 2,
  debug: 3,
  trace: 4,
};

// Default configuration based on environment
const DEFAULT_CONFIG: LoggerConfig = {
  level: process.env.NODE_ENV === 'production' ? 'warn' : 'debug',
  enableConsole: process.env.NODE_ENV !== 'production',
  enableFile: false,
  enableRemote: process.env.NODE_ENV === 'production',
  maxLogSize: 1000, // Max characters per log entry
  enabledContexts: process.env.LOG_CONTEXTS?.split(',') ?? ['*'],
};

class Logger {
  private config: LoggerConfig;
  private buffer: LogEntry[] = [];
  private maxBufferSize = 100;

  constructor(config: Partial<LoggerConfig> = {}) {
    this.config = { ...DEFAULT_CONFIG, ...config };
  }

  private get isProduction(): boolean {
    return process.env.NODE_ENV === 'production';
  }

  private shouldLog(level: LogLevel, context?: string): boolean {
    // Check log level
    if (LOG_LEVELS[level] > LOG_LEVELS[this.config.level]) {
      return false;
    }

    // Check context filter
    if (context && this.config.enabledContexts.length > 0) {
      const enabledContexts = this.config.enabledContexts;
      if (!enabledContexts.includes('*') && !enabledContexts.includes(context)) {
        return false;
      }
    }

    return true;
  }

  private formatMessage(entry: LogEntry): string {
    let message = entry.message;

    // Truncate if too long
    if (message.length > this.config.maxLogSize) {
      message = message.substring(0, this.config.maxLogSize - 3) + '...';
    }

    return message;
  }

  private createLogEntry(
    level: LogLevel,
    message: string,
    data?: Record<string, unknown>,
    context?: string,
    requestId?: string
  ): LogEntry {
    return {
      timestamp: new Date().toISOString(),
      level,
      message: this.formatMessage({ timestamp: '', level, message }),
      data,
      context,
      requestId,
    };
  }

  private writeToConsole(entry: LogEntry): void {
    if (!this.config.enableConsole) return;

    // Skip non-error console output in production
    const isProduction = process.env.NODE_ENV === 'production';
    if (isProduction && entry.level !== 'error') return;

    try {
      const emoji = this.getLevelEmoji(entry.level);
      const prefix = `${emoji} [${entry.level.toUpperCase()}]`;
      const timestamp = new Date(entry.timestamp).toLocaleTimeString();
      const context = entry.context ? ` [${entry.context}]` : '';
      const requestId = entry.requestId ? ` [${entry.requestId}]` : '';

      const fullMessage = `${prefix} ${timestamp}${context}${requestId} ${entry.message}`;

      switch (entry.level) {
        case 'error':
          console.error(fullMessage, entry.data);
          break;
        case 'warn':
          console.warn(fullMessage, entry.data);
          break;
        case 'info':
          console.info(fullMessage, entry.data);
          break;
        case 'debug':
        case 'trace':
          console.log(fullMessage, entry.data);
          break;
      }
    } catch {
      // Gracefully handle console errors - fallback to basic console.log (only for errors in production)
      try {
        if (!isProduction || entry.level === 'error') {
          console.log(`Logger Error: Failed to write log entry: ${entry.message}`);
        }
      } catch {
        // If even basic console.log fails, fail silently to prevent infinite loops
      }
    }
  }

  private getLevelEmoji(level: LogLevel): string {
    const emojis: Record<LogLevel, string> = {
      error: '🚨',
      warn: '⚠️',
      info: 'ℹ️',
      debug: '🐛',
      trace: '🔬',
    };
    return emojis[level] || '📝';
  }

  private addToBuffer(entry: LogEntry): void {
    this.buffer.push(entry);

    // Maintain buffer size - truncate to maxBufferSize if exceeded
    if (this.buffer.length > this.maxBufferSize) {
      const excessEntries = this.buffer.length - this.maxBufferSize;
      this.buffer.splice(0, excessEntries);
    }
  }

  private async sendToRemote(entry: LogEntry): Promise<void> {
    // Disable remote logging in development to prevent recursion issues
    if (!this.config.enableRemote || process.env.NODE_ENV === 'development') return;

    try {
      // Import remote logger dynamically to avoid circular dependencies
      const { remoteLogger } = await import('./remote-logging');

      // Send to remote logging service
      await remoteLogger.log(
        entry.level === 'trace' ? 'debug' : entry.level,
        entry.message,
        entry.data,
        entry.context,
        {
          requestId: entry.requestId,
          userId: entry.userId,
          sessionId: entry.sessionId,
          tags: entry.tags
        }
      );
    } catch (error) {
      // Fail silently to prevent logging loops
      if (this.config.enableConsole && process.env.NODE_ENV !== 'production') {
        console.error('Failed to send log to remote service:', error);
      }
    }
  }

  private log(
    level: LogLevel,
    message: string,
    data?: Record<string, unknown>,
    context?: string,
    requestId?: string
  ): void {
    if (!this.shouldLog(level, context)) {
      return;
    }

    const entry = this.createLogEntry(level, message, data, context, requestId);

    // Add to buffer for debugging
    this.addToBuffer(entry);

    // Write to console
    this.writeToConsole(entry);

    // Send to remote (async, fire-and-forget) - disabled in development
    if (process.env.NODE_ENV !== 'development') {
      this.sendToRemote(entry).catch(() => {
        // Silently ignore remote logging errors
      });
    }
  }

  // Public logging methods
  error(message: string, data?: Record<string, unknown>, context?: string, requestId?: string): void {
    this.log('error', message, data, context, requestId);
  }

  warn(message: string, data?: Record<string, unknown>, context?: string, requestId?: string): void {
    this.log('warn', message, data, context, requestId);
  }

  info(message: string, data?: Record<string, unknown>, context?: string, requestId?: string): void {
    this.log('info', message, data, context, requestId);
  }

  debug(message: string, data?: Record<string, unknown>, context?: string, requestId?: string): void {
    this.log('debug', message, data, context, requestId);
  }

  trace(message: string, data?: Record<string, unknown>, context?: string, requestId?: string): void {
    this.log('trace', message, data, context, requestId);
  }

  // Console grouping methods (only in development)
  group(label: string): void {
    if (this.isProduction) return;
    if (typeof console !== 'undefined' && console.group) {
      console.group(label);
    }
  }

  groupEnd(): void {
    if (this.isProduction) return;
    if (typeof console !== 'undefined' && console.groupEnd) {
      console.groupEnd();
    }
  }

  table(data: unknown): void {
    if (this.isProduction) return;
    if (typeof console !== 'undefined' && console.table) {
      console.table(data);
    }
  }

  // Utility methods
  setLevel(level: LogLevel): void {
    this.config.level = level;
  }

  getBuffer(): LogEntry[] {
    return [...this.buffer];
  }

  clearBuffer(): void {
    this.buffer = [];
  }

  // Performance logging helpers
  time(label: string, context?: string): () => void {
    const start = performance.now();
    const startMessage = `⏱️ Timer started: ${label}`;

    this.debug(startMessage, undefined, context);

    return () => {
      const duration = performance.now() - start;
      const endMessage = `⏱️ Timer ended: ${label} (${duration.toFixed(2)}ms)`;
      this.debug(endMessage, { duration }, context);
    };
  }
}

// Export Logger class for custom instances
export { Logger };

// Create singleton logger instance
export const logger = new Logger();

// Convenience functions for backward compatibility
export function logError(message: string, data?: Record<string, unknown>, context?: string): void {
  logger.error(message, data, context);
}

export function logWarn(message: string, data?: Record<string, unknown>, context?: string): void {
  logger.warn(message, data, context);
}

export function logInfo(message: string, data?: Record<string, unknown>, context?: string): void {
  logger.info(message, data, context);
}

export function logDebug(message: string, data?: Record<string, unknown>, context?: string): void {
  logger.debug(message, data, context);
}

// Production-safe console replacement
export const productionConsole = {
  log: (message: string, ...args: unknown[]) => logger.info(message, { args }),
  error: (message: string, ...args: unknown[]) => logger.error(message, { args }),
  warn: (message: string, ...args: unknown[]) => logger.warn(message, { args }),
  info: (message: string, ...args: unknown[]) => logger.info(message, { args }),
  debug: (message: string, ...args: unknown[]) => logger.debug(message, { args }),
};

export default logger;

// Enhanced Memgraph Analytics Schema for User Journey Tracking
import * as neo4j from 'neo4j-driver';
import { logger } from '@/lib/logger';

// Connect to Memgraph using existing connection pattern
let driver: neo4j.Driver | null = null;

function getDriver(): neo4j.Driver {
  if (!driver) {
    const uri = process.env.MEMGRAPH_URL || 'bolt://143.42.189.57:7687';
    const user = process.env.MEMGRAPH_USER || '';
    const password = process.env.MEMGRAPH_PASSWORD || '';

    driver = neo4j.driver(uri, neo4j.auth.basic(user, password));
  }
  return driver;
}

// Helper function to execute queries with session management
async function withSession<T>(operation: (session: neo4j.Session) => Promise<T>): Promise<T> {
  const session = getDriver().session();
  try {
    return await operation(session);
  } finally {
    await session.close();
  }
}

// Analytics Node Types
export interface AnalyticsUser {
  id: string;
  isAnonymous: boolean;
  firstSeen: number;
  lastSeen: number;
  totalSessions: number;
  metadata?: Record<string, unknown>;
}

export interface AnalyticsSession {
  id: string;
  userId: string;
  startTime: number;
  endTime?: number;
  pageViews: number;
  events: number;
  deviceType?: string;
  browser?: string;
  referrer?: string;
}

export interface AnalyticsEvent {
  id: string;
  type: string;
  timestamp: number;
  sessionId: string;
  userId: string;
  productId?: number;
  metadata: Record<string, unknown>;
}

export interface PageView {
  id: string;
  path: string;
  timestamp: number;
  sessionId: string;
  userId: string;
  duration?: number;
  metadata: Record<string, unknown>;
}

// Initialize Analytics Schema
export async function initializeMemgraphAnalytics(): Promise<void> {
  try {
    await withSession(async (session) => {
      // Create indexes for performance
      const indexes = [
        'CREATE INDEX ON :AnalyticsUser(id);',
        'CREATE INDEX ON :AnalyticsSession(id);',
        'CREATE INDEX ON :AnalyticsSession(userId);',
        'CREATE INDEX ON :AnalyticsEvent(id);',
        'CREATE INDEX ON :AnalyticsEvent(type);',
        'CREATE INDEX ON :AnalyticsEvent(timestamp);',
        'CREATE INDEX ON :PageView(id);',
        'CREATE INDEX ON :PageView(path);',
        'CREATE INDEX ON :PageView(timestamp);',
      ];

      for (const indexQuery of indexes) {
        try {
          await session.run(indexQuery);
        } catch {
          // Index might already exist, continue
          logger.info(`Index creation skipped (might exist): ${indexQuery}`);
        }
      }

      // Create constraints
      const constraints = [
        'CREATE CONSTRAINT ON (u:AnalyticsUser) ASSERT u.id IS UNIQUE;',
        'CREATE CONSTRAINT ON (s:AnalyticsSession) ASSERT s.id IS UNIQUE;',
        'CREATE CONSTRAINT ON (e:AnalyticsEvent) ASSERT e.id IS UNIQUE;',
        'CREATE CONSTRAINT ON (pv:PageView) ASSERT pv.id IS UNIQUE;',
      ];

      for (const constraintQuery of constraints) {
        try {
          await session.run(constraintQuery);
        } catch {
          // Constraint might already exist, continue
          logger.info(`Constraint creation skipped (might exist): ${constraintQuery}`);
        }
      }

      logger.info('✅ Memgraph analytics schema initialized successfully');
    });
  } catch (error) {
    logger.error('❌ Failed to initialize Memgraph analytics schema:', error as Record<string, unknown>);
    throw error;
  }
}

// Create or update analytics user
export async function createAnalyticsUser(userData: AnalyticsUser): Promise<void> {
  try {
    await withSession(async (session) => {
      await session.run(`
        MERGE (u:AnalyticsUser {id: $id})
        SET u.isAnonymous = $isAnonymous,
            u.firstSeen = CASE WHEN u.firstSeen IS NULL THEN $firstSeen ELSE u.firstSeen END,
            u.lastSeen = $lastSeen,
            u.totalSessions = COALESCE(u.totalSessions, 0) + 1,
            u.metadata = $metadata,
            u.updatedAt = datetime()
      `, userData);

      logger.info(`Analytics user created/updated: ${userData.id}`);
    });
  } catch (error) {
    logger.error('Failed to create analytics user:', error as Record<string, unknown>);
    throw error;
  }
}

// Create analytics session
export async function createAnalyticsSession(sessionData: AnalyticsSession): Promise<void> {
  try {
    await withSession(async (session) => {
      // Create session node
      await session.run(`
        CREATE (s:AnalyticsSession {
          id: $id,
          userId: $userId,
          startTime: $startTime,
          endTime: $endTime,
          pageViews: $pageViews,
          events: $events,
          deviceType: $deviceType,
          browser: $browser,
          referrer: $referrer,
          createdAt: datetime()
        })
      `, sessionData);

      // Create relationship to user
      await session.run(`
        MATCH (u:AnalyticsUser {id: $userId})
        MATCH (s:AnalyticsSession {id: $sessionId})
        MERGE (u)-[:HAS_SESSION]->(s)
      `, {
        userId: sessionData.userId,
        sessionId: sessionData.id
      });

      logger.info(`Analytics session created: ${sessionData.id}`);
    });
  } catch (error) {
    logger.error('Failed to create analytics session:', error as Record<string, unknown>);
    throw error;
  }
}

// Create analytics event
export async function createAnalyticsEvent(eventData: AnalyticsEvent): Promise<void> {
  try {
    await withSession(async (session) => {
      // Create event node
      await session.run(`
        CREATE (e:AnalyticsEvent {
          id: $id,
          type: $type,
          timestamp: $timestamp,
          sessionId: $sessionId,
          userId: $userId,
          productId: $productId,
          metadata: $metadata,
          createdAt: datetime()
        })
      `, eventData);

      // Create relationships
      const relationships = [
        // Event belongs to session
        `
        MATCH (s:AnalyticsSession {id: $sessionId})
        MATCH (e:AnalyticsEvent {id: $eventId})
        MERGE (s)-[:HAS_EVENT]->(e)
        `,
        // Event belongs to user
        `
        MATCH (u:AnalyticsUser {id: $userId})
        MATCH (e:AnalyticsEvent {id: $eventId})
        MERGE (u)-[:PERFORMED]->(e)
        `
      ];

      for (const relQuery of relationships) {
        await session.run(relQuery, {
          sessionId: eventData.sessionId,
          userId: eventData.userId,
          eventId: eventData.id
        });
      }

      // If product event, create relationship to product
      if (eventData.productId) {
        await session.run(`
          MATCH (p:Product {id: $productId})
          MATCH (e:AnalyticsEvent {id: $eventId})
          MERGE (e)-[:RELATES_TO]->(p)
        `, {
          productId: eventData.productId,
          eventId: eventData.id
        });
      }

      logger.info(`Analytics event created: ${eventData.type} for ${eventData.userId}`);
    });
  } catch (error) {
    logger.error('Failed to create analytics event:', error as Record<string, unknown>);
    throw error;
  }
}

// Create page view
export async function createPageView(pageViewData: PageView): Promise<void> {
  try {
    await withSession(async (session) => {
      // Create page view node
      await session.run(`
        CREATE (pv:PageView {
          id: $id,
          path: $path,
          timestamp: $timestamp,
          sessionId: $sessionId,
          userId: $userId,
          duration: $duration,
          metadata: $metadata,
          createdAt: datetime()
        })
      `, pageViewData);

      // Create relationships
      const relationships = [
        // Page view belongs to session
        `
        MATCH (s:AnalyticsSession {id: $sessionId})
        MATCH (pv:PageView {id: $pageViewId})
        MERGE (s)-[:HAS_PAGE_VIEW]->(pv)
        `,
        // Page view belongs to user
        `
        MATCH (u:AnalyticsUser {id: $userId})
        MATCH (pv:PageView {id: $pageViewId})
        MERGE (u)-[:VIEWED]->(pv)
        `
      ];

      for (const relQuery of relationships) {
        await session.run(relQuery, {
          sessionId: pageViewData.sessionId,
          userId: pageViewData.userId,
          pageViewId: pageViewData.id
        });
      }

      logger.info(`Page view created: ${pageViewData.path} for ${pageViewData.userId}`);
    });
  } catch (error) {
    logger.error('Failed to create page view:', error as Record<string, unknown>);
    throw error;
  }
}

// Update session end time and stats
export async function updateSessionEnd(sessionId: string, endTime: number, stats: {
  pageViews: number;
  events: number;
}): Promise<void> {
  try {
    await withSession(async (session) => {
      await session.run(`
        MATCH (s:AnalyticsSession {id: $sessionId})
        SET s.endTime = $endTime,
            s.pageViews = $pageViews,
            s.events = $events,
            s.updatedAt = datetime()
      `, {
        sessionId,
        endTime,
        pageViews: stats.pageViews,
        events: stats.events
      });

      logger.info(`Session updated: ${sessionId}`);
    });
  } catch (error) {
    logger.error('Failed to update session:', error as Record<string, unknown>);
    throw error;
  }
}

// Analytics Query Functions

// Get user journey path
export async function getUserJourney(userId: string, limit: number = 50): Promise<any[]> {
  try {
    return await withSession(async (session) => {
      const result = await session.run(`
        MATCH (u:AnalyticsUser {id: $userId})-[:HAS_SESSION]->(s:AnalyticsSession)
        MATCH (s)-[:HAS_PAGE_VIEW]->(pv:PageView)
        RETURN pv.path as path, pv.timestamp as timestamp, s.id as sessionId
        ORDER BY pv.timestamp DESC
        LIMIT $limit
      `, { userId, limit });

      return result.records.map(record => ({
        path: record.get('path'),
        timestamp: record.get('timestamp'),
        sessionId: record.get('sessionId')
      }));
    });
  } catch (error) {
    logger.error('Failed to get user journey:', error as Record<string, unknown>);
    return [];
  }
}

// Get popular paths
export async function getPopularPaths(limit: number = 10): Promise<any[]> {
  try {
    return await withSession(async (session) => {
      const result = await session.run(`
        MATCH (pv:PageView)
        RETURN pv.path as path, count(*) as views
        ORDER BY views DESC
        LIMIT $limit
      `, { limit });

      return result.records.map(record => ({
        path: record.get('path'),
        views: record.get('views').toNumber()
      }));
    });
  } catch (error) {
    logger.error('Failed to get popular paths:', error as Record<string, unknown>);
    return [];
  }
}

// Get user behavior patterns
export async function getUserBehaviorPatterns(userId: string): Promise<any> {
  try {
    return await withSession(async (session) => {
      const result = await session.run(`
        MATCH (u:AnalyticsUser {id: $userId})-[:PERFORMED]->(e:AnalyticsEvent)
        RETURN e.type as eventType, count(*) as frequency
        ORDER BY frequency DESC
      `, { userId });

      const patterns = result.records.map(record => ({
        eventType: record.get('eventType'),
        frequency: record.get('frequency').toNumber()
      }));

      return { userId, patterns };
    });
  } catch (error) {
    logger.error('Failed to get user behavior patterns:', error as Record<string, unknown>);
    return { userId, patterns: [] };
  }
}

// Clean up old analytics data
export async function cleanupOldAnalyticsData(daysOld: number = 90): Promise<number> {
  try {
    return await withSession(async (session) => {
      const cutoffTime = Date.now() - (daysOld * 24 * 60 * 60 * 1000);

      const result = await session.run(`
        MATCH (e:AnalyticsEvent)
        WHERE e.timestamp < $cutoffTime
        DETACH DELETE e
        RETURN count(*) as deletedCount
      `, { cutoffTime });

      const deletedCount = result.records[0]?.get('deletedCount')?.toNumber() || 0;
      logger.info(`Cleaned up ${deletedCount} old analytics events`);

      return deletedCount;
    });
  } catch (error) {
    logger.error('Failed to cleanup old analytics data:', error as Record<string, unknown>);
    return 0;
  }
}

// Export connection cleanup function
export async function closeAnalyticsConnection(): Promise<void> {
  if (driver) {
    await driver.close();
    driver = null;
    logger.info('Memgraph analytics connection closed');
  }
}

// Memory Optimization Utilities
// Addresses the 47MB memory usage issue identified in testing

import { logger } from '@/lib/logger';

interface MemoryStats {
  heapUsed: number;
  heapTotal: number;
  external: number;
  rss: number;
}

interface CacheEntry<T> {
  value: T;
  timestamp: number;
  accessCount: number;
  size?: number;
}

export class MemoryOptimizedCache<T> {
  private cache = new Map<string, CacheEntry<T>>();
  private maxSize: number;
  private ttl: number;
  private cleanupInterval: NodeJS.Timeout | null = null;

  constructor(maxSize = 1000, ttlMs = 300000) { // 5 minute default TTL
    this.maxSize = maxSize;
    this.ttl = ttlMs;
    this.startCleanup();
  }

  set(key: string, value: T): void {
    // If at capacity, remove least recently used
    if (this.cache.size >= this.maxSize) {
      this.evictLRU();
    }

    this.cache.set(key, {
      value,
      timestamp: Date.now(),
      accessCount: 1,
      size: this.estimateSize(value)
    });
  }

  get(key: string): T | undefined {
    const entry = this.cache.get(key);
    if (!entry) return undefined;

    // Check if expired
    if (Date.now() - entry.timestamp > this.ttl) {
      this.cache.delete(key);
      return undefined;
    }

    // Update access count for LRU
    entry.accessCount++;
    return entry.value;
  }

  private evictLRU(): void {
    let lruKey: string | null = null;
    let lruAccessCount = Infinity;
    let oldestTimestamp = Infinity;

    for (const [key, entry] of this.cache.entries()) {
      if (entry.accessCount < lruAccessCount ||
          (entry.accessCount === lruAccessCount && entry.timestamp < oldestTimestamp)) {
        lruKey = key;
        lruAccessCount = entry.accessCount;
        oldestTimestamp = entry.timestamp;
      }
    }

    if (lruKey) {
      this.cache.delete(lruKey);
    }
  }

  private estimateSize(value: T): number {
    try {
      return JSON.stringify(value).length * 2; // Rough estimate: 2 bytes per char
    } catch {
      return 100; // Fallback estimate
    }
  }

  private startCleanup(): void {
    this.cleanupInterval = setInterval(() => {
      this.cleanup();
    }, 60000); // Cleanup every minute
  }

  private cleanup(): void {
    const now = Date.now();
    const toDelete: string[] = [];

    for (const [key, entry] of this.cache.entries()) {
      if (now - entry.timestamp > this.ttl) {
        toDelete.push(key);
      }
    }

    toDelete.forEach(key => this.cache.delete(key));

    if (toDelete.length > 0) {
      logger.debug(`Cleaned up ${toDelete.length} expired cache entries`);
    }
  }

  clear(): void {
    this.cache.clear();
  }

  delete(key: string): boolean {
    return this.cache.delete(key);
  }

  get size(): number {
    return this.cache.size;
  }

  keys(): IterableIterator<string> {
    return this.cache.keys();
  }

  entries(): IterableIterator<[string, CacheEntry<T>]> {
    return this.cache.entries();
  }

  getStats() {
    const entries = Array.from(this.cache.values());
    const totalSize = entries.reduce((sum, entry) => sum + (entry.size || 0), 0);

    return {
      size: this.cache.size,
      maxSize: this.maxSize,
      totalEstimatedSize: totalSize,
      averageAccessCount: entries.reduce((sum, e) => sum + e.accessCount, 0) / entries.length || 0
    };
  }

  destroy(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = null;
    }
    this.cache.clear();
  }
}

// Event bus memory optimization
export class MemoryOptimizedEventBus {
  private listeners = new Map<string, Set<Function>>();
  private eventHistory = new MemoryOptimizedCache<any>(100, 60000); // Keep last 100 events for 1 minute

  on(event: string, listener: Function): () => void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }
    this.listeners.get(event)?.add(listener);

    // Return cleanup function
    return () => {
      const listeners = this.listeners.get(event);
      if (listeners) {
        listeners.delete(listener);
        if (listeners.size === 0) {
          this.listeners.delete(event);
        }
      }
    };
  }

  emit(event: string, data: any): void {
    // Store in history for debugging
    this.eventHistory.set(`${event}_${Date.now()}`, data);

    const listeners = this.listeners.get(event);
    if (listeners) {
      listeners.forEach(listener => {
        try {
          listener(data);
        } catch (error) {
          logger.error('Event listener error:', error as Record<string, unknown>);
        }
      });
    }
  }

  removeAllListeners(event?: string): void {
    if (event) {
      this.listeners.delete(event);
    } else {
      this.listeners.clear();
    }
  }

  getStats() {
    return {
      listenerCount: Array.from(this.listeners.values()).reduce((sum, set) => sum + set.size, 0),
      eventTypes: this.listeners.size,
      historyStats: this.eventHistory.getStats()
    };
  }

  destroy(): void {
    this.listeners.clear();
    this.eventHistory.destroy();
  }
}

// Memory monitoring
class MemoryMonitor {
  private measurements: MemoryStats[] = [];
  private maxMeasurements = 100;

  measure(): MemoryStats {
    const memUsage = process.memoryUsage();
    const stats: MemoryStats = {
      heapUsed: memUsage.heapUsed,
      heapTotal: memUsage.heapTotal,
      external: memUsage.external,
      rss: memUsage.rss
    };

    this.measurements.push(stats);
    if (this.measurements.length > this.maxMeasurements) {
      this.measurements.shift();
    }

    return stats;
  }

  getStats() {
    if (this.measurements.length === 0) return null;

    const latest = this.measurements[this.measurements.length - 1];
    const peak = this.measurements.reduce((max, curr) =>
      curr.heapUsed > max.heapUsed ? curr : max
    );

    return {
      current: latest,
      peak,
      measurementCount: this.measurements.length,
      trend: this.measurements.length > 1 && latest && this.measurements[0] ?
        latest.heapUsed - this.measurements[0].heapUsed : 0
    };
  }

  forceGC(): void {
    if (global.gc) {
      global.gc();
      logger.debug('Forced garbage collection');
    }
  }
}

export const memoryMonitor = new MemoryMonitor();

// Helper function to create memory-optimized objects
export function createMemoryOptimizedMap<K, V>(
  maxSize = 1000,
  ttlMs = 300000
): Map<K, V> & { cleanup: () => void } {
  const map = new Map<K, { value: V; timestamp: number }>();

  const cleanup = () => {
    const now = Date.now();
    const toDelete: K[] = [];

    for (const [key, entry] of map.entries()) {
      if (now - entry.timestamp > ttlMs) {
        toDelete.push(key);
      }
    }

    toDelete.forEach(key => map.delete(key));
  };

  const intervalId = setInterval(cleanup, Math.min(ttlMs / 4, 60000));

  const optimizedMap = {
    set(key: K, value: V): typeof optimizedMap {
      if (map.size >= maxSize) {
        const firstKey = map.keys().next().value;
        if (firstKey !== undefined) {
          map.delete(firstKey);
        }
      }
      map.set(key, { value, timestamp: Date.now() });
      return this;
    },

    get(key: K): V | undefined {
      const entry = map.get(key);
      if (!entry) return undefined;

      if (Date.now() - entry.timestamp > ttlMs) {
        map.delete(key);
        return undefined;
      }

      return entry.value;
    },

    has(key: K): boolean {
      const entry = map.get(key);
      if (!entry) return false;

      if (Date.now() - entry.timestamp > ttlMs) {
        map.delete(key);
        return false;
      }

      return true;
    },

    delete(key: K): boolean {
      return map.delete(key);
    },

    clear(): void {
      map.clear();
    },

    get size(): number {
      cleanup(); // Clean expired entries before returning size
      return map.size;
    },

    cleanup(): void {
      cleanup();
    },

    destroy(): void {
      clearInterval(intervalId);
      map.clear();
    }
  } as Map<K, V> & { cleanup: () => void; destroy: () => void };

  return optimizedMap;
}

// Memory-optimized JSON stringify with circular reference detection
export function safeJSONStringify(obj: any, maxDepth = 10): string {
  const seen = new WeakSet();

  const stringifyWithCircularCheck = (value: any, depth: number): any => {
    if (depth > maxDepth) return '[Max Depth Reached]';

    if (value === null || typeof value !== 'object') {
      return value;
    }

    if (seen.has(value)) {
      return '[Circular Reference]';
    }

    seen.add(value);

    if (Array.isArray(value)) {
      return value.map(item => stringifyWithCircularCheck(item, depth + 1));
    }

    const result: any = {};
    for (const key in value) {
      if (Object.prototype.hasOwnProperty.call(value, key)) {
        result[key] = stringifyWithCircularCheck(value[key], depth + 1);
      }
    }

    return result;
  };

  try {
    return JSON.stringify(stringifyWithCircularCheck(obj, 0));
  } catch {
    return JSON.stringify({ error: 'Failed to stringify object' });
  }
}

/**
 * Adapter layer for migrating from old EntityMetadata to new typed metadata
 * This allows gradual migration without breaking existing code
 */

import { EntityMetadata as OldEntityMetadata } from '@/types/common';
import {
  EntityMetadata as NewEntityMetadata,
  UserMetadata,
  migrateFromLegacyMetadata,
  createProductMetadata,
  createOrderMetadata,
  createSearchMetadata,
  createUserMetadata,
  createSyncMetadata,
  createPageMetadata,
  createGenericMetadata,
} from '@/types/metadata';

/**
 * Adapter class that can work with both old and new metadata formats
 */
export class MetadataAdapter {
  /**
   * Convert new metadata back to old format for backward compatibility
   */
  static toLegacy(metadata: NewEntityMetadata): OldEntityMetadata {
    const base: OldEntityMetadata = {
      timestamp: metadata.timestamp,
      correlationId: metadata.correlationId,
      requestId: metadata.requestId,
      environment: metadata.environment,
      region: metadata.region,
      tenant: metadata.tenant,
      createdAt: metadata.createdAt,
      updatedAt: metadata.updatedAt,
      createdBy: metadata.createdBy,
      updatedBy: metadata.updatedBy,
      version: metadata.version,
      tags: metadata.tags,
      source: metadata.source,
    };

    // Add type-specific fields
    switch (metadata.type) {
      case 'product':
        return {
          ...base,
          productId: metadata.productId,
        };

      case 'order':
        return {
          ...base,
          orderId: metadata.orderId,
          orderTotal: metadata.orderTotal,
          itemCount: metadata.itemCount,
          paymentMethod: metadata.paymentMethod,
          shippingMethod: metadata.shippingMethod,
        };

      case 'search':
        return {
          ...base,
          searchQuery: metadata.searchQuery,
          queryLength: metadata.queryLength,
          hasFilters: metadata.hasFilters,
          clickedResults: metadata.clickedResults,
        };

      case 'user':
        return {
          ...base,
          type: 'user',
          userId: metadata.userId,
          sessionId: metadata.sessionId,
          userAgent: metadata.userAgent,
          ipAddress: metadata.ipAddress,
        } as UserMetadata;

      case 'sync':
        return {
          ...base,
          syncSource: metadata.syncSource,
          autoSync: metadata.autoSync,
          realTimeSync: metadata.realTimeSync,
          priority: metadata.priority,
        };

      case 'page':
        return {
          ...base,
          pageUrl: metadata.pageUrl,
          entryType: metadata.entryType,
          startTime: metadata.startTime,
        };

      case 'validation':
        return {
          ...base,
          validatedAt: metadata.validatedAt,
        };

      case 'analytics':
      case 'generic':
      default:
        return {
          ...base,
          name: metadata.type === 'generic' ? metadata.name : undefined,
          value: metadata.type === 'generic' ? metadata.value : undefined,
        };
    }
  }

  /**
   * Convert old metadata to new format
   */
  static fromLegacy(metadata: OldEntityMetadata): NewEntityMetadata {
    return migrateFromLegacyMetadata(metadata);
  }

  /**
   * Check if metadata is in new format
   */
  static isNewFormat(metadata: any): metadata is NewEntityMetadata {
    return metadata && typeof metadata === 'object' && 'type' in metadata;
  }

  /**
   * Check if metadata is in old format
   */
  static isOldFormat(metadata: any): metadata is OldEntityMetadata {
    return metadata && typeof metadata === 'object' && !('type' in metadata);
  }

  /**
   * Normalize metadata to new format regardless of input
   */
  static normalize(metadata: OldEntityMetadata | NewEntityMetadata | unknown): NewEntityMetadata {
    if (!metadata || typeof metadata !== 'object') {
      return createGenericMetadata({});
    }

    if (this.isNewFormat(metadata)) {
      return metadata;
    }

    if (this.isOldFormat(metadata)) {
      return this.fromLegacy(metadata);
    }

    // Unknown format - treat as generic
    return createGenericMetadata(metadata as any);
  }
}

/**
 * Helper functions for working with metadata in transition period
 */

// For functions that expect old metadata
export function withLegacyMetadata<T extends any[], R>(
  fn: (...args: T) => R
): (...args: T) => R {
  return (...args: T): R => {
    // Convert any NewEntityMetadata arguments to OldEntityMetadata
    const convertedArgs = args.map(arg => {
      if (MetadataAdapter.isNewFormat(arg)) {
        return MetadataAdapter.toLegacy(arg);
      }
      return arg;
    }) as T;

    return fn(...convertedArgs);
  };
}

// For functions that expect new metadata
export function withNewMetadata<T extends any[], R>(
  fn: (...args: T) => R
): (...args: T) => R {
  return (...args: T): R => {
    // Convert any OldEntityMetadata arguments to NewEntityMetadata
    const convertedArgs = args.map(arg => {
      if (MetadataAdapter.isOldFormat(arg)) {
        return MetadataAdapter.fromLegacy(arg);
      }
      return arg;
    }) as T;

    return fn(...convertedArgs);
  };
}

/**
 * Decorator for class methods to auto-convert metadata
 */
export function AutoConvertMetadata(target: any, propertyKey: string, descriptor: PropertyDescriptor) {
  const originalMethod = descriptor.value;

  descriptor.value = function (...args: any[]) {
    // Convert metadata arguments
    const convertedArgs = args.map(arg => {
      if (MetadataAdapter.isOldFormat(arg)) {
        return MetadataAdapter.fromLegacy(arg);
      }
      return arg;
    });

    // Call original method
    const result = originalMethod.apply(this, convertedArgs);

    // Convert result if it's metadata
    if (MetadataAdapter.isNewFormat(result)) {
      return MetadataAdapter.toLegacy(result);
    }

    return result;
  };

  return descriptor;
}

/**
 * Type-safe metadata builders for common scenarios
 */
export class MetadataBuilder {
  static forProduct(productId: number, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createProductMetadata({
      productId,
      timestamp: Date.now(),
      ...additional,
    });
  }

  static forOrder(orderId: string, orderTotal: number, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createOrderMetadata({
      orderId,
      orderTotal,
      itemCount: 0,
      timestamp: Date.now(),
      ...additional,
    });
  }

  static forSearch(query: string, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createSearchMetadata({
      searchQuery: query,
      queryLength: query.length,
      hasFilters: false,
      timestamp: Date.now(),
      ...additional,
    });
  }

  static forUser(userId?: string, sessionId?: string, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createUserMetadata({
      userId,
      sessionId,
      timestamp: Date.now(),
      ...additional,
    });
  }

  static forSync(source: string, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createSyncMetadata({
      syncSource: source,
      timestamp: Date.now(),
      ...additional,
    });
  }

  static forPage(url: string, additional?: Partial<OldEntityMetadata>): NewEntityMetadata {
    return createPageMetadata({
      pageUrl: url,
      timestamp: Date.now(),
      ...additional,
    });
  }
}

// Multi-Vector Embedding Strategy for Enhanced Semantic Search
import { generateEnhancedEmbedding } from './embeddings';
import { logger } from './logger';
import { WCProduct } from '@/types/woocommerce';

export interface MultiVectorEmbedding {
  productId: number;
  titleEmbedding: number[];
  descriptionEmbedding: number[];
  categoryEmbedding: number[];
  benefitsEmbedding: number[];
  combinedEmbedding: number[];
  metadata: {
    productSlug: string;
    timestamp: string;
    embeddingVersion: string;
  };
}

export interface MultiVectorSearchOptions {
  weights?: {
    title?: number;
    description?: number;
    categories?: number;
    benefits?: number;
  };
  useEnhanced?: boolean;
  intent?: 'product' | 'health' | 'recipe' | 'information' | 'comparison';
  dynamicWeights?: boolean;
}

// Default weights for combining embeddings
const DEFAULT_WEIGHTS = {
  title: 0.4,
  description: 0.3,
  categories: 0.2,
  benefits: 0.1,
};

// Intent-based weight configurations
const INTENT_WEIGHTS = {
  product: { title: 0.5, description: 0.3, categories: 0.15, benefits: 0.05 },
  health: { title: 0.2, description: 0.2, categories: 0.15, benefits: 0.45 },
  recipe: { title: 0.4, description: 0.4, categories: 0.15, benefits: 0.05 },
  information: { title: 0.3, description: 0.4, categories: 0.2, benefits: 0.1 },
  comparison: { title: 0.3, description: 0.25, categories: 0.2, benefits: 0.25 },
};

/**
 * Calculate dynamic weights based on search intent and context
 */
export function calculateDynamicWeights(
  query: string,
  intent: 'product' | 'health' | 'recipe' | 'information' | 'comparison' = 'product',
  userContext?: {
    healthFocused?: boolean;
    experienceLevel?: 'beginner' | 'intermediate' | 'expert';
    primaryUse?: 'cooking' | 'health' | 'both';
  }
): MultiVectorSearchOptions['weights'] {
  // Start with intent-based weights
  let weights = { ...INTENT_WEIGHTS[intent] };

  // Adjust based on query characteristics
  const lowerQuery = query.toLowerCase();

  // Health-focused adjustments
  if (lowerQuery.match(/\b(benefit|health|healing|medicine|therapeutic|cure|treat)\b/)) {
    weights.benefits += 0.15;
    weights.description += 0.05;
    weights.title -= 0.1;
    weights.categories -= 0.1;
  }

  // Brand/quality focused adjustments
  if (lowerQuery.match(/\b(organic|pure|raw|natural|premium|best|quality)\b/)) {
    weights.title += 0.1;
    weights.categories += 0.05;
    weights.description -= 0.05;
    weights.benefits -= 0.1;
  }

  // Recipe/cooking focused adjustments
  if (lowerQuery.match(/\b(cook|recipe|ingredient|spice|flavor|seasoning)\b/)) {
    weights.description += 0.15;
    weights.categories += 0.1;
    weights.benefits -= 0.15;
    weights.title -= 0.1;
  }

  // User context adjustments
  if (userContext) {
    if (userContext.healthFocused) {
      weights.benefits += 0.1;
      weights.description += 0.05;
      weights.title -= 0.075;
      weights.categories -= 0.075;
    }

    if (userContext.experienceLevel === 'beginner') {
      weights.description += 0.1;
      weights.categories += 0.05;
      weights.title -= 0.075;
      weights.benefits -= 0.075;
    }

    if (userContext.primaryUse === 'health') {
      weights.benefits += 0.15;
      weights.description += 0.05;
      weights.title -= 0.1;
      weights.categories -= 0.1;
    } else if (userContext.primaryUse === 'cooking') {
      weights.description += 0.1;
      weights.categories += 0.1;
      weights.benefits -= 0.15;
      weights.title -= 0.05;
    }
  }

  // Normalize weights to sum to 1
  const totalWeight = weights.title + weights.description + weights.categories + weights.benefits;
  if (totalWeight !== 1) {
    weights.title /= totalWeight;
    weights.description /= totalWeight;
    weights.categories /= totalWeight;
    weights.benefits /= totalWeight;
  }

  // Ensure minimum weights
  const minWeight = 0.05;
  if (weights.title < minWeight) weights.title = minWeight;
  if (weights.description < minWeight) weights.description = minWeight;
  if (weights.categories < minWeight) weights.categories = minWeight;
  if (weights.benefits < minWeight) weights.benefits = minWeight;

  return weights;
}

/**
 * Generate multi-vector embeddings for a product
 */
export async function generateMultiVectorEmbedding(
  product: WCProduct,
  healthBenefits?: string[]
): Promise<MultiVectorEmbedding> {
  try {
    logger.info(`Generating multi-vector embeddings for product: ${product.name}`);

    // Extract text from different fields
    const titleText = product.name || '';
    const descriptionText = stripHtmlTags(
      product.description || product.short_description || ''
    );
    const categoryText = product.categories?.map(cat => cat.name).join(' ') || '';
    const benefitsText = healthBenefits?.join(' ') || extractBenefitsFromDescription(descriptionText);

    // Generate embeddings for each field
    const [titleEmb, descEmb, catEmb, benEmb] = await Promise.all([
      generateEnhancedEmbedding(titleText),
      generateEnhancedEmbedding(descriptionText),
      generateEnhancedEmbedding(categoryText),
      generateEnhancedEmbedding(benefitsText),
    ]);

    // Create weighted combination
    const combinedEmb = combineEmbeddings(
      [titleEmb, descEmb, catEmb, benEmb],
      [DEFAULT_WEIGHTS.title, DEFAULT_WEIGHTS.description, DEFAULT_WEIGHTS.categories, DEFAULT_WEIGHTS.benefits]
    );

    return {
      productId: product.id,
      titleEmbedding: titleEmb,
      descriptionEmbedding: descEmb,
      categoryEmbedding: catEmb,
      benefitsEmbedding: benEmb,
      combinedEmbedding: combinedEmb,
      metadata: {
        productSlug: product.slug,
        timestamp: new Date().toISOString(),
        embeddingVersion: 'mpnet-v2-multi',
      },
    };
  } catch (error) {
    logger.error(`Failed to generate multi-vector embedding for product ${product.id}:`, error as Record<string, unknown>);
    throw error;
  }
}

/**
 * Combine multiple embeddings with weights
 */
export function combineEmbeddings(
  embeddings: number[][],
  weights: number[]
): number[] {
  if (embeddings.length === 0) {
    throw new Error('No embeddings to combine');
  }

  if (embeddings.length !== weights.length) {
    throw new Error('Number of embeddings and weights must match');
  }

  // Normalize weights to sum to 1
  const totalWeight = weights.reduce((sum, w) => sum + w, 0);
  const normalizedWeights = weights.map(w => w / totalWeight);

  // Initialize combined embedding with zeros
  const dimensions = embeddings[0]?.length || 768;
  const combined = new Array(dimensions).fill(0);

  // Weighted sum of embeddings
  for (let i = 0; i < embeddings.length; i++) {
    const embedding = embeddings[i];
    const weight = normalizedWeights[i];

    if (!embedding || !weight) continue;

    for (let j = 0; j < dimensions; j++) {
      combined[j] += (embedding[j] || 0) * weight;
    }
  }

  // Normalize the combined embedding
  const norm = Math.sqrt(combined.reduce((sum, val) => sum + val * val, 0));
  if (norm > 0) {
    for (let i = 0; i < dimensions; i++) {
      combined[i] /= norm;
    }
  }

  return combined;
}

/**
 * Calculate similarity between multi-vector embeddings with field-specific weights
 */
export function calculateMultiVectorSimilarity(
  embedding1: MultiVectorEmbedding,
  embedding2: MultiVectorEmbedding,
  options: MultiVectorSearchOptions = {}
): number {
  let weights = {
    title: DEFAULT_WEIGHTS.title,
    description: DEFAULT_WEIGHTS.description,
    categories: DEFAULT_WEIGHTS.categories,
    benefits: DEFAULT_WEIGHTS.benefits,
    ...(options.weights || {})
  };

  // Use dynamic weights if requested and intent is provided
  if (options.dynamicWeights && options.intent) {
    const dynamicWeights = calculateDynamicWeights('', options.intent);
    if (dynamicWeights) {
      weights = {
        title: dynamicWeights.title ?? weights.title,
        description: dynamicWeights.description ?? weights.description,
        categories: dynamicWeights.categories ?? weights.categories,
        benefits: dynamicWeights.benefits ?? weights.benefits
      };
    }
  }

  // Calculate similarities for each field
  const titleSim = cosineSimilarity(embedding1.titleEmbedding, embedding2.titleEmbedding);
  const descSim = cosineSimilarity(embedding1.descriptionEmbedding, embedding2.descriptionEmbedding);
  const catSim = cosineSimilarity(embedding1.categoryEmbedding, embedding2.categoryEmbedding);
  const benSim = cosineSimilarity(embedding1.benefitsEmbedding, embedding2.benefitsEmbedding);

  // Weighted combination of similarities
  const totalWeight = weights.title + weights.description + weights.categories + weights.benefits;

  return (
    (titleSim * weights.title +
     descSim * weights.description +
     catSim * weights.categories +
     benSim * weights.benefits) / totalWeight
  );
}

/**
 * Calculate cosine similarity between two vectors
 */
export function cosineSimilarity(vec1: number[], vec2: number[]): number {
  if (vec1.length !== vec2.length) {
    throw new Error('Vectors must have the same dimensions');
  }

  let dotProduct = 0;
  let norm1 = 0;
  let norm2 = 0;

  for (let i = 0; i < vec1.length; i++) {
    const v1 = vec1[i] || 0;
    const v2 = vec2[i] || 0;

    dotProduct += v1 * v2;
    norm1 += v1 * v1;
    norm2 += v2 * v2;
  }

  const denominator = Math.sqrt(norm1) * Math.sqrt(norm2);

  return denominator === 0 ? 0 : dotProduct / denominator;
}

/**
 * Search using multi-vector embeddings
 */
export async function searchWithMultiVectors(
  queryEmbedding: MultiVectorEmbedding,
  candidateEmbeddings: MultiVectorEmbedding[],
  options: MultiVectorSearchOptions = {}
): Promise<Array<{ embedding: MultiVectorEmbedding; score: number }>> {
  const results = candidateEmbeddings.map(candidate => ({
    embedding: candidate,
    score: calculateMultiVectorSimilarity(queryEmbedding, candidate, options),
  }));

  // Sort by score in descending order
  results.sort((a, b) => b.score - a.score);

  return results;
}

/**
 * Generate multi-vector embedding for a search query
 */
export async function generateQueryMultiVectorEmbedding(
  query: string,
  context?: {
    category?: string;
    benefits?: string[];
  }
): Promise<Partial<MultiVectorEmbedding>> {
  try {
    // Generate embedding for the main query
    const queryEmbedding = await generateEnhancedEmbedding(query);

    // Generate embeddings for context if provided
    const categoryEmbedding = context?.category
      ? await generateEnhancedEmbedding(context.category)
      : queryEmbedding;

    const benefitsEmbedding = context?.benefits?.length
      ? await generateEnhancedEmbedding(context.benefits.join(' '))
      : queryEmbedding;

    return {
      titleEmbedding: queryEmbedding,
      descriptionEmbedding: queryEmbedding,
      categoryEmbedding,
      benefitsEmbedding,
      combinedEmbedding: queryEmbedding,
    };
  } catch (error) {
    logger.error('Failed to generate query multi-vector embedding:', error as Record<string, unknown>);
    throw error;
  }
}

// Helper functions

function stripHtmlTags(html: string): string {
  return html.replace(/<[^>]*>/g, '').replace(/&[^;]+;/g, ' ').trim();
}

function extractBenefitsFromDescription(description: string): string {
  // Simple extraction of health benefits keywords
  const benefitKeywords = [
    'anti-inflammatory', 'antioxidant', 'immune', 'digestive',
    'heart health', 'brain health', 'energy', 'vitamin',
    'mineral', 'protein', 'fiber', 'omega-3',
  ];

  const foundBenefits = benefitKeywords.filter(keyword =>
    description.toLowerCase().includes(keyword)
  );

  return foundBenefits.join(' ');
}

/**
 * Batch generate multi-vector embeddings for products
 */
export async function batchGenerateMultiVectorEmbeddings(
  products: WCProduct[],
  batchSize: number = 5
): Promise<MultiVectorEmbedding[]> {
  const embeddings: MultiVectorEmbedding[] = [];

  for (let i = 0; i < products.length; i += batchSize) {
    const batch = products.slice(i, i + batchSize);

    logger.info(`Processing batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(products.length / batchSize)}`);

    const batchEmbeddings = await Promise.all(
      batch.map(product => generateMultiVectorEmbedding(product))
    );

    embeddings.push(...batchEmbeddings);
  }

  return embeddings;
}

// Performance optimization utilities

/**
 * Debounce function to limit the rate at which a function can fire
 */
export function debounce<T extends (...args: unknown[]) => unknown>(
  func: T,
  wait: number,
  immediate: boolean = false
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout | null = null;

  return function executedFunction(...args: Parameters<T>) {
    const later = () => {
      timeout = null;
      if (!immediate) func(...args);
    };

    const callNow = immediate && !timeout;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(later, wait);

    if (callNow) func(...args);
  };
}

/**
 * Throttle function to ensure a function is called at most once per specified time period
 */
export function throttle<T extends (...args: unknown[]) => unknown>(
  func: T,
  limit: number
): (...args: Parameters<T>) => void {
  let inThrottle: boolean = false;
  let lastFunc: NodeJS.Timeout | null = null;
  let lastRan: number | null = null;

  return function executedFunction(...args: Parameters<T>) {
    if (!inThrottle) {
      func(...args);
      lastRan = Date.now();
      inThrottle = true;
    } else {
      if (lastFunc) clearTimeout(lastFunc);
      lastFunc = setTimeout(() => {
        if (Date.now() - (lastRan || 0) >= limit) {
          func(...args);
          lastRan = Date.now();
        }
      }, limit - (Date.now() - (lastRan || 0)));
    }
  };
}

/**
 * Request Animation Frame throttle for smooth animations
 */
export function rafThrottle<T extends (...args: unknown[]) => unknown>(
  func: T
): (...args: Parameters<T>) => void {
  let rafId: number | null = null;

  return function executedFunction(...args: Parameters<T>) {
    if (rafId) cancelAnimationFrame(rafId);

    rafId = requestAnimationFrame(() => {
      func(...args);
      rafId = null;
    });
  };
}

/**
 * Memoization helper for expensive computations
 */
export function memoize<T extends (...args: unknown[]) => unknown>(
  func: T,
  maxCacheSize: number = 100
): T {
  const cache = new Map<string, ReturnType<T>>();

  return ((...args: Parameters<T>) => {
    const key = JSON.stringify(args);

    if (cache.has(key)) {
      return cache.get(key) as ReturnType<T>;
    }

    const result = func(...args) as ReturnType<T>;

    // Implement LRU cache
    if (cache.size >= maxCacheSize) {
      const firstKey = cache.keys().next().value;
      if (firstKey !== undefined) {
        cache.delete(firstKey);
      }
    }

    cache.set(key, result);
    return result;
  }) as T;
}

/**
 * Lazy load images with Intersection Observer
 */
export function lazyLoadImages(selector: string = 'img[data-src]'): () => void {
  const images = document.querySelectorAll<HTMLImageElement>(selector);

  if ('IntersectionObserver' in window) {
    const imageObserver = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const img = entry.target as HTMLImageElement;
          const src = img.dataset.src;

          if (src) {
            img.src = src;
            img.removeAttribute('data-src');
            imageObserver.unobserve(img);
          }
        }
      });
    }, {
      rootMargin: '50px 0px',
      threshold: 0.01
    });

    images.forEach(img => imageObserver.observe(img));

    // Return cleanup function
    return () => {
      images.forEach(img => imageObserver.unobserve(img));
    };
  } else {
    // Fallback for browsers without Intersection Observer
    images.forEach(img => {
      const src = img.dataset.src;
      if (src) {
        img.src = src;
        img.removeAttribute('data-src');
      }
    });

    return () => {};
  }
}

/**
 * Virtual scrolling helper for large lists
 */
export interface VirtualScrollOptions {
  itemHeight: number;
  containerHeight: number;
  totalItems: number;
  overscan?: number;
}

export function calculateVirtualScroll(
  scrollTop: number,
  options: VirtualScrollOptions
) {
  const {
    itemHeight,
    containerHeight,
    totalItems,
    overscan = 3
  } = options;

  const startIndex = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
  const endIndex = Math.min(
    totalItems - 1,
    Math.ceil((scrollTop + containerHeight) / itemHeight) + overscan
  );

  return {
    startIndex,
    endIndex,
    offsetY: startIndex * itemHeight,
    visibleItems: endIndex - startIndex + 1
  };
}

/**
 * Batch DOM updates using requestAnimationFrame
 */
export class DOMBatcher {
  private reads: (() => void)[] = [];
  private writes: (() => void)[] = [];
  private scheduled = false;

  read(fn: () => void): void {
    this.reads.push(fn);
    this.scheduleFlush();
  }

  write(fn: () => void): void {
    this.writes.push(fn);
    this.scheduleFlush();
  }

  private scheduleFlush(): void {
    if (!this.scheduled) {
      this.scheduled = true;
      requestAnimationFrame(() => this.flush());
    }
  }

  private flush(): void {
    const reads = [...this.reads];
    const writes = [...this.writes];

    this.reads = [];
    this.writes = [];
    this.scheduled = false;

    // Execute all reads first
    reads.forEach(read => read());

    // Then execute all writes
    writes.forEach(write => write());
  }
}

/**
 * Web Worker pool for CPU-intensive tasks
 */
export class WorkerPool {
  private workers: Worker[] = [];
  private queue: Array<{
    data: unknown;
    resolve: (value: unknown) => void;
    reject: (reason?: unknown) => void;
  }> = [];
  private busyWorkers = new Set<Worker>();

  constructor(
    workerScript: string,
    poolSize: number = navigator.hardwareConcurrency || 4
  ) {
    for (let i = 0; i < poolSize; i++) {
      const worker = new Worker(workerScript);
      this.workers.push(worker);
    }
  }

  async execute(data: unknown): Promise<unknown> {
    return new Promise((resolve, reject) => {
      const availableWorker = this.workers.find(w => !this.busyWorkers.has(w));

      if (availableWorker) {
        this.runWorker(availableWorker, data, resolve, reject);
      } else {
        this.queue.push({ data, resolve, reject });
      }
    });
  }

  private runWorker(
    worker: Worker,
    data: unknown,
    resolve: (value: unknown) => void,
    reject: (reason?: unknown) => void
  ): void {
    this.busyWorkers.add(worker);

    const handleMessage = (e: MessageEvent) => {
      worker.removeEventListener('message', handleMessage);
      worker.removeEventListener('error', handleError);
      this.busyWorkers.delete(worker);

      resolve(e.data);

      // Process queue
      if (this.queue.length > 0) {
        const next = this.queue.shift();
        if (next) {
          this.runWorker(worker, next.data, next.resolve, next.reject);
        }
      }
    };

    const handleError = (e: ErrorEvent) => {
      worker.removeEventListener('message', handleMessage);
      worker.removeEventListener('error', handleError);
      this.busyWorkers.delete(worker);

      reject(e);

      // Process queue
      if (this.queue.length > 0) {
        const next = this.queue.shift();
        if (next) {
          this.runWorker(worker, next.data, next.resolve, next.reject);
        }
      }
    };

    worker.addEventListener('message', handleMessage);
    worker.addEventListener('error', handleError);
    worker.postMessage(data);
  }

  terminate(): void {
    this.workers.forEach(worker => worker.terminate());
    this.workers = [];
    this.queue = [];
    this.busyWorkers.clear();
  }
}

/**
 * Memory-efficient object pool for frequent allocations
 */
export class ObjectPool<T> {
  private pool: T[] = [];
  private createFn: () => T;
  private resetFn: (obj: T) => void;
  private maxSize: number;

  constructor(
    createFn: () => T,
    resetFn: (obj: T) => void = () => {},
    maxSize: number = 100
  ) {
    this.createFn = createFn;
    this.resetFn = resetFn;
    this.maxSize = maxSize;
  }

  acquire(): T {
    if (this.pool.length > 0) {
      const obj = this.pool.pop();
      if (obj) return obj;
    }
    return this.createFn();
  }

  release(obj: T): void {
    if (this.pool.length < this.maxSize) {
      this.resetFn(obj);
      this.pool.push(obj);
    }
  }

  clear(): void {
    this.pool = [];
  }

  get size(): number {
    return this.pool.length;
  }
}

/**
 * Performance monitoring utilities
 */
export class PerformanceMonitor {
  private marks = new Map<string, number>();
  private measures = new Map<string, number[]>();

  mark(name: string): void {
    this.marks.set(name, performance.now());
  }

  measure(name: string, startMark: string, endMark?: string): number {
    const start = this.marks.get(startMark);
    const end = endMark ? this.marks.get(endMark) : performance.now();

    if (!start) {
      throw new Error(`Start mark "${startMark}" not found`);
    }

    if (endMark && !this.marks.has(endMark)) {
      throw new Error(`End mark "${endMark}" not found`);
    }

    const duration = (end || performance.now()) - start;

    if (!this.measures.has(name)) {
      this.measures.set(name, []);
    }

    const measures = this.measures.get(name);
    if (measures) measures.push(duration);

    return duration;
  }

  getStats(name: string): {
    count: number;
    min: number;
    max: number;
    mean: number;
    median: number;
  } | null {
    const measures = this.measures.get(name);

    if (!measures || measures.length === 0) {
      return null;
    }

    const sorted = [...measures].sort((a, b) => a - b);
    const sum = sorted.reduce((acc, val) => acc + val, 0);

    return {
      count: sorted.length,
      min: sorted[0] ?? 0,
      max: sorted[sorted.length - 1] ?? 0,
      mean: sum / sorted.length,
      median: sorted[Math.floor(sorted.length / 2)] ?? 0
    };
  }

  clear(): void {
    this.marks.clear();
    this.measures.clear();
  }
}

// Export singleton instance
export const performanceMonitor = new PerformanceMonitor();
export const domBatcher = new DOMBatcher();

// Performance optimization utilities and Core Web Vitals monitoring
import { performanceEvent } from '@/lib/gtag';

import { logger } from '@/lib/logger';

// Performance entry types
interface LCPEntry extends PerformanceEntry {
  startTime: number;
}

interface FIDEntry extends PerformanceEntry {
  processingStart: number;
  startTime: number;
}

interface CLSEntry extends PerformanceEntry {
  hadRecentInput: boolean;
  value: number;
}

interface NavigationEntry extends PerformanceEntry {
  responseStart: number;
  requestStart: number;
  loadEventEnd: number;
  navigationStart: number;
}

// Core Web Vitals thresholds (based on Google recommendations)
export const CORE_WEB_VITALS_THRESHOLDS = {
  LCP: { good: 2500, needsImprovement: 4000 }, // Largest Contentful Paint (ms)
  FID: { good: 100, needsImprovement: 300 },   // First Input Delay (ms)
  CLS: { good: 0.1, needsImprovement: 0.25 }   // Cumulative Layout Shift
};

// Performance metrics interface
export interface PerformanceMetrics {
  lcp?: number;
  fid?: number;
  cls?: number;
  fcp?: number; // First Contentful Paint
  ttfb?: number; // Time to First Byte
  pageLoadTime?: number;
}

// Enhanced Core Web Vitals tracking with optimization hints
class PerformanceOptimizer {
  private metrics: PerformanceMetrics = {};
  private observers: PerformanceObserver[] = [];

  constructor() {
    if (typeof window !== 'undefined') {
      this.initializeObservers();
    }
  }

  // Initialize all performance observers
  private initializeObservers() {
    this.observeLCP();
    this.observeFID();
    this.observeCLS();
    this.observeFCP();
    this.observeNavigation();
  }

  // Largest Contentful Paint observer
  private observeLCP() {
    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        const lastEntry = entries[entries.length - 1] as LCPEntry;

        if (lastEntry) {
          this.metrics.lcp = lastEntry.startTime;
          this.trackMetric('lcp', lastEntry.startTime, CORE_WEB_VITALS_THRESHOLDS.LCP);

          // LCP optimization suggestions
          if (lastEntry.startTime > CORE_WEB_VITALS_THRESHOLDS.LCP.needsImprovement) {
            logger.warn('🚨 LCP Performance Issue:', {
              value: lastEntry.startTime,
              threshold: CORE_WEB_VITALS_THRESHOLDS.LCP.good,
              suggestions: [
                'Optimize largest image on page',
                'Remove unused JavaScript',
                'Implement resource preloading',
                'Upgrade server response time'
              ]
            });
          }
        }
      });

      observer.observe({ entryTypes: ['largest-contentful-paint'] });
      this.observers.push(observer);
    } catch {
      logger.warn('LCP observer not supported');
    }
  }

  // First Input Delay observer
  private observeFID() {
    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();

        entries.forEach((entry) => {
          const fidEntry = entry as FIDEntry;
          if (fidEntry.processingStart && fidEntry.startTime) {
            const fid = fidEntry.processingStart - fidEntry.startTime;
            this.metrics.fid = fid;
            this.trackMetric('fid', fid, CORE_WEB_VITALS_THRESHOLDS.FID);

            // FID optimization suggestions
            if (fid > CORE_WEB_VITALS_THRESHOLDS.FID.needsImprovement) {
              logger.warn('🚨 FID Performance Issue:', {
                value: fid,
                threshold: CORE_WEB_VITALS_THRESHOLDS.FID.good,
                suggestions: [
                  'Break up long tasks',
                  'Optimize third-party code',
                  'Use a web worker',
                  'Reduce JavaScript execution time'
                ]
              });
            }
          }
        });
      });

      observer.observe({ entryTypes: ['first-input'] });
      this.observers.push(observer);
    } catch {
      logger.warn('FID observer not supported');
    }
  }

  // Cumulative Layout Shift observer
  private observeCLS() {
    try {
      let clsValue = 0;
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();

        entries.forEach((entry) => {
          const clsEntry = entry as CLSEntry;
          if (clsEntry.hadRecentInput === false && clsEntry.value) {
            clsValue += clsEntry.value;
          }
        });

        if (clsValue > 0) {
          this.metrics.cls = clsValue;
          this.trackMetric('cls', clsValue, CORE_WEB_VITALS_THRESHOLDS.CLS);

          // CLS optimization suggestions
          if (clsValue > CORE_WEB_VITALS_THRESHOLDS.CLS.needsImprovement) {
            logger.warn('🚨 CLS Performance Issue:', {
              value: clsValue,
              threshold: CORE_WEB_VITALS_THRESHOLDS.CLS.good,
              suggestions: [
                'Set size attributes on images and videos',
                'Reserve space for dynamic content',
                'Avoid inserting content above existing content',
                'Use transform animations instead of changing layout properties'
              ]
            });
          }
        }
      });

      observer.observe({ entryTypes: ['layout-shift'] });
      this.observers.push(observer);
    } catch {
      logger.warn('CLS observer not supported');
    }
  }

  // First Contentful Paint observer
  private observeFCP() {
    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        const fcpEntry = entries.find(entry => entry.name === 'first-contentful-paint');

        if (fcpEntry) {
          this.metrics.fcp = fcpEntry.startTime;
          this.trackMetric('fcp', fcpEntry.startTime, { good: 1800, needsImprovement: 3000 });
        }
      });

      observer.observe({ entryTypes: ['paint'] });
      this.observers.push(observer);
    } catch {
      logger.warn('FCP observer not supported');
    }
  }

  // Navigation timing observer
  private observeNavigation() {
    try {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();

        entries.forEach((entry) => {
          const navEntry = entry as NavigationEntry;
          if (navEntry.responseStart && navEntry.requestStart) {
            this.metrics.ttfb = navEntry.responseStart - navEntry.requestStart;
            this.trackMetric('ttfb', this.metrics.ttfb, { good: 800, needsImprovement: 1800 });
          }

          if (navEntry.loadEventEnd && navEntry.navigationStart) {
            this.metrics.pageLoadTime = navEntry.loadEventEnd - navEntry.navigationStart;
            this.trackMetric('page_load', this.metrics.pageLoadTime, { good: 3000, needsImprovement: 5000 });
          }
        });
      });

      observer.observe({ entryTypes: ['navigation'] });
      this.observers.push(observer);
    } catch {
      logger.warn('Navigation observer not supported');
    }
  }

  // Track performance metric with scoring
  private trackMetric(name: string, value: number, thresholds: { good: number; needsImprovement: number }) {
    const score = value <= thresholds.good ? 'good' : 
                  value <= thresholds.needsImprovement ? 'needs_improvement' : 'poor';

    performanceEvent.pageLoad(window.location.pathname, value, this.metrics);

    // Track individual metric
    if (typeof window !== 'undefined' && window.gtag) {
      window.gtag('event', `performance_${name}`, {
        value: Math.round(value),
        score: score,
        page_path: window.location.pathname
      });
    }
  }

  // Get current performance metrics
  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  // Generate performance report
  generateReport(): PerformanceReport {
    const scores = {
      lcp: this.metrics.lcp ? this.getScore(this.metrics.lcp, CORE_WEB_VITALS_THRESHOLDS.LCP) : null,
      fid: this.metrics.fid ? this.getScore(this.metrics.fid, CORE_WEB_VITALS_THRESHOLDS.FID) : null,
      cls: this.metrics.cls ? this.getScore(this.metrics.cls, CORE_WEB_VITALS_THRESHOLDS.CLS) : null
    };

    const overallScore = this.calculateOverallScore(scores);

    return {
      metrics: this.metrics,
      scores,
      overallScore,
      recommendations: this.generateRecommendations(scores),
      timestamp: Date.now()
    };
  }

  // Get performance score for a metric
  private getScore(value: number, thresholds: { good: number; needsImprovement: number }): 'good' | 'needs_improvement' | 'poor' {
    return value <= thresholds.good ? 'good' : 
           value <= thresholds.needsImprovement ? 'needs_improvement' : 'poor';
  }

  // Calculate overall performance score
  private calculateOverallScore(scores: Record<string, string | null>): number {
    const validScores = Object.values(scores).filter(score => score !== null);
    if (validScores.length === 0) return 0;

    const scoreValues = validScores.map(score => {
      switch (score) {
        case 'good': return 100;
        case 'needs_improvement': return 50;
        case 'poor': return 0;
        default: return 0;
      }
    });

    return Math.round(scoreValues.reduce((sum: number, score) => sum + score, 0) / validScores.length);
  }

  // Generate performance recommendations
  private generateRecommendations(scores: Record<string, string | null>): string[] {
    const recommendations: string[] = [];

    if (scores.lcp === 'needs_improvement' || scores.lcp === 'poor') {
      recommendations.push(
        'Optimize Largest Contentful Paint: Compress images, remove unused JavaScript, implement preloading'
      );
    }

    if (scores.fid === 'needs_improvement' || scores.fid === 'poor') {
      recommendations.push(
        'Improve First Input Delay: Break up long tasks, optimize third-party scripts, consider web workers'
      );
    }

    if (scores.cls === 'needs_improvement' || scores.cls === 'poor') {
      recommendations.push(
        'Reduce Cumulative Layout Shift: Set image dimensions, reserve space for dynamic content, use transform animations'
      );
    }

    if (recommendations.length === 0) {
      recommendations.push('Great job! Your Core Web Vitals are performing well.');
    }

    return recommendations;
  }

  // Clean up observers
  cleanup() {
    this.observers.forEach(observer => observer.disconnect());
    this.observers = [];
  }
}

// Performance report interface
export interface PerformanceReport {
  metrics: PerformanceMetrics;
  scores: Record<string, string | null>;
  overallScore: number;
  recommendations: string[];
  timestamp: number;
}

// Advanced performance monitoring and optimization
export class PerformanceMonitor {
  private static instance: PerformanceMonitor;
  private metrics: Map<string, PerformanceMetrics> = new Map();
  private budgets: PerformanceBudgets = {
    lcp: 2500,
    fid: 100,
    cls: 0.1,
    bundleSize: 250000, // 250KB
    imageSize: 150000   // 150KB
  };

  static getInstance(): PerformanceMonitor {
    if (!PerformanceMonitor.instance) {
      PerformanceMonitor.instance = new PerformanceMonitor();
    }
    return PerformanceMonitor.instance;
  }

  // Store page-specific metrics
  storePageMetrics(page: string, metrics: PerformanceMetrics) {
    this.metrics.set(page, metrics);
    this.checkPerformanceBudgets(page, metrics);
  }

  // Get aggregated metrics across all pages
  getAggregatedMetrics(): AggregatedMetrics {
    const allMetrics = Array.from(this.metrics.values());
    if (allMetrics.length === 0) return this.getEmptyAggregatedMetrics();

    return {
      avgLCP: this.calculateAverage(allMetrics, 'lcp'),
      avgFID: this.calculateAverage(allMetrics, 'fid'),
      avgCLS: this.calculateAverage(allMetrics, 'cls'),
      avgTTFB: this.calculateAverage(allMetrics, 'ttfb'),
      avgPageLoad: this.calculateAverage(allMetrics, 'pageLoadTime'),
      pageCount: allMetrics.length,
      performanceScore: this.calculateAggregatedScore(allMetrics)
    };
  }

  // Check if metrics exceed performance budgets
  private checkPerformanceBudgets(page: string, metrics: PerformanceMetrics) {
    const violations: string[] = [];

    if (metrics.lcp && metrics.lcp > this.budgets.lcp) {
      violations.push(`LCP budget exceeded: ${metrics.lcp}ms > ${this.budgets.lcp}ms`);
    }
    if (metrics.fid && metrics.fid > this.budgets.fid) {
      violations.push(`FID budget exceeded: ${metrics.fid}ms > ${this.budgets.fid}ms`);
    }
    if (metrics.cls && metrics.cls > this.budgets.cls) {
      violations.push(`CLS budget exceeded: ${metrics.cls} > ${this.budgets.cls}`);
    }

    if (violations.length > 0) {
      logger.warn(`🚨 Performance Budget Violations on ${page}:`, { violations });

      // Send budget violation to analytics
      if (typeof window !== 'undefined' && window.gtag) {
        window.gtag('event', 'performance_budget_violation', {
          page_path: page,
          violations: violations.length,
          custom_parameter: violations.join(', ')
        });
      }
    }
  }

  private calculateAverage(metrics: PerformanceMetrics[], key: keyof PerformanceMetrics): number {
    const values = metrics.map(m => m[key]).filter(v => v !== undefined && typeof v === 'number');
    return values.length > 0 ? values.reduce((sum: number, val) => sum + val, 0) / values.length : 0;
  }

  private calculateAggregatedScore(metrics: PerformanceMetrics[]): number {
    const scores = metrics.map(m => {
      const lcpScore = m.lcp ? (m.lcp <= 2500 ? 100 : m.lcp <= 4000 ? 50 : 0) : 100;
      const fidScore = m.fid ? (m.fid <= 100 ? 100 : m.fid <= 300 ? 50 : 0) : 100;
      const clsScore = m.cls ? (m.cls <= 0.1 ? 100 : m.cls <= 0.25 ? 50 : 0) : 100;
      return (lcpScore + fidScore + clsScore) / 3;
    });
    return scores.length > 0 ? scores.reduce((sum: number, score) => sum + score, 0) / scores.length : 0;
  }

  private getEmptyAggregatedMetrics(): AggregatedMetrics {
    return { avgLCP: 0, avgFID: 0, avgCLS: 0, avgTTFB: 0, avgPageLoad: 0, pageCount: 0, performanceScore: 0 };
  }
}

interface PerformanceBudgets {
  lcp: number;
  fid: number;
  cls: number;
  bundleSize: number;
  imageSize: number;
}

interface AggregatedMetrics {
  avgLCP: number;
  avgFID: number;
  avgCLS: number;
  avgTTFB: number;
  avgPageLoad: number;
  pageCount: number;
  performanceScore: number;
}

// Bundle analyzer utility
export const BundleAnalyzer = {
  // Analyze bundle size and suggest optimizations
  async analyzeBundleSize(): Promise<BundleAnalysis> {
    try {
      const performance = window.performance;
      const _navigationEntries = performance.getEntriesByType('navigation') as PerformanceNavigationTiming[];
      void _navigationEntries; // Preserved for future navigation timing analysis
      const resourceEntries = performance.getEntriesByType('resource') as PerformanceResourceTiming[];

      const jsResources = resourceEntries.filter(entry => 
        entry.name.includes('.js') || entry.name.includes('/_next/static/chunks/')
      );

      const cssResources = resourceEntries.filter(entry => 
        entry.name.includes('.css') || entry.name.includes('/_next/static/css/')
      );

      const totalJSSize = jsResources.reduce((sum, entry) => sum + (entry.transferSize || 0), 0);
      const totalCSSSize = cssResources.reduce((sum, entry) => sum + (entry.transferSize || 0), 0);

      return {
        totalJSSize,
        totalCSSSize,
        totalBundleSize: totalJSSize + totalCSSSize,
        jsFileCount: jsResources.length,
        cssFileCount: cssResources.length,
        largestJSFile: this.findLargestResource(jsResources),
        suggestions: this.generateBundleSuggestions(totalJSSize + totalCSSSize),
        timestamp: Date.now()
      };
    } catch (error) {
      logger.error('Bundle analysis failed:', { 
        error: error instanceof Error ? error.message : String(error),
        stack: error instanceof Error ? error.stack : undefined
      });
      return this.getEmptyBundleAnalysis();
    }
  },

  findLargestResource(resources: PerformanceResourceTiming[]): { name: string; size: number } | null {
    if (resources.length === 0) return null;

    const largest = resources.reduce((max, current) => 
      (current.transferSize ?? 0) > (max.transferSize ?? 0) ? current : max
    );

    return {
      name: largest.name.split('/').pop() ?? largest.name,
      size: largest.transferSize ?? 0
    };
  },

  generateBundleSuggestions(totalSize: number): string[] {
    const suggestions: string[] = [];

    if (totalSize > 250000) { // 250KB
      suggestions.push('Bundle size exceeds 250KB - consider code splitting');
    }
    if (totalSize > 500000) { // 500KB
      suggestions.push('Bundle size is very large - implement dynamic imports');
      suggestions.push('Use tree shaking to remove unused code');
    }
    if (totalSize > 1000000) { // 1MB
      suggestions.push('Critical bundle size issue - immediate optimization required');
      suggestions.push('Consider lazy loading non-critical components');
    }

    if (suggestions.length === 0) {
      suggestions.push('Bundle size is within acceptable limits');
    }

    return suggestions;
  },

  getEmptyBundleAnalysis(): BundleAnalysis {
    return {
      totalJSSize: 0,
      totalCSSSize: 0,
      totalBundleSize: 0,
      jsFileCount: 0,
      cssFileCount: 0,
      largestJSFile: null,
      suggestions: ['Unable to analyze bundle'],
      timestamp: Date.now()
    };
  }
};

export interface BundleAnalysis {
  totalJSSize: number;
  totalCSSSize: number;
  totalBundleSize: number;
  jsFileCount: number;
  cssFileCount: number;
  largestJSFile: { name: string; size: number } | null;
  suggestions: string[];
  timestamp: number;
}

// Global performance optimizer instance
export const performanceOptimizer = new PerformanceOptimizer();
export const performanceMonitor = PerformanceMonitor.getInstance();

// Resource preloading utilities
export const ResourcePreloader = {
  // Preload critical resources based on current page
  preloadCriticalResources() {
    const currentPath = typeof window !== 'undefined' ? window.location.pathname : '';

    // Clean up any legacy preload links first - run immediately and repeatedly
    this.cleanupLegacyPreloads();

    // Set up periodic cleanup to remove any legacy preloads that might get added
    if (typeof window !== 'undefined') {
      setTimeout(() => this.cleanupLegacyPreloads(), 1000);
      setTimeout(() => this.cleanupLegacyPreloads(), 3000);
    }

    // Always preload critical fonts
    const fontResource = {
      href: 'https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Crimson+Pro:wght@400;600&display=swap',
      as: 'style'
    };

    // Only preload images if they're likely to be used on the current page
    const criticalResources = [fontResource];

    // Add page-specific image preloads based on actual usage
    if (currentPath === '/' || currentPath === '/about') {
      // Main hero image used on home and about pages
      criticalResources.push({ href: '/images/hero.png', as: 'image' });
    }

    if (currentPath === '/') {
      // Featured product images on home page
      criticalResources.push({ href: '/images/agriko-turmeric-5in1-blend-500g-health-supplement.jpg', as: 'image' });
    }

    if (currentPath === '/products') {
      // Products page specific images
      criticalResources.push({ href: '/images/organic-rice-varieties.jpg', as: 'image' });
    }

    // Debug logging to track what's being preloaded
    if (process.env.NODE_ENV === 'development') {
      logger.debug('🔄 Preloading resources for path:', { currentPath, resources: criticalResources.map(r => r.href) });
    }

    criticalResources.forEach(resource => {
      // Check if resource is already preloaded to avoid duplicates
      const existingLink = document.head.querySelector(`link[rel="preload"][href="${resource.href}"]`);
      if (existingLink) {
        if (process.env.NODE_ENV === 'development') {
          logger.debug('⏭️ Skipping duplicate preload:', { href: resource.href });
        }
        return;
      }

      // Explicitly prevent preloading of unused legacy images
      if (resource.href.includes('hero-organic-farm.jpg') || resource.href.includes('products-hero.jpg')) {
        console.warn('🚫 Blocked legacy image preload:', resource.href);
        return;
      }

      const link = document.createElement('link');
      link.rel = 'preload';
      link.href = resource.href;
      link.as = resource.as;
      if (resource.as === 'style') {
        link.onload = () => {
          link.rel = 'stylesheet';
        };
      }

      if (process.env.NODE_ENV === 'development') {
        logger.debug('✅ Preloading:', { href: resource.href });
      }

      document.head.appendChild(link);
    });
  },

  // Clean up legacy preload links that shouldn't be there
  cleanupLegacyPreloads() {
    const legacyImages = ['hero-organic-farm.jpg', 'products-hero.jpg'];
    legacyImages.forEach(imageName => {
      // Check both head and body for preload links
      const allLinks = document.querySelectorAll(`link[rel="preload"][href*="${imageName}"]`);
      allLinks.forEach(link => {
        console.warn('🗑️ Removing legacy preload:', link.getAttribute('href'));
        link.remove();
      });
    });

    // Set up mutation observer to prevent future legacy preloads
    if (typeof window !== 'undefined' && !this._cleanupObserver) {
      this._cleanupObserver = new MutationObserver((mutations) => {
        mutations.forEach((mutation) => {
          mutation.addedNodes.forEach((node) => {
            if (node.nodeType === 1 && node.nodeName === 'LINK') {
              const link = node as HTMLLinkElement;
              if (link.rel === 'preload' &&
                  (link.href.includes('hero-organic-farm.jpg') || link.href.includes('products-hero.jpg'))) {
                console.warn('🚫 Blocked dynamic legacy preload:', link.href);
                link.remove();
              }
            }
          });
        });
      });

      this._cleanupObserver.observe(document.head, {
        childList: true,
        subtree: true
      });
    }
  },

  _cleanupObserver: null as MutationObserver | null,

  // Prefetch next page resources
  prefetchNextPageResources() {
    const prefetchUrls = [
      '/products',
      '/about',
      '/contact'
    ];

    prefetchUrls.forEach(url => {
      const link = document.createElement('link');
      link.rel = 'prefetch';
      link.href = url;
      document.head.appendChild(link);
    });
  },

  // Preconnect to external domains
  preconnectExternalDomains() {
    const domains = [
      'URL_CONSTANTS.COMPANY_BASE_URL',
      'https://www.googletagmanager.com',
      'https://fonts.googleapis.com',
      'https://fonts.gstatic.com'
    ];

    domains.forEach(domain => {
      const link = document.createElement('link');
      link.rel = 'preconnect';
      link.href = domain;
      link.crossOrigin = 'anonymous';
      document.head.appendChild(link);
    });
  }
};

// Image optimization utilities
export const ImageOptimizer = {
  // Create responsive image component props
  getOptimizedImageProps(src: string, alt: string, width?: number, height?: number) {
    return {
      src,
      alt,
      width,
      height,
      loading: 'lazy' as const,
      placeholder: 'blur' as const,
      blurDataURL: 'data:image/jpeg;base64,',
      style: width && height ? { aspectRatio: `${width}/${height}` } : undefined
    };
  },

  // Lazy load images with intersection observer
  lazyLoadImages() {
    if ('IntersectionObserver' in window) {
      const imageObserver = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const img = entry.target as HTMLImageElement;
            const src = img.dataset.src;
            if (src) {
              img.src = src;
              img.classList.remove('blur-sm');
              imageObserver.unobserve(img);
            }
          }
        });
      });

      Array.from(document.querySelectorAll('img[data-src]')).forEach(img => {
        imageObserver.observe(img);
      });
    }
  }
};

