import { logger } from '@/lib/logger';

// Secure price parsing with comprehensive validation
export interface PriceParsingResult {
  success: boolean;
  value: number;
  error?: string;
  originalInput?: string | number;
}

// Price constraints for security
const PRICE_CONSTRAINTS = {
  MIN_VALUE: 0,
  MAX_VALUE: 999999999, // 999 million max
  MAX_DECIMAL_PLACES: 4, // For crypto compatibility if needed
  MAX_STRING_LENGTH: 20, // Prevent extremely long strings
} as const;

/**
 * Safely parse and validate price inputs
 * Handles string/number inputs, validates ranges, prevents overflow
 */
export function parsePrice(input: string | number | null | undefined, context?: string): PriceParsingResult {
  const logContext = context ? ` in ${context}` : '';

  // Handle null/undefined
  if (input === null || input === undefined) {
    logger.warn(`Price parsing: null/undefined input${logContext}`);
    return {
      success: false,
      value: 0,
      error: 'Price input is null or undefined',
      originalInput: undefined
    };
  }

  // Handle numeric input
  if (typeof input === 'number') {
    if (!Number.isFinite(input)) {
      logger.warn(`Price parsing: non-finite number${logContext}`, { input });
      return {
        success: false,
        value: 0,
        error: 'Price is not a finite number',
        originalInput: input
      };
    }

    if (input < PRICE_CONSTRAINTS.MIN_VALUE) {
      logger.warn(`Price parsing: negative price${logContext}`, { input });
      return {
        success: false,
        value: 0,
        error: 'Price cannot be negative',
        originalInput: input
      };
    }

    if (input > PRICE_CONSTRAINTS.MAX_VALUE) {
      logger.warn(`Price parsing: price exceeds maximum${logContext}`, { input });
      return {
        success: false,
        value: PRICE_CONSTRAINTS.MAX_VALUE,
        error: 'Price exceeds maximum allowed value',
        originalInput: input
      };
    }

    return {
      success: true,
      value: Math.round(input * 10000) / 10000, // Round to 4 decimal places
      originalInput: input
    };
  }

  // Handle string input
  if (typeof input === 'string') {
    // Check string length to prevent DoS
    if (input.length > PRICE_CONSTRAINTS.MAX_STRING_LENGTH) {
      logger.warn(`Price parsing: string too long${logContext}`, { length: input.length });
      return {
        success: false,
        value: 0,
        error: 'Price string is too long',
        originalInput: input
      };
    }

    // Trim whitespace
    const trimmed = input.trim();
    if (trimmed === '') {
      logger.warn(`Price parsing: empty string${logContext}`);
      return {
        success: false,
        value: 0,
        error: 'Price string is empty',
        originalInput: input
      };
    }

    // Remove common currency symbols and commas
    let cleaned = trimmed
      .replace(/[$₱€£¥₨₩]/g, '') // Remove currency symbols
      .replace(/,/g, '') // Remove commas
      .trim();

    // Validate format (numbers, decimal point, optional minus sign)
    const pricePattern = /^-?\d+\.?\d*$/;
    if (!pricePattern.test(cleaned)) {
      logger.warn(`Price parsing: invalid format${logContext}`, { input, cleaned });
      return {
        success: false,
        value: 0,
        error: 'Price string has invalid format',
        originalInput: input
      };
    }

    // Count decimal places
    const decimalParts = cleaned.split('.');
    if (decimalParts.length > 2) {
      logger.warn(`Price parsing: multiple decimal points${logContext}`, { input });
      return {
        success: false,
        value: 0,
        error: 'Price has multiple decimal points',
        originalInput: input
      };
    }

    if (decimalParts.length === 2) {
      const decimalPart = decimalParts[1];
      if (decimalPart && decimalPart.length > PRICE_CONSTRAINTS.MAX_DECIMAL_PLACES) {
        logger.warn(`Price parsing: too many decimal places${logContext}`, { 
          input, 
          decimalPlaces: decimalPart.length
        });
        return {
          success: false,
          value: 0,
          error: `Price has too many decimal places (max ${PRICE_CONSTRAINTS.MAX_DECIMAL_PLACES})`,
          originalInput: input
        };
      }
    }

    // Parse the number
    const parsed = parseFloat(cleaned);

    // Re-validate the parsed result
    return parsePrice(parsed, context);
  }

  // Handle other types
  logger.warn(`Price parsing: unsupported type${logContext}`, { 
    input, 
    type: typeof input 
  });
  return {
    success: false,
    value: 0,
    error: `Unsupported price type: ${typeof input}`,
    originalInput: input
  };
}

/**
 * Calculate safe price multiplication with overflow protection
 */
export function safePriceMultiply(price: string | number, quantity: number, context?: string): PriceParsingResult {
  const priceResult = parsePrice(price, context);
  if (!priceResult.success) {
    return priceResult;
  }

  const quantityResult = parsePrice(quantity, `${context} quantity`);
  if (!quantityResult.success) {
    return quantityResult;
  }

  // Check for overflow before multiplication
  if (priceResult.value > 0 && quantityResult.value > PRICE_CONSTRAINTS.MAX_VALUE / priceResult.value) {
    logger.error(`Price multiplication overflow prevented`, {
      price: priceResult.value,
      quantity: quantityResult.value,
      context
    });
    return {
      success: false,
      value: PRICE_CONSTRAINTS.MAX_VALUE,
      error: 'Price calculation would cause overflow',
      originalInput: `${price} * ${quantity}`
    };
  }

  const result = priceResult.value * quantityResult.value;
  return {
    success: true,
    value: Math.round(result * 10000) / 10000, // Round to 4 decimal places
    originalInput: `${price} * ${quantity}`
  };
}

/**
 * Calculate discount percentage safely
 */
export function calculateDiscountPercentage(
  regularPrice: string | number, 
  salePrice: string | number,
  context?: string
): PriceParsingResult {
  const regularResult = parsePrice(regularPrice, `${context} regular price`);
  if (!regularResult.success) {
    return regularResult;
  }

  const saleResult = parsePrice(salePrice, `${context} sale price`);
  if (!saleResult.success) {
    return saleResult;
  }

  // Validate relationship
  if (saleResult.value > regularResult.value) {
    logger.warn(`Price calculation: sale price higher than regular${context ? ` in ${context}` : ''}`, {
      regular: regularResult.value,
      sale: saleResult.value
    });
    return {
      success: false,
      value: 0,
      error: 'Sale price is higher than regular price',
      originalInput: `${regularPrice} vs ${salePrice}`
    };
  }

  if (regularResult.value === 0) {
    return {
      success: false,
      value: 0,
      error: 'Cannot calculate discount: regular price is zero',
      originalInput: `${regularPrice} vs ${salePrice}`
    };
  }

  const discount = ((regularResult.value - saleResult.value) / regularResult.value) * 100;

  return {
    success: true,
    value: Math.round(discount * 100) / 100, // Round to 2 decimal places
    originalInput: `${regularPrice} vs ${salePrice}`
  };
}

/**
 * Validate a price object structure
 */
export function validatePrice(priceObj: { regular?: string; sale?: string }): boolean {
  if (!priceObj.regular) {
    return false;
  }

  const regularResult = parsePrice(priceObj.regular, 'validatePrice regular');
  if (!regularResult.success) {
    return false;
  }

  if (priceObj.sale) {
    const saleResult = parsePrice(priceObj.sale, 'validatePrice sale');
    if (!saleResult.success) {
      return false;
    }

    // Sale price cannot be higher than regular price
    if (saleResult.value > regularResult.value) {
      return false;
    }
  }

  return true;
}

/**
 * Format price for display with currency
 */
export function formatPriceForDisplay(price: string | number | null | undefined, currency: string = 'PHP'): string {
  if (price === null || price === undefined) {
    return 'N/A';
  }

  const priceResult = parsePrice(price, 'formatPriceForDisplay');
  if (!priceResult.success) {
    return 'N/A';
  }

  return new Intl.NumberFormat('en-PH', {
    style: 'currency',
    currency: currency,
  }).format(priceResult.value);
}

/**
 * Legacy parseFloat replacement - DEPRECATED, use parsePrice instead
 * This is for gradual migration only
 */
export function legacyParseFloat(input: string | number, fallback: number = 0): number {
  const result = parsePrice(input, 'legacy-migration');
  if (!result.success) {
    logger.warn('Using legacy parseFloat fallback', { input, fallback, error: result.error });
    return fallback;
  }
  return result.value;
}

// Production Monitoring and Alerting System
// NOTE: Configuration only - monitoring setup without deployment

import { logger } from './logger';
import { memoryMonitor } from './memory-optimizer';

interface MonitoringConfig {
  enableHealthChecks: boolean;
  enablePerformanceMonitoring: boolean;
  enableErrorTracking: boolean;
  enableAlerts: boolean;
  alertThresholds: {
    memoryUsageMB: number;
    responseTimeMs: number;
    errorRate: number;
    cpuUsagePercent: number;
  };
  healthCheckInterval: number;
  metricsRetentionMs: number;
}

interface SystemHealth {
  status: 'healthy' | 'degraded' | 'critical';
  timestamp: number;
  checks: {
    memory: { status: string; usageMB: number; limit: number };
    database: { status: string; connections: number; responseTime: number };
    redis: { status: string; connected: boolean; memory: string };
    api: { status: string; averageResponseTime: number; errorRate: number };
  };
  uptime: number;
  version: string;
}

interface Alert {
  id: string;
  type: 'memory' | 'performance' | 'error' | 'database' | 'security';
  severity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  timestamp: number;
  metadata: Record<string, unknown>;
  resolved: boolean;
}

class ProductionMonitoringService {
  private config: MonitoringConfig;
  private alerts: Alert[] = [];
  private metrics: Map<string, number[]> = new Map();
  private healthCheckTimer: NodeJS.Timeout | null = null;
  private cleanupInterval: NodeJS.Timeout | null = null;
  private startTime: number;

  constructor(config: Partial<MonitoringConfig> = {}) {
    this.config = {
      enableHealthChecks: config.enableHealthChecks ?? true,
      enablePerformanceMonitoring: config.enablePerformanceMonitoring ?? true,
      enableErrorTracking: config.enableErrorTracking ?? true,
      enableAlerts: config.enableAlerts ?? true,
      alertThresholds: {
        memoryUsageMB: config.alertThresholds?.memoryUsageMB ?? 45,
        responseTimeMs: config.alertThresholds?.responseTimeMs ?? 1000,
        errorRate: config.alertThresholds?.errorRate ?? 0.05, // 5%
        cpuUsagePercent: config.alertThresholds?.cpuUsagePercent ?? 80,
        ...config.alertThresholds
      },
      healthCheckInterval: config.healthCheckInterval ?? 30000, // 30 seconds
      metricsRetentionMs: config.metricsRetentionMs ?? 3600000, // 1 hour
    };

    this.startTime = Date.now();
    this.initializeMonitoring();
  }

  private initializeMonitoring(): void {
    if (this.config.enableHealthChecks) {
      this.startHealthChecks();
    }

    // Set up cleanup for old metrics
    this.cleanupInterval = setInterval(() => {
      this.cleanupOldMetrics();
    }, 300000); // Every 5 minutes
  }

  private startHealthChecks(): void {
    if (this.healthCheckTimer) {
      clearInterval(this.healthCheckTimer);
    }

    this.healthCheckTimer = setInterval(async () => {
      try {
        await this.performHealthCheck();
      } catch (error) {
        logger.error('Health check failed:', error as Record<string, unknown>);
      }
    }, this.config.healthCheckInterval);
  }

  async performHealthCheck(): Promise<SystemHealth> {
    const timestamp = Date.now();

    // Memory check
    const memoryStats = memoryMonitor.getStats();
    const currentMemory = memoryStats?.current?.heapUsed || 0;
    const memoryMB = Math.round(currentMemory / 1024 / 1024);

    // Database health check
    const dbHealth = await this.checkDatabaseHealth();

    // Redis health check
    const redisHealth = await this.checkRedisHealth();

    // API performance check
    const apiHealth = this.getAPIHealthMetrics();

    const health: SystemHealth = {
      status: this.calculateOverallStatus(memoryMB, dbHealth, redisHealth, apiHealth),
      timestamp,
      checks: {
        memory: {
          status: memoryMB > this.config.alertThresholds.memoryUsageMB ? 'warning' : 'ok',
          usageMB: memoryMB,
          limit: this.config.alertThresholds.memoryUsageMB
        },
        database: dbHealth,
        redis: redisHealth,
        api: apiHealth
      },
      uptime: timestamp - this.startTime,
      version: process.env.npm_package_version || '1.0.0'
    };

    // Check for alert conditions
    if (this.config.enableAlerts) {
      await this.checkAlertConditions(health);
    }

    // Record metrics
    this.recordMetric('memory.usage', memoryMB);
    this.recordMetric('api.response_time', apiHealth.averageResponseTime);
    this.recordMetric('api.error_rate', apiHealth.errorRate);

    return health;
  }

  private async checkDatabaseHealth(): Promise<any> {
    try {
      // This would normally check actual database connections
      // For now, return a mock healthy status
      return {
        status: 'ok',
        connections: 5,
        responseTime: 25
      };
    } catch (error) {
      return {
        status: 'error',
        connections: 0,
        responseTime: -1,
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  private async checkRedisHealth(): Promise<any> {
    try {
      // This would normally check Redis connection
      // For now, return a mock healthy status
      return {
        status: 'ok',
        connected: true,
        memory: '10MB'
      };
    } catch (error) {
      return {
        status: 'error',
        connected: false,
        memory: 'unknown',
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  private getAPIHealthMetrics(): any {
    const responseTimeMetrics = this.metrics.get('api.response_time') || [];
    const errorRateMetrics = this.metrics.get('api.error_rate') || [];

    const avgResponseTime = responseTimeMetrics.length > 0
      ? responseTimeMetrics.reduce((a, b) => a + b, 0) / responseTimeMetrics.length
      : 0;

    const avgErrorRate = errorRateMetrics.length > 0
      ? errorRateMetrics.reduce((a, b) => a + b, 0) / errorRateMetrics.length
      : 0;

    return {
      status: avgResponseTime > this.config.alertThresholds.responseTimeMs ? 'warning' : 'ok',
      averageResponseTime: Math.round(avgResponseTime),
      errorRate: parseFloat(avgErrorRate.toFixed(4))
    };
  }

  private calculateOverallStatus(
    memoryMB: number,
    dbHealth: any,
    redisHealth: any,
    apiHealth: any
  ): 'healthy' | 'degraded' | 'critical' {
    const issues = [
      memoryMB > this.config.alertThresholds.memoryUsageMB,
      dbHealth.status === 'error',
      redisHealth.status === 'error',
      apiHealth.averageResponseTime > this.config.alertThresholds.responseTimeMs
    ];

    const criticalIssues = [
      dbHealth.status === 'error',
      memoryMB > this.config.alertThresholds.memoryUsageMB * 1.2
    ];

    if (criticalIssues.some(Boolean)) return 'critical';
    if (issues.some(Boolean)) return 'degraded';
    return 'healthy';
  }

  private async checkAlertConditions(health: SystemHealth): Promise<void> {
    // Memory alert
    if (health.checks.memory.usageMB > this.config.alertThresholds.memoryUsageMB) {
      await this.createAlert({
        type: 'memory',
        severity: health.checks.memory.usageMB > this.config.alertThresholds.memoryUsageMB * 1.2 ? 'critical' : 'high',
        message: `High memory usage: ${health.checks.memory.usageMB}MB (limit: ${this.config.alertThresholds.memoryUsageMB}MB)`,
        metadata: { currentUsage: health.checks.memory.usageMB, limit: this.config.alertThresholds.memoryUsageMB }
      });
    }

    // API performance alert
    if (health.checks.api.averageResponseTime > this.config.alertThresholds.responseTimeMs) {
      await this.createAlert({
        type: 'performance',
        severity: 'medium',
        message: `Slow API response: ${health.checks.api.averageResponseTime}ms (threshold: ${this.config.alertThresholds.responseTimeMs}ms)`,
        metadata: { responseTime: health.checks.api.averageResponseTime, threshold: this.config.alertThresholds.responseTimeMs }
      });
    }

    // Database alert
    if (health.checks.database.status === 'error') {
      await this.createAlert({
        type: 'database',
        severity: 'critical',
        message: 'Database connectivity issues detected',
        metadata: health.checks.database
      });
    }
  }

  private async createAlert(alertData: Omit<Alert, 'id' | 'timestamp' | 'resolved'>): Promise<void> {
    const alert: Alert = {
      id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      timestamp: Date.now(),
      resolved: false,
      ...alertData
    };

    this.alerts.push(alert);

    // Log the alert
    logger.error(`ALERT [${alert.severity.toUpperCase()}]: ${alert.message}`, alert.metadata);

    // In production, this would send notifications (email, Slack, PagerDuty, etc.)
    await this.sendAlertNotification(alert);

    // Keep only recent alerts
    if (this.alerts.length > 100) {
      this.alerts = this.alerts.slice(-50);
    }
  }

  private async sendAlertNotification(alert: Alert): Promise<void> {
    // In production, implement actual notification sending
    // For now, just log it
    logger.warn(`Alert notification would be sent: ${alert.type} - ${alert.severity} - ${alert.message}`);

    // Example notification implementations:
    // await this.sendSlackAlert(alert);
    // await this.sendEmailAlert(alert);
    // await this.sendPagerDutyAlert(alert);
  }

  recordMetric(name: string, value: number): void {
    if (!this.metrics.has(name)) {
      this.metrics.set(name, []);
    }

    const values = this.metrics.get(name);
    if (!values) return;
    values.push(value);

    // Keep only recent values
    if (values.length > 100) {
      values.splice(0, values.length - 50);
    }
  }

  recordError(error: Error, context?: string): void {
    if (!this.config.enableErrorTracking) return;

    const errorData = {
      message: error.message,
      stack: error.stack,
      context,
      timestamp: Date.now()
    };

    logger.error('Application error recorded:', errorData);

    // Record error rate metric
    this.recordMetric('errors.count', 1);

    // Create alert for critical errors
    if (context === 'critical') {
      this.createAlert({
        type: 'error',
        severity: 'high',
        message: `Critical error: ${error.message}`,
        metadata: errorData
      });
    }
  }

  recordPerformanceMetric(name: string, value: number, unit = 'ms'): void {
    if (!this.config.enablePerformanceMonitoring) return;

    this.recordMetric(`performance.${name}`, value);

    logger.debug(`Performance metric: ${name} = ${value}${unit}`);
  }

  private cleanupOldMetrics(): void {
    const cutoff = Date.now() - this.config.metricsRetentionMs;

    // Clean up old alerts
    this.alerts = this.alerts.filter(alert => alert.timestamp > cutoff);

    logger.debug(`Cleaned up old monitoring data. Current alerts: ${this.alerts.length}`);
  }

  getSystemStatus(): SystemHealth | null {
    // This would return the latest health check result
    // For now, return null if no health check has been performed
    return null;
  }

  getRecentAlerts(limit = 10): Alert[] {
    return this.alerts
      .sort((a, b) => b.timestamp - a.timestamp)
      .slice(0, limit);
  }

  getMetrics(name: string): number[] {
    return this.metrics.get(name) || [];
  }

  resolveAlert(alertId: string): void {
    const alert = this.alerts.find(a => a.id === alertId);
    if (alert) {
      alert.resolved = true;
      logger.info(`Alert resolved: ${alertId}`);
    }
  }

  destroy(): void {
    if (this.healthCheckTimer) {
      clearInterval(this.healthCheckTimer);
      this.healthCheckTimer = null;
    }

    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
      this.cleanupInterval = null;
    }

    this.alerts = [];
    this.metrics.clear();

    logger.info('Monitoring service destroyed');
  }
}

