  upload: {
    timeout: 60000,
    retries: 1,
    description: 'File upload operations',
  },

  // External third-party APIs
  external: {
    timeout: 15000,
    retries: 2,
    description: 'External third-party API calls',
  },
} as const;

export type TimeoutLevel = keyof typeof TIMEOUT_CONFIGS;

interface ReliableFetchOptions extends RequestInit {
  timeoutLevel?: TimeoutLevel;
  customTimeout?: number;
  retries?: number;
  includeAuth?: boolean;
  onTimeout?: () => void;
  onRetry?: (attempt: number) => void;
  onError?: (error: Error) => void;
}

/**
 * Enhanced fetch with timeout, retry, and error handling
 */
export async function reliableFetch(
  url: string,
  options: ReliableFetchOptions = {}
): Promise<Response> {
  const {
    timeoutLevel = 'standard',
    customTimeout,
    retries,
    onTimeout,
    onRetry,
    onError,
    ...fetchOptions
  } = options;

  const config = TIMEOUT_CONFIGS[timeoutLevel];
  const timeoutMs = customTimeout || config.timeout;
  const maxRetries = retries !== undefined ? retries : config.retries;

  // Create AbortController for timeout
  const controller = new AbortController();
  const timeoutId = setTimeout(() => {
    controller.abort();
    if (onTimeout) {
      onTimeout();
    }
  }, timeoutMs);

  try {
    // Add signal to fetch options
    const enhancedOptions: RequestInit = {
      ...fetchOptions,
      signal: controller.signal,
    };

    // Log the request
    logger.debug('🔄 Reliable fetch initiated', {
      url,
      timeoutLevel,
      timeoutMs,
      maxRetries,
    });

    // Wrap with retry logic if retries are enabled
    if (maxRetries > 0) {
      return await withExternalAPIRetry(
        async () => {
          const response = await fetch(url, enhancedOptions);

          // Check for successful response
          if (!response.ok && response.status >= 500) {
            throw new Error(`Server error: ${response.status} ${response.statusText}`);
          }

          return response;
        },
        url,
        {
          maxAttempts: maxRetries,
          baseDelayMs: 1000,
          maxDelayMs: 10000,
          backoffMultiplier: 2,
          onRetry: (attempt: number, error: unknown) => {
            logger.warn('⚠️ Fetch retry attempt', {
              url,
              attempt,
              error: error instanceof Error ? error.message : 'Unknown error',
            });
            if (onRetry) {
              onRetry(attempt);
            }
          },
        }
      );
    } else {
      // Single attempt without retry
      return await fetch(url, enhancedOptions);
    }
  } catch (error: unknown) {
    // Clear the timeout
    clearTimeout(timeoutId);

    // Handle specific error types
    if (error instanceof Error && error.name === 'AbortError') {
      const timeoutError = new Error(
        `Request timeout after ${timeoutMs}ms for ${url}`
      );
      logger.error('⏱️ Fetch timeout', {
        url,
        timeoutMs,
        timeoutLevel,
      });
      if (onError) {
        onError(timeoutError);
      }
      throw timeoutError;
    }

    // Log and handle other errors
    const sanitizedError = handleError(error, 'reliableFetch');

    logger.error('❌ Fetch failed', {
      url,
      error: sanitizedError.message,
      timeoutLevel,
    });

    if (onError) {
      onError(new Error(String(sanitizedError?.message || 'Unknown error')));
    }

    throw sanitizedError;
  } finally {
    // Always clear the timeout
    clearTimeout(timeoutId);
  }
}

/**
 * Batch fetch with concurrent request limiting
 */
export async function batchFetch(
  urls: string[],
  options: ReliableFetchOptions & { concurrency?: number } = {}
): Promise<Response[]> {
  const { concurrency = 5, ...fetchOptions } = options;

  const results: Response[] = [];
  const errors: Error[] = [];

  // Process in batches
  for (let i = 0; i < urls.length; i += concurrency) {
    const batch = urls.slice(i, i + concurrency);

    const batchPromises = batch.map((url) =>
      reliableFetch(url, fetchOptions).catch((error) => {
        errors.push(error);
        return null;
      })
    );

    const batchResults = await Promise.all(batchPromises);
    results.push(...batchResults.filter((r): r is Response => r !== null));
  }

  // Log batch completion
  logger.info('📦 Batch fetch completed', {
    totalUrls: urls.length,
    successful: results.length,
    failed: errors.length,
  });

  // If all requests failed, throw an aggregate error
  if (results.length === 0 && errors.length > 0) {
    throw new AggregateError(errors, 'All batch requests failed');
  }

  return results;
}

/**
 * Fetch with automatic JSON parsing and validation
 */
export async function fetchJSON<T>(
  url: string,
  options: ReliableFetchOptions = {}
): Promise<T> {
  const response = await reliableFetch(url, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
  });

  if (!response.ok) {
    throw new Error(
      `Failed to fetch JSON from ${url}: ${response.status} ${response.statusText}`
    );
  }

  try {
    const data = await response.json();
    return data as T;
  } catch (error) {
    throw new Error(`Failed to parse JSON response from ${url}: ${error}`);
  }
}

/**
 * Health check with timeout
 */
export async function healthCheck(
  url: string,
  timeoutMs: number = 5000
): Promise<boolean> {
  try {
    const response = await reliableFetch(url, {
      method: 'HEAD',
      customTimeout: timeoutMs,
      retries: 0,
    });
    return response.ok;
  } catch {
    return false;
  }
}

/**
 * Fetch with progress tracking for large downloads
 */
export async function fetchWithProgress(
  url: string,
  onProgress?: (loaded: number, total: number) => void,
  options: ReliableFetchOptions = {}
): Promise<Blob> {
  const response = await reliableFetch(url, options);

  if (!response.ok) {
    throw new Error(`Failed to fetch ${url}: ${response.status}`);
  }

  const contentLength = response.headers.get('content-length');
  const total = contentLength ? parseInt(contentLength, 10) : 0;
  let loaded = 0;

  const reader = response.body?.getReader();
  if (!reader) {
    throw new Error('Response body is not readable');
  }

  const chunks: Uint8Array[] = [];

  while (true) {
    const { done, value } = await reader.read();

    if (done) break;

    chunks.push(value);
    loaded += value.length;

    if (onProgress && total > 0) {
      onProgress(loaded, total);
    }
  }

  return new Blob(chunks as BlobPart[]);
}

export default reliableFetch;

import DOMPurify from 'isomorphic-dompurify';

import { logger } from '@/lib/logger';

// Default sanitization options for different content types
const DEFAULT_OPTIONS: SanitizeOptions = {
  allowedTags: [
    'p', 'br', 'strong', 'em', 'u', 'i', 'b',
    'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
    'ul', 'ol', 'li',
    'blockquote', 'code', 'pre',
    'a', 'img'
  ],
  allowedAttributes: {
    '*': ['href', 'src', 'alt', 'title', 'class', 'id', 'target', 'rel', 'width', 'height']
  },
  ADD_ATTR: ['target', 'rel'],
  FORBID_TAGS: ['script', 'object', 'embed', 'base', 'link', 'meta', 'style'],
  FORBID_ATTR: ['onerror', 'onload', 'onclick', 'onmouseover', 'onfocus', 'onblur'],
  KEEP_CONTENT: true,
  RETURN_DOM: false,
  RETURN_DOM_FRAGMENT: false,
  RETURN_DOM_IMPORT: false,
  SANITIZE_DOM: true,
  WHOLE_DOCUMENT: false,
  FORCE_BODY: false,
};

// Strict sanitization for user-generated content
const STRICT_OPTIONS: SanitizeOptions = {
  allowedTags: ['p', 'br', 'strong', 'em', 'u', 'i', 'b'],
  allowedAttributes: {},
  KEEP_CONTENT: true,
  SANITIZE_DOM: true,
};

// Very permissive for admin content (still safe)
const PERMISSIVE_OPTIONS: SanitizeOptions = {
  ...DEFAULT_OPTIONS,
  allowedTags: [
    ...(DEFAULT_OPTIONS.allowedTags || []),
    'div', 'span', 'table', 'tr', 'td', 'th', 'thead', 'tbody',
    'dl', 'dt', 'dd', 'figure', 'figcaption'
  ],
  allowedAttributes: {
    ...(DEFAULT_OPTIONS.allowedAttributes || {}),
    '*': [...(DEFAULT_OPTIONS.allowedAttributes?.['*'] || []), 'style'],
    'data-*': []
  },
};

// Text-only sanitization (strips all HTML)
const TEXT_ONLY_OPTIONS: SanitizeOptions = {
  allowedTags: [],
  allowedAttributes: {},
  KEEP_CONTENT: true,
};

/**
 * Sanitize HTML content to prevent XSS attacks
 */
interface SanitizeOptions {
  allowedTags?: string[];
  allowedAttributes?: Record<string, string[]>;
  allowedSchemes?: string[];
  allowedIframeHostnames?: string[];
  transformTags?: Record<string, string | ((tagName: string, attribs: Record<string, string>) => { tagName: string; attribs: Record<string, string> })>;
  KEEP_CONTENT?: boolean;
  SANITIZE_DOM?: boolean;
  FORBID_TAGS?: string[];
  FORBID_ATTR?: string[];
  ADD_ATTR?: string[];
  RETURN_DOM?: boolean;
  RETURN_DOM_FRAGMENT?: boolean;
  RETURN_DOM_IMPORT?: boolean;
  WHOLE_DOCUMENT?: boolean;
  FORCE_BODY?: boolean;
}

// Add a specific type for DOMPurify config
interface DOMPurifyConfig extends SanitizeOptions {}

export function sanitizeHtml(
  html: string, 
  options: 'default' | 'strict' | 'permissive' | 'textOnly' | SanitizeOptions = 'default'
): string {
  if (!html || typeof html !== 'string') {
    return '';
  }

  let config: SanitizeOptions;

  switch (options) {
    case 'strict':
      config = STRICT_OPTIONS;
      break;
    case 'permissive':
      config = PERMISSIVE_OPTIONS;
      break;
    case 'textOnly':
      config = TEXT_ONLY_OPTIONS;
      break;
    case 'default':
      config = DEFAULT_OPTIONS;
      break;
    default:
      config = { ...DEFAULT_OPTIONS, ...(typeof options === 'object' ? options : {}) } as SanitizeOptions;
  }

  try {
    // Use the specific DOMPurifyConfig type instead of any
    const sanitized = DOMPurify.sanitize(html, config as DOMPurifyConfig);
    return sanitized;
  } catch (error) {
    logger.error('HTML sanitization error:', error as Record<string, unknown>);
    // Return empty string on error for security
    return '';
  }
}

/**
 * Common typo corrections for product descriptions
 */
function correctCommonTypos(text: string): string {
  const corrections: Record<string, string> = {
    'descirptions': 'descriptions',
    'descirption': 'description',
    'reccomend': 'recommend',
    'reccomended': 'recommended',
    'occassion': 'occasion',
    'seperate': 'separate',
    'teh': 'the',
    'adn': 'and',
    'orgainc': 'organic',
    'benifits': 'benefits',
    'benifit': 'benefit',
    'flavour': 'flavor',
    'colour': 'color',
    'favourite': 'favorite'
  };

  let correctedText = text;
  for (const [typo, correction] of Object.entries(corrections)) {
    // Case-insensitive replacement
    const regex = new RegExp(`\\b${typo}\\b`, 'gi');
    correctedText = correctedText.replace(regex, (match) => {
      // Preserve original case
      if (match === match.toUpperCase()) return correction.toUpperCase();
      if (match[0] && match[0] === match[0].toUpperCase()) return correction.charAt(0).toUpperCase() + correction.slice(1);
      return correction;
    });
  }

  return correctedText;
}

/**
 * Sanitize text content (removes all HTML tags) and corrects common typos
 */
export function sanitizeText(text: string): string {
  const sanitized = sanitizeHtml(text, 'textOnly');
  return correctCommonTypos(sanitized);
}

/**
 * Sanitize user-generated content with strict rules
 */
export function sanitizeUserContent(html: string): string {
  return sanitizeHtml(html, 'strict');
}

/**
 * Sanitize admin content with more permissive rules
 */
export function sanitizeAdminContent(html: string): string {
  return sanitizeHtml(html, 'permissive');
}

/**
 * Sanitize and truncate content
 */
export function sanitizeAndTruncate(
  html: string, 
  maxLength: number = 200,
  options: 'default' | 'strict' | 'permissive' | 'textOnly' = 'default'
): string {
  const sanitized = sanitizeHtml(html, options);

  if (sanitized.length <= maxLength) {
    return sanitized;
  }

  // Truncate while preserving word boundaries
  const truncated = sanitized.slice(0, maxLength);
  const lastSpace = truncated.lastIndexOf(' ');

  if (lastSpace > maxLength * 0.7) {
    return truncated.slice(0, lastSpace) + '...';
  }

  return truncated + '...';
}

/**
 * Check if content contains potentially dangerous elements
 */
export function containsDangerousContent(html: string): boolean {
  if (!html || typeof html !== 'string') {
    return false;
  }

  const dangerousPatterns = [
    /<script[\s\S]*?<\/script>/gi,
    /javascript:/gi,
    /vbscript:/gi,
    /data:text\/html/gi,
    /on\w+\s*=/gi, // Event handlers
    /<iframe[\s\S]*?>/gi,
    /<object[\s\S]*?>/gi,
    /<embed[\s\S]*?>/gi,
    /<form[\s\S]*?>/gi,
    /<meta[\s\S]*?>/gi,
    /<link[\s\S]*?>/gi,
  ];

  return dangerousPatterns.some(pattern => pattern.test(html));
}

/**
 * Extract plain text from HTML
 */
export function extractText(html: string, maxLength?: number): string {
  const text = sanitizeText(html).trim();

  if (!maxLength || text.length <= maxLength) {
    return text;
  }

  return sanitizeAndTruncate(text, maxLength, 'textOnly');
}

/**
 * Validate and sanitize URL
 */
export function sanitizeUrl(url: string): string {
  if (!url || typeof url !== 'string') {
    return '';
  }

  // Remove dangerous protocols
  const dangerousProtocols = /^(javascript|vbscript|data|file|ftp):/i;
  if (dangerousProtocols.test(url)) {
    return '';
  }

  // Ensure URL is properly formatted
  try {
    const parsed = new URL(url, 'https://example.com');
    return parsed.href;
  } catch {
    // If URL parsing fails, treat as relative URL
    return url.replace(/[<>"']/g, '');
  }
}

/**
 * Sanitize file name
 */
export function sanitizeFileName(fileName: string): string {
  if (!fileName || typeof fileName !== 'string') {
    return '';
  }

  return fileName
    .replace(/[<>:"/\\|?*\u0000-\u001f]/g, '') // Remove invalid characters
    .replace(/^\.+/, '') // Remove leading dots
    .slice(0, 255) // Limit length
    .trim();
}

/**
 * React component helper for safe HTML rendering
 */
export function createSafeHtml(
  html: string, 
  options?: 'default' | 'strict' | 'permissive' | 'textOnly'
) {
  return {
    __html: sanitizeHtml(html, options)
  };
}

const sanitize = {
  sanitizeHtml,
  sanitizeText,
  sanitizeUserContent,
  sanitizeAdminContent,
  sanitizeAndTruncate,
  containsDangerousContent,
  extractText,
  sanitizeUrl,
  sanitizeFileName,
  createSafeHtml,
};

export default sanitize;

/**
 * Real Web Scraper Implementation
 * Provides actual web scraping capabilities with proper error handling and compliance
 */

import axios from 'axios';
import * as cheerio from 'cheerio';
import PQueue from 'p-queue';
import UserAgent from 'user-agents';
import { CompetitorConfig } from './competitor-config';

// Product interface
export interface RealScrapedProduct {
  url: string;
  title: string | null;
  price: number | null;
  currency: string;
  availability: string | null;
  image: string | null;
  description: string | null;
  rating: number | null;
  reviews: number | null;
  brand: string | null;
  sku: string | null;
  scrapedAt: Date;
  competitor: string;
  error?: string;
}

// Robots.txt parser
class RobotsChecker {
  private robotsCache: Map<string, { allowed: boolean; checkedAt: Date }> = new Map();
  private cacheExpiryMs = 3600000; // 1 hour

  async isAllowed(url: string, _userAgent: string = '*'): Promise<boolean> { // userAgent not used in this mock
    try {
      const urlObj = new URL(url);
      const robotsUrl = `${urlObj.protocol}//${urlObj.host}/robots.txt`;

      // Check cache
      const cached = this.robotsCache.get(robotsUrl);
      if (cached && (Date.now() - cached.checkedAt.getTime()) < this.cacheExpiryMs) {
        return cached.allowed;
      }

      // Fetch robots.txt
      try {
        const response = await axios.get(robotsUrl, {
          timeout: 5000,
          validateStatus: (status) => status < 500
        });

        if (response.status === 404) {
          // No robots.txt means we can scrape
          this.robotsCache.set(robotsUrl, { allowed: true, checkedAt: new Date() });
          return true;
        }

        // Parse robots.txt (basic implementation)
        const content = response.data.toLowerCase();
        const disallowedPaths = content
          .split('\n')
          .filter((line: string) => line.trim().startsWith('disallow:'))
          .map((line: string) => line.replace('disallow:', '').trim());

        const pathname = urlObj.pathname.toLowerCase();
        const isDisallowed = disallowedPaths.some((path: string) =>
          path === '/' || pathname.startsWith(path)
        );

        const allowed = !isDisallowed;
        this.robotsCache.set(robotsUrl, { allowed, checkedAt: new Date() });
        return allowed;
      } catch (_error) {
        // If we can't fetch robots.txt, be conservative and allow
        console.warn(`Could not fetch robots.txt for ${robotsUrl}`);
        return true;
      }
    } catch (_error) {
      console.error('Error checking robots.txt:', error);
      return false;
    }
  }
}

/**
 * Real web scraper class
 */
export class RealWebScraper {
  private config: CompetitorConfig;
  private queue: PQueue;
  private robotsChecker: RobotsChecker;
  private userAgent: string;

  constructor(config: CompetitorConfig) {
    this.config = config;
    this.queue = new PQueue({
      concurrency: 1,
      interval: config.rateLimitMs,
      intervalCap: 1
    });
    this.robotsChecker = new RobotsChecker();
    this.userAgent = new UserAgent().toString();
  }

  /**
   * Scrape a single URL
   */
  async scrapeUrl(url: string): Promise<RealScrapedProduct> {
    // Check robots.txt compliance
    const isAllowed = await this.robotsChecker.isAllowed(url, this.userAgent);
    if (!isAllowed) {
      return {
        url,
        title: null,
        price: null,
        currency: this.config.priceParsing.currencySymbol,
        availability: null,
        image: null,
        description: null,
        rating: null,
        reviews: null,
        brand: null,
        sku: null,
        scrapedAt: new Date(),
        competitor: this.config.name,
        error: 'Blocked by robots.txt'
      };
    }

    const result = this.queue.add(async (): Promise<RealScrapedProduct> => {
      try {
        // Fetch the page
        const response = await axios.get(url, {
          headers: {
            'User-Agent': this.userAgent,
            'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
            'Accept-Language': 'en-US,en;q=0.5',
            'Accept-Encoding': 'gzip, deflate',
            'Connection': 'keep-alive',
            'Upgrade-Insecure-Requests': '1',
            ...this.config.headers
          },
          timeout: 30000,
          maxRedirects: 5,
          validateStatus: (status) => status < 500
        });

        if (response.status !== 200) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        // Parse HTML
        const $ = cheerio.load(response.data);

        // Extract data using selectors
        const product: RealScrapedProduct = {
          url,
          title: this.extractText($, this.config.selectors.productName),
          price: this.extractPrice($, this.config.selectors.price),
          currency: this.config.priceParsing.currencySymbol,
          availability: this.extractText($, this.config.selectors.availability),
          image: this.extractImage($, this.config.selectors.imageUrl, url),
          description: this.extractText($, this.config.selectors.description),
          rating: this.extractRating($, this.config.selectors.rating),
          reviews: this.extractReviews($, this.config.selectors.reviews),
          brand: this.extractText($, 'meta[property="og:brand"], .brand-name, .product-brand'),
          sku: this.extractText($, this.config.selectors.sku),
          scrapedAt: new Date(),
          competitor: this.config.name
        };

        return product;
      } catch (error) {
        console.error(`Error scraping ${url}:`, error);
        return {
          url,
          title: null,
          price: null,
          currency: this.config.priceParsing.currencySymbol,
          availability: null,
          image: null,
          description: null,
          rating: null,
          reviews: null,
          brand: null,
          sku: null,
          scrapedAt: new Date(),
          competitor: this.config.name,
          error: error instanceof Error ? error.message : 'Unknown error'
        };
      }
    });

    return result as Promise<RealScrapedProduct>;
  }

  /**
   * Scrape multiple URLs
   */
  async scrapeUrls(urls: string[]): Promise<RealScrapedProduct[]> {
    const results: RealScrapedProduct[] = [];

    for (const url of urls) {
      try {
        const product = await this.scrapeUrl(url);
        results.push(product);

        // Add delay between requests
        await new Promise(resolve => setTimeout(resolve, this.config.rateLimitMs));
      } catch (error) {
        console.error(`Failed to scrape ${url}:`, error);
        results.push({
          url,
          title: null,
          price: null,
          currency: this.config.priceParsing.currencySymbol,
          availability: null,
          image: null,
          description: null,
          rating: null,
          reviews: null,
          brand: null,
          sku: null,
          scrapedAt: new Date(),
          competitor: this.config.name,
          error: error instanceof Error ? error.message : 'Unknown error'
        });
      }
    }

    return results;
  }

  /**
   * Helper methods for data extraction
   */
  private extractText($: cheerio.Root, selector?: string): string | null {
    if (!selector) return null;

    try {
      // Try multiple selector variations
      const selectors = selector.split(',').map(s => s.trim());

      for (const sel of selectors) {
        const element = $(sel).first();
        if (element.length > 0) {
          // Try different methods to get text
          let text = element.text().trim();
          if (!text) {
            text = element.attr('content') || '';
          }
          if (!text) {
            text = element.attr('value') || '';
          }
          if (text) {
            return text.substring(0, 1000); // Limit text length
          }
        }
      }

      return null;
    } catch (error) {
      console.error('Error extracting text:', error);
      return null;
    }
  }

  private extractPrice($: cheerio.Root, selector?: string): number | null {
    if (!selector) return null;

    try {
      const priceText = this.extractText($, selector);
      if (!priceText) return null;

      // Clean price text and extract number
      const cleanPrice = priceText
        .replace(/[^0-9.,]/g, '')
        .replace(',', '');

      const price = parseFloat(cleanPrice);
      return isNaN(price) ? null : price;
    } catch (error) {
      console.error('Error extracting price:', error);
      return null;
    }
  }

  private extractImage($: cheerio.Root, selector?: string, baseUrl?: string): string | null {
    if (!selector) return null;

    try {
      const element = $(selector).first();
      if (element.length === 0) return null;

      // Try different attributes
      let imageUrl = element.attr('src') ||
                     element.attr('data-src') ||
                     element.attr('data-lazy-src') ||
                     element.attr('content');

      if (!imageUrl) return null;

      // Make URL absolute if it's relative
      if (baseUrl && !imageUrl.startsWith('http')) {
        const urlObj = new URL(baseUrl);
        if (imageUrl.startsWith('//')) {
          imageUrl = urlObj.protocol + imageUrl;
        } else if (imageUrl.startsWith('/')) {
          imageUrl = `${urlObj.protocol}//${urlObj.host}${imageUrl}`;
        } else {
          imageUrl = `${urlObj.protocol}//${urlObj.host}/${imageUrl}`;
        }
      }

      return imageUrl;
    } catch (error) {
      console.error('Error extracting image:', error);
      return null;
    }
  }

  private extractRating($: cheerio.Root, selector?: string): number | null {
    if (!selector) return null;

    try {
      const ratingText = this.extractText($, selector);
      if (!ratingText) return null;

      // Extract number from rating text
      const match = ratingText.match(/[\d.]+/);
      if (match) {
        const rating = parseFloat(match[0]);
        return isNaN(rating) ? null : Math.min(5, rating); // Cap at 5
      }

      return null;
    } catch (error) {
      console.error('Error extracting rating:', error);
      return null;
    }
  }

  private extractReviews($: cheerio.Root, selector?: string): number | null {
    if (!selector) return null;

    try {
      const reviewText = this.extractText($, selector);
      if (!reviewText) return null;

      // Extract number from review text
      const cleanText = reviewText.replace(/,/g, '');
      const match = cleanText.match(/\d+/);
      if (match) {
        const reviews = parseInt(match[0]);
        return isNaN(reviews) ? null : reviews;
      }

      return null;
    } catch (error) {
      console.error('Error extracting reviews:', error);
      return null;
    }
  }
}

