/**
 * Robots.txt Compliance Checker
 * Ensures scraping respects website robots.txt files
 */

import axios from 'axios';
import { parse as parseRobotsTxt } from 'robots-txt-parser';
import { logger } from '@/lib/logger';
import { handleError } from '@/lib/error-sanitizer';

// Types for robots-txt-parser
interface RobotsRules {
  isAllowed(path: string, userAgent: string): boolean;
  getCrawlDelay(userAgent: string): number | null;
}

// Cache robots.txt files (domain -> rules)
const robotsCache = new Map<string, RobotsCache>();
const CACHE_TTL = 3600000; // 1 hour

interface RobotsCache {
  rules: RobotsRules;
  timestamp: number;
}

/**
 * Check if a URL is allowed to be scraped according to robots.txt
 */
export async function checkRobotsCompliance(url: string): Promise<{
  allowed: boolean;
  crawlDelay?: number;
  error?: string;
}> {
  try {
    const urlObj = new URL(url);
    const domain = urlObj.hostname;
    const path = urlObj.pathname;

    // Check cache
    const cached = robotsCache.get(domain) as RobotsCache;
    if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
      return checkPathAgainstRules(cached.rules, path);
    }

    // Fetch robots.txt
    const robotsUrl = `${urlObj.protocol}//${domain}/robots.txt`;

    try {
      const response = await axios.get(robotsUrl, {
        timeout: 5000,
        headers: {
          'User-Agent': 'Agriko-Scraper/1.0 (https://agriko.com/bot)'
        }
      });

      // Parse robots.txt
      const rules = await parseRobotsTxt(robotsUrl, response.data);

      // Cache the rules
      robotsCache.set(domain, {
        rules,
        timestamp: Date.now()
      });

      return checkPathAgainstRules(rules, path);
    } catch (fetchError) {
      // If robots.txt doesn't exist or can't be fetched, assume allowed
      logger.info(`No robots.txt found for ${domain}, assuming allowed`);
      return { allowed: true };
    }
  } catch (error) {
    logger.error('Error checking robots compliance:', handleError(error, 'robots-checker-compliance'));
    return {
      allowed: false,
      error: 'Failed to check robots.txt compliance'
    };
  }
}

/**
 * Check a path against parsed robots.txt rules
 */
function checkPathAgainstRules(rules: RobotsRules, path: string): {
  allowed: boolean;
  crawlDelay?: number;
  error?: string;
} {
  try {
    // Check for our user agent or wildcard
    const userAgent = 'Agriko-Scraper';

    // Check if path is allowed
    const allowed = rules.isAllowed(path, userAgent);

    // Get crawl delay if specified
    const crawlDelay = rules.getCrawlDelay(userAgent) || undefined;

    if (!allowed) {
      return {
        allowed: false,
        error: `Path ${path} is disallowed by robots.txt`
      };
    }

    return {
      allowed: true,
      crawlDelay
    };
  } catch (error) {
    // If parsing fails, be conservative and disallow
    return {
      allowed: false,
      error: 'Failed to parse robots.txt rules'
    };
  }
}

/**
 * Get crawl delay for a domain
 */
export async function getCrawlDelay(domain: string): Promise<number> {
  try {
    const cached = robotsCache.get(domain) as RobotsCache;
    if (cached && cached.rules) {
      const delay = cached.rules.getCrawlDelay('Agriko-Scraper');
      return delay || 1; // Default 1 second if not specified
    }

    // Check robots.txt
    const result = await checkRobotsCompliance(`https://${domain}/`);
    return result.crawlDelay || 1;
  } catch (error) {
    return 1; // Default 1 second on error
  }
}

/**
 * Clean up old cache entries
 */
export function cleanupRobotsCache() {
  const now = Date.now();
  for (const [domain, cache] of robotsCache.entries()) {
    if (now - cache.timestamp > CACHE_TTL * 2) {
      robotsCache.delete(domain);
    }
  }
}

// Run cleanup every hour
if (typeof setInterval !== 'undefined') {
  setInterval(cleanupRobotsCache, 3600000);
}

/**
 * Scraper Authentication & Security Module
 * Validates requests to scraping endpoints
 */

import { NextRequest } from 'next/server';
import { cookies } from 'next/headers';
import { verify } from 'jsonwebtoken';
import { logger } from '@/lib/logger';
import { handleError } from '@/lib/error-sanitizer';

const JWT_SECRET = process.env.JWT_SECRET || 'development-secret-key';

// Whitelisted domains for scraping
const ALLOWED_DOMAINS = [
  'wholefoodsmarket.com',
  'naturesbasket.co.in',
  'freshdirect.com',
  'instacart.com',
  'walmart.com',
  'target.com',
  'kroger.com',
  'safeway.com',
  'amazon.com',
  'costco.com'
];

// Rate limiting configuration
const RATE_LIMITS = {
  requestsPerMinute: 10,
  requestsPerHour: 100,
  requestsPerDay: 500
};

// In-memory rate limiting (should use Redis in production)
const rateLimitMap = new Map<string, { count: number; resetTime: number }>();

/**
 * Validate authentication for scraper endpoints
 */
export async function validateScraperAuth(_request: NextRequest): Promise<{ // request not used in current implementation
  success: boolean;
  userId?: string;
  error?: string;
}> {
  try {
    // Check for session cookie
    const cookieStore = await cookies();
    const sessionToken = cookieStore.get('session-token');

    if (!sessionToken) {
      return { success: false, error: 'No authentication token found' };
    }

    // Verify JWT token
    try {
      const decoded = verify(sessionToken.value, JWT_SECRET, {
        issuer: 'agriko-api',
        audience: 'agriko-services'
      }) as any;

      // Check if user has admin or scraper permissions
      if (!decoded.permissions?.includes('admin') &&
          !decoded.permissions?.includes('scraper')) {
        return { success: false, error: 'Insufficient permissions for scraping' };
      }

      return { success: true, userId: decoded.userId };
    } catch (error) {
      logger.error('Token verification failed:', handleError(error, 'scraper-auth-token-verify'));
      return { success: false, error: 'Invalid authentication token' };
    }
  } catch (error) {
    logger.error('Auth validation error:', handleError(error, 'scraper-auth-validate'));
    return { success: false, error: 'Authentication failed' };
  }
}

/**
 * Validate URL is allowed for scraping
 */
export function validateScrapingUrl(url: string): {
  valid: boolean;
  domain?: string;
  error?: string;
} {
  try {
    const urlObj = new URL(url);
    const hostname = urlObj.hostname.replace('www.', '');

    // Check if domain is whitelisted
    const isAllowed = ALLOWED_DOMAINS.some(domain =>
      hostname.includes(domain.replace('www.', ''))
    );

    if (!isAllowed) {
      return {
        valid: false,
        error: `Domain ${hostname} is not whitelisted for scraping`
      };
    }

    // Check protocol (only HTTPS allowed)
    if (urlObj.protocol !== 'https:') {
      return {
        valid: false,
        error: 'Only HTTPS URLs are allowed'
      };
    }

    return { valid: true, domain: hostname };
  } catch (_error) {
    return {
      valid: false,
      error: 'Invalid URL format'
    };
  }
}

/**
 * Check rate limiting for user
 */
export function checkRateLimit(userId: string): {
  allowed: boolean;
  remaining?: number;
  resetTime?: number;
  error?: string;
} {
  const now = Date.now();
  const userLimit = rateLimitMap.get(userId);

  // Reset if time window has passed (1 minute)
  if (!userLimit || userLimit.resetTime < now) {
    rateLimitMap.set(userId, {
      count: 1,
      resetTime: now + 60000 // 1 minute
    });
    return { allowed: true, remaining: RATE_LIMITS.requestsPerMinute - 1 };
  }

  // Check if limit exceeded
  if (userLimit.count >= RATE_LIMITS.requestsPerMinute) {
    const waitTime = Math.ceil((userLimit.resetTime - now) / 1000);
    return {
      allowed: false,
      remaining: 0,
      resetTime: userLimit.resetTime,
      error: `Rate limit exceeded. Please wait ${waitTime} seconds`
    };
  }

  // Increment counter
  userLimit.count++;
  rateLimitMap.set(userId, userLimit);

  return {
    allowed: true,
    remaining: RATE_LIMITS.requestsPerMinute - userLimit.count
  };
}

/**
 * Clean up old rate limit entries (run periodically)
 */
export function cleanupRateLimits() {
  const now = Date.now();
  for (const [userId, limit] of rateLimitMap.entries()) {
    if (limit.resetTime < now - 3600000) { // 1 hour old
      rateLimitMap.delete(userId);
    }
  }
}

// Run cleanup every 30 minutes
if (typeof setInterval !== 'undefined') {
  setInterval(cleanupRateLimits, 1800000);
}

/**
 * Server-Side Scraper Service
 * Uses proxy API to actually scrape websites
 */

import db from '@/lib/database/competitor-qdrant';
import { checkRobotsCompliance } from './robots-checker';
// Unused import: getCrawlDelay
import { ScrapingResult, ScrapedProduct } from '@/types/scraping';

export class ServerScraper {
  private baseUrl: string;

  constructor() {
    // Use the app's own proxy endpoint
    this.baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3001';
  }

  /**
   * Scrape URLs using a specific competitor's configuration
   */
  async scrapeWithCompetitor(
    competitorKey: string,
    urls: string[]
  ): Promise<ScrapingResult> {
    const competitor = await db.competitor.getByKey(competitorKey);

    if (!competitor) {
      return {
        success: false,
        competitorKey,
        competitorName: 'Unknown',
        products: [],
        errors: [{ url: '', error: `Competitor ${competitorKey} not found` }],
        totalProducts: 0,
        successCount: 0,
        errorCount: 1,
      };
    }

    if (!competitor.enabled) {
      return {
        success: false,
        competitorKey: competitor.key,
        competitorName: competitor.name,
        products: [],
        errors: [{ url: '', error: `Competitor ${competitor.name} is disabled` }],
        totalProducts: 0,
        successCount: 0,
        errorCount: 1,
      };
    }

    // Create a scraping job
    const job = await db.job.create(competitorKey, urls);

    // Update job status to processing
    await db.job.updateStatus(job.id, 'processing', {
      startedAt: new Date(),
    });

    const products: ScrapedProduct[] = [];
    const errors: Array<{ url: string; error: string }> = [];
    let successCount = 0;
    let errorCount = 0;

    // Scrape each URL
    for (const url of urls) {
      try {
        // Check robots.txt compliance
        const robotsCheck = await checkRobotsCompliance(url);
        if (!robotsCheck.allowed) {
          errors.push({
            url,
            error: `Blocked by robots.txt: ${robotsCheck.error}`
          });
          errorCount++;
          continue;
        }

        // Add rate limiting delay (respect robots.txt crawl delay)
        if (products.length > 0) {
          const crawlDelay = robotsCheck.crawlDelay
            ? Math.max(robotsCheck.crawlDelay * 1000, competitor.rateLimitMs)
            : competitor.rateLimitMs;
          await this.delay(crawlDelay);
        }

        // Call our proxy endpoint
        const response = await fetch(`${this.baseUrl}/api/scraper/proxy`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            url,
            selectors: competitor.selectors as any,
            headers: competitor.headers as any,
          }),
        });

        const result = await response.json();

        if (result.success && result.data) {
          const productData: ScrapedProduct = {
            id: `${competitor.key}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
            url,
            title: result.data.title || result.metaData?.ogTitle || 'Unknown Product',
            price: result.data.price || parseFloat(result.metaData?.ogPrice || '0') || 0,
            originalPrice: undefined,
            currency: competitor.currency,
            availability: this.parseAvailability(
              result.data.availability || result.metaData?.ogAvailability
            ),
            description: result.data.description || result.metaData?.ogDescription,
            imageUrl: result.data.imageUrl || result.metaData?.ogImage,
            rating: result.data.rating,
            reviewCount: result.data.reviewCount,
            sku: result.data.sku,
            brand: competitor.name,
            category: this.extractCategory(url),
            competitorName: competitor.name,
            competitorKey: competitor.key,
            lastUpdated: new Date().toISOString(),
          };

          products.push(productData);
          successCount++;
        } else {
          errors.push({
            url,
            error: result.error || 'Failed to extract data',
          });
          errorCount++;
        }
      } catch (error) {
        errors.push({
          url,
          error: error instanceof Error ? error.message : 'Unknown error',
        });
        errorCount++;
      }
    }

    // Save products to database
    if (products.length > 0) {
      // Transform products to match expected interface
      const productsForDb = products.map(p => ({
        url: p.url,
        title: p.title,
        price: p.price,
        originalPrice: p.originalPrice || undefined,
        currency: p.currency,
        availability: p.availability,
        stockLevel: p.stockLevel,
        description: p.description || undefined,
        imageUrl: p.imageUrl || undefined,
        rating: p.rating || undefined,
        reviewCount: p.reviewCount || undefined,
        brand: p.brand || undefined,
        category: p.category || undefined,
        tags: p.tags || undefined
      }));
      await db.product.saveMany(competitor.key, job.id, productsForDb);
    }

    // Update job status
    await db.job.updateStatus(job.id, errorCount === urls.length ? 'failed' : 'completed', {
      completedAt: new Date(),
      totalProducts: products.length,
      successCount,
      errorCount,
      error: errors.length > 0 ? JSON.stringify(errors) : undefined,
    });

    return {
      success: successCount > 0,
      competitorKey: competitor.key,
      competitorName: competitor.name,
      products,
      errors,
      totalProducts: products.length,
      successCount,
      errorCount,
      jobId: job.id,
    };
  }

  /**
   * Scrape URLs from all enabled competitors
   */
  async scrapeAllCompetitors(urls: string[]): Promise<ScrapingResult[]> {
    const competitors = await db.competitor.getAll();
    const results: ScrapingResult[] = [];

    for (const competitor of competitors) {
      if (competitor.enabled) {
        const result = await this.scrapeWithCompetitor(competitor.key, urls);
        results.push(result);

        // Add delay between competitors
        await this.delay(2000);
      }
    }

    return results;
  }

  /**
   * Process pending scraping jobs
   */
  async processPendingJobs(limit = 5): Promise<void> {
    const jobs = await db.job.getPending(limit);

    for (const job of jobs) {
      if (job.competitor) {
        const urls = job.urls as string[];
        await this.scrapeWithCompetitor(job.competitor.key, urls);
      }
    }
  }

  /**
   * Parse availability status
   */
  private parseAvailability(text?: string | null): string {
    if (!text) return 'Out of Stock';

    const lower = text.toLowerCase();
    if (lower.includes('in stock') || lower.includes('available')) {
      return 'In Stock';
    }
    if (lower.includes('out of stock') || lower.includes('unavailable')) {
      return 'Out of Stock';
    }
    if (lower.includes('limited')) {
      return 'Limited Stock';
    }
    if (lower.includes('pre-order') || lower.includes('preorder')) {
      return 'Pre-order';
    }

    return 'Unknown';
  }

  /**
   * Extract category from URL
   */
  private extractCategory(url: string): string {
    const urlParts = url.split('/');
    const categories = ['rice', 'grain', 'spice', 'organic', 'produce', 'dairy'];

    for (const part of urlParts) {
      for (const category of categories) {
        if (part.toLowerCase().includes(category)) {
          return category;
        }
      }
    }

    return 'general';
  }

  /**
   * Delay helper
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

