}

// Update user preferences based on search behavior
function updatePreferencesFromSearch(profile: UserProfile, searchEvent: SearchEvent): void {
  // Extract categories from results and boost preferences
  for (const result of searchEvent.results) {
    // This would be enhanced with actual product data lookup
    // For now, we'll simulate category extraction from result titles
    const categories = extractCategoriesFromTitle(result.title);

    for (const category of categories) {
      profile.preferences.categories[category] = (profile.preferences.categories[category] ?? 0) + 0.1;
    }
  }

  // Extract health benefits from query
  const healthBenefits = extractHealthBenefitsFromQuery(searchEvent.query);
  for (const benefit of healthBenefits) {
    profile.preferences.healthBenefits[benefit] = (profile.preferences.healthBenefits[benefit] ?? 0) + 0.2;
  }
}

// Extract categories from product titles (simplified)
function extractCategoriesFromTitle(title: string): string[] {
  const categories: string[] = [];
  const lowerTitle = title.toLowerCase();

  const categoryKeywords = {
    'spices': ['turmeric', 'ginger', 'cinnamon', 'cumin', 'pepper'],
    'honey': ['honey', 'raw honey', 'organic honey'],
    'rice': ['rice', 'black rice', 'brown rice', 'wild rice'],
    'herbs': ['moringa', 'basil', 'oregano', 'thyme', 'sage'],
    'tea': ['tea', 'blend', 'salabat', 'herbal tea']
  };

  for (const [category, keywords] of Object.entries(categoryKeywords)) {
    for (const keyword of keywords) {
      if (lowerTitle.includes(keyword)) {
        categories.push(category);
        break;
      }
    }
  }

  return categories;
}

// Extract health benefits from search query
function extractHealthBenefitsFromQuery(query: string): string[] {
  const benefits: string[] = [];
  const lowerQuery = query.toLowerCase();

  for (const [benefit, synonyms] of Object.entries(healthSynonyms)) {
    if (synonyms.some(synonym => lowerQuery.includes(synonym.toLowerCase()))) {
      benefits.push(benefit);
    }
  }

  return benefits;
}

// Expand search query with synonyms and related terms
export function expandSearchQuery(originalQuery: string, userProfile?: UserProfile): string[] {
  const expansions = new Set<string>();
  expansions.add(originalQuery);

  const lowerQuery = originalQuery.toLowerCase();

  // Add health synonyms
  for (const [mainTerm, synonyms] of Object.entries(healthSynonyms)) {
    if (synonyms.some(syn => lowerQuery.includes(syn.toLowerCase())) || lowerQuery.includes(mainTerm)) {
      synonyms.forEach(syn => expansions.add(syn));
      expansions.add(mainTerm);
    }
  }

  // Add user preference-based expansions
  if (userProfile) {
    const topCategories = Object.entries(userProfile.preferences.categories)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 3)
      .map(([category]) => category);

    // If query relates to user's preferred categories, add category-specific terms
    for (const category of topCategories) {
      if (isQueryRelatedToCategory(lowerQuery, category)) {
        expansions.add(category);
      }
    }
  }

  return Array.from(expansions);
}

// Check if query relates to a category
function isQueryRelatedToCategory(query: string, category: string): boolean {
  const categoryRelations: Record<string, string[]> = {
    'spices': ['seasoning', 'flavor', 'cooking', 'culinary'],
    'honey': ['sweet', 'natural sweetener', 'syrup'],
    'rice': ['grain', 'carbohydrate', 'staple'],
    'herbs': ['medicinal', 'herbal', 'botanical'],
    'tea': ['beverage', 'drink', 'infusion']
  };

  const relations = categoryRelations[category] ?? [];
  return relations.some(relation => query.includes(relation));
}

// Get seasonal boost for current time
export function getSeasonalBoost(query: string): number {
  const currentMonth = new Date().getMonth() + 1; // 1-12
  let totalBoost = 1.0;

  for (const boost of seasonalBoosts) {
    if (query.toLowerCase().includes(boost.keyword.toLowerCase())) {
      // Handle year-crossing seasons (e.g., winter: Dec-Feb)
      const inSeason = boost.startMonth <= boost.endMonth
        ? currentMonth >= boost.startMonth && currentMonth <= boost.endMonth
        : currentMonth >= boost.startMonth || currentMonth <= boost.endMonth;

      if (inSeason) {
        totalBoost *= boost.boost;
      }
    }
  }

  return totalBoost;
}

// Get personalized search recommendations
export function getPersonalizedBoosts(sessionId: string, _query: string): Record<string, number> {
  const profile = getUserProfile(sessionId);
  const boosts: Record<string, number> = {};

  // Category preference boosts
  const topCategories = Object.entries(profile.preferences.categories)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5);

  for (const [category, weight] of topCategories) {
    if (weight > 0.5) { // Only significant preferences
      boosts[category] = 1 + (weight * 0.2); // Max 20% boost
    }
  }

  // Health benefit preference boosts
  const topBenefits = Object.entries(profile.preferences.healthBenefits)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5);

  for (const [benefit, weight] of topBenefits) {
    if (weight > 0.3) {
      boosts[benefit] = 1 + (weight * 0.15); // Max 15% boost
    }
  }

  return boosts;
}

// Get search analytics summary
export function getSearchAnalytics(timeRange: number = 24 * 60 * 60 * 1000): {
  totalSearches: number;
  uniqueUsers: number;
  topQueries: Array<{ query: string; count: number; ctr: number }>;
  topResults: Array<{ productId: number; title: string; clicks: number; impressions: number; ctr: number }>;
  seasonalTrends: Record<string, number>;
} {
  const cutoffTime = Date.now() - timeRange;
  const recentEvents = searchEvents.filter(event => event.timestamp >= cutoffTime);

  // Calculate metrics
  const totalSearches = recentEvents.length;
  const uniqueUsers = new Set(recentEvents.map(e => e.sessionId)).size;

  // Top queries with CTR
  const queryStats = new Map<string, { count: number; clicks: number; impressions: number }>();

  for (const event of recentEvents) {
    const query = event.query.toLowerCase();
    if (!queryStats.has(query)) {
      queryStats.set(query, { count: 0, clicks: 0, impressions: 0 });
    }

    const stats = queryStats.get(query);
    if (stats) {
      stats.count++;
      stats.impressions += event.results.length;
      stats.clicks += event.results.filter(r => r.clicked).length;
    }
  }

  const topQueries = Array.from(queryStats.entries())
    .map(([query, stats]) => ({
      query,
      count: stats.count,
      ctr: stats.impressions > 0 ? (stats.clicks / stats.impressions) : 0
    }))
    .sort((a, b) => b.count - a.count)
    .slice(0, 10);

  // Top results
  const resultStats = new Map<number, { title: string; clicks: number; impressions: number }>();

  for (const event of recentEvents) {
    for (const result of event.results) {
      if (!resultStats.has(result.productId)) {
        resultStats.set(result.productId, { 
          title: result.title, 
          clicks: 0, 
          impressions: 0 
        });
      }

      const stats = resultStats.get(result.productId);
      if (stats) {
        stats.impressions++;
        if (result.clicked) stats.clicks++;
      }
    }
  }

  const topResults = Array.from(resultStats.entries())
    .map(([productId, stats]) => ({
      productId,
      title: stats.title,
      clicks: stats.clicks,
      impressions: stats.impressions,
      ctr: stats.impressions > 0 ? (stats.clicks / stats.impressions) : 0
    }))
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0, 10);

  // Seasonal trends (simplified)
  const seasonalTrends: Record<string, number> = {};
  const currentMonth = new Date().getMonth() + 1;

  for (const boost of seasonalBoosts) {
    const inSeason = boost.startMonth <= boost.endMonth
      ? currentMonth >= boost.startMonth && currentMonth <= boost.endMonth
      : currentMonth >= boost.startMonth || currentMonth <= boost.endMonth;

    if (inSeason) {
      seasonalTrends[boost.keyword] = (seasonalTrends[boost.keyword] ?? 0) + 1;
    }
  }

  return {
    totalSearches,
    uniqueUsers,
    topQueries,
    topResults,
    seasonalTrends
  };
}

// Clear old data (cleanup function)
export function clearOldAnalytics(maxAge: number = 7 * 24 * 60 * 60 * 1000): void {
  const cutoffTime = Date.now() - maxAge;

  // Clear old search events
  searchEvents = searchEvents.filter(event => event.timestamp >= cutoffTime);

  // Clear old user profiles
  for (const [sessionId, profile] of userProfiles.entries()) {
    if (profile.updatedAt < cutoffTime) {
      userProfiles.delete(sessionId);
    } else {
      // Clean old history within profiles
      profile.searchHistory = profile.searchHistory.filter(s => s.timestamp >= cutoffTime);
      profile.clickHistory = profile.clickHistory.filter(c => c.timestamp >= cutoffTime);
    }
  }

  logger.info(`🧹 Cleaned analytics: ${searchEvents.length} events, ${userProfiles.size} profiles remaining`);
}

import { logger } from '@/lib/logger';
import type {
  ServiceWorkerMessage,
  ServiceWorkerResponse,
  CacheInfo,
  ServiceWorkerStatus
  // BackgroundSyncOptions // Preserved for future background sync implementation
} from '@/types/service-worker';
// Service Worker registration and management utilities

// Service Worker Manager interface for no-op fallback
// interface ServiceWorkerManagerInterface {
//   register(): Promise<boolean>;
//   checkForUpdates(): Promise<void>;
//   unregister(): Promise<boolean>;
//   sendMessage(message: ServiceWorkerMessage): Promise<ServiceWorkerResponse>;
//   registerBackgroundSync(tag: string, options?: BackgroundSyncOptions): Promise<void>;
//   getCacheInfo(): Promise<CacheInfo | null>;
//   clearCache(): Promise<boolean>;
//   getStatus(): ServiceWorkerStatus;
// } // Preserved for future service worker implementation

// Add a new interface that extends the base interface with all required properties
// interface FullServiceWorkerManagerInterface extends ServiceWorkerManagerInterface {
//   registration: ServiceWorkerRegistration | null;
//   isSupported: boolean;
//   updateCheckInterval: NodeJS.Timeout | null;
//   handleUpdate(): void;
//   showUpdateNotification(): void;
// } // Preserved for future full service worker implementation

// Background sync registration interface
interface ServiceWorkerRegistrationWithSync extends ServiceWorkerRegistration {
  sync?: {
    register(tag: string): Promise<void>;
  };
}

// Layout shift entry interface
interface LayoutShiftEntry extends PerformanceEntry {
  entryType: 'layout-shift';
  hadRecentInput: boolean;
  value: number;
}

export class ServiceWorkerManager {
  private static instance: ServiceWorkerManager;
  private registration: ServiceWorkerRegistration | null = null;
  private isSupported: boolean = false;
  private updateCheckInterval: NodeJS.Timeout | null = null;

  constructor() {
    this.isSupported = typeof window !== 'undefined' && 'serviceWorker' in navigator;
  }

  static getInstance(): ServiceWorkerManager {
    if (typeof window === 'undefined') {
      // Return a no-op instance during SSR
      return {
        register: async () => false,
        checkForUpdates: async () => {},
        unregister: async () => false,
        sendMessage: async () => Promise.reject(new Error('Not available during SSR')),
        registerBackgroundSync: async () => Promise.reject(new Error('Not available during SSR')),
        getCacheInfo: async () => null,
        clearCache: async () => false,
        getStatus: () => ({ supported: false, registered: false }),
        // Add missing properties for SSR fallback
        registration: null,
        isSupported: false,
        updateCheckInterval: null,
        handleUpdate: () => {},
        showUpdateNotification: () => {}
      } as unknown as ServiceWorkerManager;
    }

    if (!ServiceWorkerManager.instance) {
      ServiceWorkerManager.instance = new ServiceWorkerManager();
    }
    return ServiceWorkerManager.instance;
  }

  // Register the service worker
  async register(): Promise<boolean> {
    if (!this.isSupported) {
      logger.warn('Service Worker not supported in this browser');
      return false;
    }

    try {
      logger.info('🔧 Registering Service Worker...');

      this.registration = await navigator.serviceWorker.register('/sw.js', {
        scope: '/',
        updateViaCache: 'imports' // Better caching strategy
      });

      logger.info('✅ Service Worker registered successfully:', { scope: this.registration.scope });

      // Listen for updates
      this.registration.addEventListener('updatefound', () => {
        this.handleUpdate();
      });

      // Check for updates periodically (every 24 hours)
      this.updateCheckInterval = setInterval(() => {
        this.checkForUpdates();
      }, 24 * 60 * 60 * 1000);

      return true;

    } catch (error) {
      logger.error('❌ Service Worker registration failed:', { error: error instanceof Error ? error.message : String(error) });
      return false;
    }
  }

  // Handle service worker updates
  private handleUpdate() {
    if (!this.registration) return;

    const newWorker = this.registration.installing;
    if (!newWorker) return;

    logger.info('🔄 New Service Worker available');

    newWorker.addEventListener('statechange', () => {
      if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
        // New version is available
        this.showUpdateNotification();
      }
    });
  }

  // Show update notification to user
  private showUpdateNotification() {
    if (typeof window === 'undefined') return;

    // In a production app, you might show a toast or banner
    logger.info('📢 New version available! Refresh to update.');

    // Auto-refresh in development, manual in production
    if (process.env.NODE_ENV === 'development') {
      window.location.reload();
    } else {
      // You could dispatch a custom event here for the UI to handle
      window.dispatchEvent(new CustomEvent('sw-update-available'));
    }
  }

  // Check for service worker updates
  async checkForUpdates(): Promise<void> {
    if (!this.registration) return;

    try {
      await this.registration.update();
      logger.info('🔍 Checked for Service Worker updates');
    } catch (error) {
      logger.error('Failed to check for updates:', { error: error instanceof Error ? error.message : String(error) });
    }
  }

  // Unregister service worker (for testing or cleanup)
  async unregister(): Promise<boolean> {
    // Clear update check interval
    if (this.updateCheckInterval) {
      clearInterval(this.updateCheckInterval);
      this.updateCheckInterval = null;
    }

    if (!this.registration) return false;

    try {
      const result = await this.registration.unregister();
      logger.info('🗑️ Service Worker unregistered:', { result });
      return result;
    } catch (error) {
      logger.error('Failed to unregister Service Worker:', { error: error instanceof Error ? error.message : String(error) });
      return false;
    }
  }

  // Send message to service worker
  async sendMessage(message: ServiceWorkerMessage): Promise<ServiceWorkerResponse> {
    if (typeof window === 'undefined' || !navigator.serviceWorker.controller) {
      throw new Error('No service worker controlling this page');
    }

    return new Promise<ServiceWorkerResponse>((resolve, reject) => {
      const messageChannel = new MessageChannel();

      messageChannel.port1.onmessage = (event) => {
        const response = event.data as ServiceWorkerResponse;
        if (response.error) {
          reject(new Error(response.error));
        } else {
          resolve(response);
        }
      };

      navigator.serviceWorker.controller?.postMessage(message, [messageChannel.port2]);
    });
  }

  // Background sync for offline actions
  async registerBackgroundSync(tag: string): Promise<void> {
    if (!this.registration) {
      throw new Error('Service Worker not registered');
    }

    if ('sync' in this.registration) {
      try {
        const syncRegistration = this.registration as ServiceWorkerRegistrationWithSync;
        if (syncRegistration.sync) {
          await syncRegistration.sync.register(tag);
        }
        logger.info('📅 Background sync registered:', { tag });
      } catch (error) {
        logger.error('Background sync registration failed:', { error: error instanceof Error ? error.message : String(error) });
      }
    } else {
      logger.warn('Background sync not supported');
    }
  }

  // Get cache storage info
  async getCacheInfo(): Promise<CacheInfo[] | null> {
    if (typeof window === 'undefined' || !('caches' in window)) return null;

    try {
      const cacheNames = await caches.keys();
      const cacheInfo: CacheInfo[] = [];

      for (const cacheName of cacheNames) {
        const cache = await caches.open(cacheName);
        const keys = await cache.keys();
        cacheInfo.push({
          name: cacheName,
          size: keys.length,
          lastModified: new Date().toISOString()
        });
      }

      return cacheInfo;
    } catch (error) {
      logger.error('Failed to get cache info:', { error: error instanceof Error ? error.message : String(error) });
      return null;
    }
  }

  // Clear specific cache
  async clearCache(cacheName?: string): Promise<boolean> {
    if (typeof window === 'undefined' || !('caches' in window)) return false;

    try {
      if (cacheName) {
        await caches.delete(cacheName);
        logger.info('🗑️ Cache cleared:', { cacheName });
      } else {
        const cacheNames = await caches.keys();
        await Promise.all(cacheNames.map(name => caches.delete(name)));
        logger.info('🗑️ All caches cleared');
      }
      return true;
    } catch (error) {
      logger.error('Failed to clear cache:', { error: error instanceof Error ? error.message : String(error) });
      return false;
    }
  }

  // Get service worker status
  getStatus(): ServiceWorkerStatus {
    return {
      supported: this.isSupported,
      registered: !!this.registration,
      scope: this.registration?.scope
    };
  }
}

// Offline queue for analytics and form data
// interface OfflineQueueInterface {
//   add(item: Omit<QueueItem, 'id' | 'timestamp' | 'retries'>): void;
//   getStatus(): { items: number; isOnline: boolean };
// } // Preserved for future offline queue interface

interface QueueItem {
  id: string;
  type: 'analytics' | 'form' | 'api';
  data: Record<string, unknown>;
  timestamp: number;
  retries: number;
  maxRetries: number;
}

// Add interface for SSR fallback
// interface FullOfflineQueueInterface extends OfflineQueueInterface {
//   queue: QueueItem[];
//   isOnline: boolean;
//   setupOnlineListener(): void;
//   processQueue(): Promise<void>;
//   processItem(item: QueueItem): Promise<void>;
//   saveQueue(): void;
//   loadQueue(): void;
// } // Preserved for future full offline queue implementation

export class OfflineQueue {
  private static instance: OfflineQueue;
  private queue: QueueItem[] = [];
  private isOnline: boolean = typeof window !== 'undefined' ? navigator.onLine : true;

  constructor() {
    this.setupOnlineListener();
    this.loadQueue();
  }

  static getInstance(): OfflineQueue {
    if (typeof window === 'undefined') {
      // Return a no-op instance during SSR
      return {
        add: () => {},
        getStatus: () => ({ items: 0, isOnline: true }),
        // Add missing properties for SSR fallback
        queue: [],
        isOnline: true,
        setupOnlineListener: () => {},
        processQueue: async () => {},
        processItem: async () => {},
        saveQueue: () => {},
        loadQueue: () => {}
      } as unknown as OfflineQueue;
    }

    if (!OfflineQueue.instance) {
      OfflineQueue.instance = new OfflineQueue();
    }
    return OfflineQueue.instance;
  }

  private setupOnlineListener() {
    if (typeof window === 'undefined') return;

    window.addEventListener('online', () => {
      this.isOnline = true;
      this.processQueue();
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
    });
  }

  // Add item to queue
  add(item: Omit<QueueItem, 'id' | 'timestamp' | 'retries'>) {
    this.queue.push({
      ...item,
      id: Math.random().toString(36).substr(2, 9),
      timestamp: Date.now(),
      retries: 0
    });

    this.saveQueue();

    if (this.isOnline) {
      this.processQueue();
    }
  }

  // Process queued items
  private async processQueue() {
    if (this.queue.length === 0) return;

    logger.info('📤 Processing offline queue:', { length: this.queue.length });

    const itemsToProcess = [...this.queue];
    this.queue = [];

    for (const item of itemsToProcess) {
      try {
        await this.processItem(item);
        logger.info('✅ Queue item processed:', { id: item.id });
      } catch (error) {
        logger.error('❌ Failed to process queue item:', { error: error instanceof Error ? error.message : String(error) });
        // Re-add failed items to queue
        this.queue.push(item);
      }
    }

    this.saveQueue();
  }

  // Process individual item
  private async processItem(item: QueueItem): Promise<void> {
    const { type, data } = item;

    switch (type) {
      case 'analytics':
        await fetch('/api/analytics/track', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data)
        });
        break;

      case 'form':
        await fetch(data.url as string, {
          method: (data.method as string) || 'POST',
          headers: (data.headers as Record<string, string>) || { 'Content-Type': 'application/json' },
          body: JSON.stringify(data.body)
        });
        break;

      default:
        logger.warn('Unknown queue item type:', { type });
    }
  }

  // Save queue to localStorage
  private saveQueue() {
    if (typeof window === 'undefined') return;

    try {
      localStorage.setItem('agriko_offline_queue', JSON.stringify(this.queue));
    } catch (error) {
      logger.error('Failed to save offline queue:', { error: error instanceof Error ? error.message : String(error) });
    }
  }

  // Load queue from localStorage
  private loadQueue() {
    if (typeof window === 'undefined') return;

    try {
      const stored = localStorage.getItem('agriko_offline_queue');
      if (stored) {
        this.queue = JSON.parse(stored) as QueueItem[];
      }
    } catch (error) {
      logger.error('Failed to load offline queue:', { error: error instanceof Error ? error.message : String(error) });
      this.queue = [];
    }
  }

  // Get queue status
  getStatus() {
    return {
      items: this.queue.length,
      isOnline: this.isOnline
    };
  }
}

// Initialize service worker and offline capabilities
export async function initializePerformanceOptimizations(): Promise<void> {
  if (typeof window === 'undefined') return;

  const swManager = ServiceWorkerManager.getInstance();
  const _offlineQueue = OfflineQueue.getInstance();
  void _offlineQueue; // Preserved for future offline queue integration

  // Register service worker
  await swManager.register();

  // Setup performance monitoring
  if ('PerformanceObserver' in window) {
    // Monitor for layout shifts and other performance issues
    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry) => {
        if (entry.entryType === 'layout-shift') {
          const layoutShiftEntry = entry as LayoutShiftEntry;
          if (!layoutShiftEntry.hadRecentInput) {
            const clsValue = layoutShiftEntry.value;
            if (clsValue > 0.1) {
              logger.warn('🚨 High CLS detected:', { value: clsValue });
            }
          }
        }
      });
    });

    observer.observe({ entryTypes: ['layout-shift'] });
  }

  logger.info('🚀 Performance optimizations initialized');
}

