import { WCProduct } from '@/types/woocommerce';
import { logger } from '@/lib/logger';

import { getAllProducts, getProductsByIds } from './woocommerce';
import { generateBatchEmbeddings, prepareTextForEmbedding } from './embeddings';
import { safeUpsertVectors } from './qdrant';

export interface ProductVector {
  id: string;
  productId: number;
  title: string;
  description: string;
  categories: string[];
  embedding: number[];
  metadata: {
    source: string;
    productId: number;
    slug: string;
    price: string;
    categories: string[];
    inStock: boolean;
    featured: boolean;
    timestamp: string;
  };
}

export function extractProductText(product: WCProduct): string {
  const categories = product.categories?.map(cat => cat.name) || [];
  return prepareTextForEmbedding(
    product.name,
    stripHtmlTags(product.description || product.short_description || ''),
    categories
  );
}

function stripHtmlTags(html: string): string {
  return html.replace(/<[^>]*>/g, '').replace(/&[^;]+;/g, ' ').trim();
}

export async function vectorizeProducts(products: WCProduct[]): Promise<ProductVector[]> {
  logger.info(`🔄 Processing ${products.length} products for vectorization...`);

  const texts = products.map(extractProductText);
  const embeddings = await generateBatchEmbeddings(texts);

  const vectors: ProductVector[] = products.map((product, index) => ({
    id: `product_${product.id}`,
    productId: product.id,
    title: product.name,
    description: stripHtmlTags(product.description || product.short_description || ''),
    categories: product.categories?.map(cat => cat.name) || [],
    embedding: embeddings[index] ?? [],
    metadata: {
      source: 'agriko',
      productId: product.id,
      slug: product.slug,
      price: product.price || '0',
      categories: product.categories?.map(cat => cat.name) || [],
      inStock: product.stock_status === 'instock',
      featured: product.featured || false,
      timestamp: new Date().toISOString(),
    },
  }));

  logger.info(`✅ Created ${vectors.length} product vectors`);
  return vectors;
}

export async function syncProductsToQdrant(
  options: {
    batchSize?: number;
    maxProducts?: number;
    featuredOnly?: boolean;
  } = {}
) {
  const { batchSize = 20, maxProducts = 100, featuredOnly = false } = options;

  try {
    logger.info('🔄 Fetching products from WooCommerce...');
    const products = await getAllProducts({
      per_page: maxProducts,
      status: 'publish',
      featured: featuredOnly ? true : undefined,
    });

    if (products.length === 0) {
      logger.info('⚠️ No products found to vectorize');
      return { success: true, processed: 0 };
    }

    logger.info(`📊 Found ${products.length} products to process`);

    let totalProcessed = 0;

    // Process in batches to avoid memory issues and API limits
    for (let i = 0; i < products.length; i += batchSize) {
      const batch = products.slice(i, i + batchSize);
      logger.info(`\n🔄 Processing batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(products.length / batchSize)}`);

      try {
        const vectors = await vectorizeProducts(batch);

        // Format vectors for Qdrant
        const qdrantVectors = vectors.map(vector => ({
          id: vector.id,
          values: vector.embedding,
          metadata: vector.metadata,
        }));

        const result = await safeUpsertVectors(qdrantVectors);

        if (result.success) {
          totalProcessed += batch.length;
          logger.info(`✅ Batch ${Math.floor(i / batchSize) + 1} completed successfully`);
        } else {
          // Properly handle the unknown error type
          // result.error is a string from safeUpsertVectors
          const errorData = {
            message: result.error,
            batchNumber: Math.floor(i / batchSize) + 1,
            batchSize: batch.length,
          };

          logger.error('❌ Batch failed:', errorData);
        }

        // Small delay between batches to be gentle on resources
        if (i + batchSize < products.length) {
          await new Promise(resolve => setTimeout(resolve, 1000));
        }

      } catch (error) {
        // Properly handle the unknown error type
        let errorData: Record<string, unknown> | undefined;

        if (error instanceof Error) {
          errorData = {
            message: error.message,
            stack: error.stack,
            name: error.name,
          };
        } else if (typeof error === 'object' && error !== null) {
          errorData = error as Record<string, unknown>;
        } else {
          errorData = {
            message: String(error),
          };
        }

        logger.error(`❌ Error processing batch ${Math.floor(i / batchSize) + 1}:`, errorData);
      }
    }

    logger.info(`\n🎉 Vectorization complete! Processed ${totalProcessed}/${products.length} products`);
    return { success: true, processed: totalProcessed };

  } catch (error) {
    // Properly handle the unknown error type
    let errorData: Record<string, unknown> | undefined;

    if (error instanceof Error) {
      errorData = {
        message: error.message,
        stack: error.stack,
        name: error.name,
      };
    } else if (typeof error === 'object' && error !== null) {
      errorData = error as Record<string, unknown>;
    } else {
      errorData = {
        message: String(error),
      };
    }

    logger.error('❌ Product vectorization failed:', errorData);
    return { success: false, error };
  }
}

export async function vectorizeSingleProduct(productId: number): Promise<boolean> {
  try {
    logger.info(`🔄 Vectorizing single product: ${productId}`);

    const products = await getProductsByIds([productId]);

    if (products.length === 0) {
      logger.error(`❌ Product ${productId} not found`);
      return false;
    }

    const vectors = await vectorizeProducts(products);

    // Check if we have vectors before proceeding
    if (vectors.length === 0) {
      logger.error(`❌ Failed to create vector for product ${productId}`);
      return false;
    }

    // Get the first vector (we know it exists since we checked the length)
    const firstVector = vectors[0];
    if (!firstVector) {
      logger.error(`❌ Unexpected: No vector available for product ${productId}`);
      return false;
    }

    const qdrantVector = {
      id: firstVector.id,
      values: firstVector.embedding,
      metadata: firstVector.metadata,
    };

    const result = await safeUpsertVectors([qdrantVector]);

    if (result.success) {
      logger.info(`✅ Product ${productId} vectorized successfully`);
      return true;
    } else {
      // Properly handle the unknown error type
      let errorData: Record<string, unknown> | undefined;

      // result.error is a string from safeUpsertVectors
      errorData = {
        message: result.error,
        productId,
      };

      logger.error(`❌ Failed to upsert vector for product ${productId}:`, errorData);
      return false;
    }

  } catch (error) {
    // Properly handle the unknown error type
    let errorData: Record<string, unknown> | undefined;

    if (error instanceof Error) {
      errorData = {
        message: error.message,
        stack: error.stack,
        name: error.name,
      };
    } else if (typeof error === 'object' && error !== null) {
      errorData = error as Record<string, unknown>;
    } else {
      errorData = {
        message: String(error),
      };
    }

    logger.error(`❌ Error vectorizing product ${productId}:`, errorData);
    return false;
  }
}

// WooCommerce Webhook Configuration for Auto-Sync
import { logger } from '@/lib/logger';
import { config } from '@/lib/unified-config';
import crypto from 'crypto';

export interface WebhookConfig {
  name: string;
  topic: string;
  delivery_url: string;
  secret: string;
  status: 'active' | 'paused' | 'disabled';
  api_version: string;
}

// Webhook configurations for auto-sync
export const AUTO_SYNC_WEBHOOKS: WebhookConfig[] = [
  {
    name: 'Auto-Sync Product Created',
    topic: 'product.created',
    delivery_url: `${config.app.baseUrl}/api/auto-sync?action=product_created`,
    secret: config.webhooks.secret || 'default-webhook-secret',
    status: 'active',
    api_version: 'wp_api_v3'
  },
  {
    name: 'Auto-Sync Product Updated',
    topic: 'product.updated',
    delivery_url: `${config.app.baseUrl}/api/auto-sync?action=product_updated`,
    secret: config.webhooks.secret || 'default-webhook-secret',
    status: 'active',
    api_version: 'wp_api_v3'
  },
  {
    name: 'Auto-Sync Order Created',
    topic: 'order.created',
    delivery_url: `${config.app.baseUrl}/api/auto-sync?action=order_created`,
    secret: config.webhooks.secret || 'default-webhook-secret',
    status: 'active',
    api_version: 'wp_api_v3'
  },
  {
    name: 'Auto-Sync Order Updated',
    topic: 'order.updated',
    delivery_url: `${config.app.baseUrl}/api/auto-sync?action=order_updated`,
    secret: config.webhooks.secret || 'default-webhook-secret',
    status: 'active',
    api_version: 'wp_api_v3'
  }
];

/**
 * Creates or updates WooCommerce webhooks for auto-sync
 */
export async function setupAutoSyncWebhooks(): Promise<{
  success: boolean;
  created: number;
  updated: number;
  errors: string[];
}> {
  const results = {
    success: true,
    created: 0,
    updated: 0,
    errors: [] as string[]
  };

  if (!config.woocommerce.apiUrl) {
    results.success = false;
    results.errors.push('Missing WooCommerce API URL');
    return results;
  }

  const webhookSecret = config.webhooks.secret || 'default-webhook-secret';
  if (!webhookSecret || webhookSecret === 'default-webhook-secret') {
    logger.warn('Using default webhook secret - configure WEBHOOK_SECRET in production');
  }

  try {
    const { wcRequest } = await import('./woocommerce');

    // Get existing webhooks
    const existingWebhooks = await wcRequest<any[]>('/webhooks');

    for (const webhookConfig of AUTO_SYNC_WEBHOOKS) {
      try {
        // Check if webhook already exists
        const existing = existingWebhooks.find(wh =>
          wh.topic === webhookConfig.topic &&
          wh.delivery_url === webhookConfig.delivery_url
        );

        if (existing) {
          // Update existing webhook
          await wcRequest(`/webhooks/${existing.id}`, {
            method: 'PUT',
            body: JSON.stringify({
              status: webhookConfig.status,
              secret: webhookConfig.secret,
              api_version: webhookConfig.api_version
            })
          });
          results.updated++;
          logger.info(`✅ Updated webhook: ${webhookConfig.name}`);
        } else {
          // Create new webhook
          await wcRequest('/webhooks', {
            method: 'POST',
            body: JSON.stringify(webhookConfig)
          });
          results.created++;
          logger.info(`✅ Created webhook: ${webhookConfig.name}`);
        }
      } catch (error) {
        const errorMsg = `Failed to setup webhook ${webhookConfig.name}: ${error instanceof Error ? error.message : String(error)}`;
        results.errors.push(errorMsg);
        logger.error(errorMsg);
        results.success = false;
      }
    }

    logger.info(`🔗 Webhook setup completed: ${results.created} created, ${results.updated} updated, ${results.errors.length} errors`);
    return results;

  } catch (error) {
    results.success = false;
    results.errors.push(`Webhook setup failed: ${error instanceof Error ? error.message : String(error)}`);
    logger.error('Failed to setup auto-sync webhooks:', error as Record<string, unknown>);
    return results;
  }
}

/**
 * Verifies webhook signature for security
 */
export function verifyWebhookSignature(
  signature: string | null,
  body: string,
  secret: string = config.webhooks.secret || 'default-webhook-secret'
): boolean {
  if (!signature || !secret) {
    logger.warn('Webhook signature verification failed: missing signature or secret');
    return false;
  }

  try {
    // WooCommerce uses HMAC-SHA256
    const expectedSignature = crypto
      .createHmac('sha256', secret)
      .update(body, 'utf8')
      .digest('base64');

    // Use timing-safe comparison to prevent timing attacks
    return crypto.timingSafeEqual(
      Buffer.from(signature),
      Buffer.from(expectedSignature)
    );
  } catch (error) {
    logger.error('Webhook signature verification error:', error as Record<string, unknown>);
    return false;
  }
}

/**
 * Processes incoming webhook data
 */
export function processWebhookData(topic: string, data: any): {
  action: string;
  processedData: any;
} {
  switch (topic) {
    case 'product.created':
    case 'product.updated':
      return {
        action: topic.replace('.', '_'),
        processedData: {
          productId: data.id,
          eventType: topic,
          productData: data,
          metadata: {
            name: data.name,
            price: data.price,
            status: data.status,
            categories: data.categories?.map((cat: any) => cat.name) || [],
            webhook: true,
            timestamp: Date.now()
          }
        }
      };

    case 'order.created':
    case 'order.updated':
      return {
        action: topic.replace('.', '_'),
        processedData: {
          orderId: data.id.toString(),
          userId: data.customer_id ? data.customer_id.toString() : undefined,
          orderValue: parseFloat(data.total || '0'),
          items: data.line_items?.map((item: any) => ({
            productId: item.product_id,
            quantity: item.quantity,
            price: parseFloat(item.price || '0')
          })) || [],
          metadata: {
            status: data.status,
            paymentMethod: data.payment_method,
            customerEmail: data.billing?.email,
            webhook: true,
            timestamp: Date.now()
          }
        }
      };

    default:
      throw new Error(`Unknown webhook topic: ${topic}`);
  }
}

/**
 * Test webhook connectivity
 */
export async function testWebhookConnectivity(): Promise<{
  success: boolean;
  webhooks: Array<{
    id: number;
    name: string;
    topic: string;
    status: string;
    lastDelivery?: string;
  }>;
  errors: string[];
}> {
  const results = {
    success: true,
    webhooks: [] as any[],
    errors: [] as string[]
  };

  try {
    const { wcRequest } = await import('./woocommerce');
    const webhooks = await wcRequest<any[]>('/webhooks');

    results.webhooks = webhooks
      .filter(wh => wh.delivery_url?.includes('/api/auto-sync'))
      .map(wh => ({
        id: wh.id,
        name: wh.name,
        topic: wh.topic,
        status: wh.status,
        lastDelivery: wh.date_modified
      }));

    logger.info(`📊 Found ${results.webhooks.length} auto-sync webhooks`);

  } catch (error) {
    results.success = false;
    results.errors.push(`Failed to test webhook connectivity: ${error instanceof Error ? error.message : String(error)}`);
    logger.error('Webhook connectivity test failed:', error as Record<string, unknown>);
  }

  return results;
}

