      });
      return products.map(product => product.slug);
    } catch (error) {
      const errorData = handleError(error, 'getAllProductSlugs');
      logger.error('Error fetching product slugs:', errorData);
      return [];
    }
  }

/**
 * Single source of truth for authentication types
 * All auth-related modules should import from here
 */

// ============================================
// Core Auth Types
// ============================================

export interface AuthUser {
  userId: string;
  username: string;
  email: string;
  role: UserRole;
  permissions: Permission[];
  apiKey?: string;
  sessionToken?: string;
  expiresAt?: number;
}

export interface AuthResult {
  isAuthenticated: boolean;
  user?: AuthUser;
  error?: string;
  expiresAt?: number;
  token?: string;
}

export interface AuthToken {
  token: string;
  type: 'Bearer' | 'Basic' | 'ApiKey';
  expiresAt: number;
  refreshToken?: string;
}

export interface AuthSession {
  sessionId: string;
  userId: string;
  createdAt: number;
  expiresAt: number;
  lastActivity: number;
  ipAddress?: string;
  userAgent?: string;
}

// ============================================
// Role & Permission Types
// ============================================

export enum UserRole {
  ADMIN = 'admin',
  READONLY = 'readonly',
  API_USER = 'api-user',
  CUSTOMER = 'customer',
  SHOP_MANAGER = 'shop_manager',
}

export enum Permission {
  // Admin permissions
  ADMIN_FULL = 'admin.full',
  ADMIN_READ = 'admin.read',
  ADMIN_WRITE = 'admin.write',
  ADMIN_DELETE = 'admin.delete',

  // API permissions
  API_READ = 'api.read',
  API_WRITE = 'api.write',

  // Analytics permissions
  ANALYTICS_VIEW = 'analytics.view',
  ANALYTICS_EXPORT = 'analytics.export',

  // Order permissions
  ORDERS_VIEW = 'orders.view',
  ORDERS_MANAGE = 'orders.manage',
  ORDERS_REFUND = 'orders.refund',

  // Product permissions
  PRODUCTS_VIEW = 'products.view',
  PRODUCTS_MANAGE = 'products.manage',

  // Customer permissions
  CUSTOMERS_VIEW = 'customers.view',
  CUSTOMERS_MANAGE = 'customers.manage',
}

// Role-based permission mappings
export const ROLE_PERMISSIONS: Record<UserRole, Permission[]> = {
  [UserRole.ADMIN]: [
    Permission.ADMIN_FULL,
    Permission.ADMIN_READ,
    Permission.ADMIN_WRITE,
    Permission.ADMIN_DELETE,
    Permission.API_READ,
    Permission.API_WRITE,
    Permission.ANALYTICS_VIEW,
    Permission.ANALYTICS_EXPORT,
    Permission.ORDERS_VIEW,
    Permission.ORDERS_MANAGE,
    Permission.ORDERS_REFUND,
    Permission.PRODUCTS_VIEW,
    Permission.PRODUCTS_MANAGE,
    Permission.CUSTOMERS_VIEW,
    Permission.CUSTOMERS_MANAGE,
  ],
  [UserRole.READONLY]: [
    Permission.ADMIN_READ,
    Permission.API_READ,
    Permission.ANALYTICS_VIEW,
    Permission.ORDERS_VIEW,
    Permission.PRODUCTS_VIEW,
    Permission.CUSTOMERS_VIEW,
  ],
  [UserRole.API_USER]: [
    Permission.API_READ,
    Permission.API_WRITE,
  ],
  [UserRole.CUSTOMER]: [
    Permission.ORDERS_VIEW, // Only their own orders
  ],
  [UserRole.SHOP_MANAGER]: [
    Permission.ORDERS_VIEW,
    Permission.ORDERS_MANAGE,
    Permission.PRODUCTS_VIEW,
    Permission.PRODUCTS_MANAGE,
    Permission.CUSTOMERS_VIEW,
    Permission.ANALYTICS_VIEW,
  ],
};

